INSERT INTO `news` VALUES (1,'NHỮNG QUY ĐỊNH CHUNG',0,NULL,'<p><span style=\"font-family:sans-serif; font-size:14px\">C&ocirc;ng ty xe kh&aacute;ch Phương Trang khuyến kh&iacute;ch qu&yacute; kh&aacute;ch đặt chỗ trước nhằm đảm bảo lu&ocirc;n c&oacute; vị tr&iacute; ghế cho qu&yacute; vị, tuy nhi&ecirc;n qu&yacute; vị vẫn c&oacute; thể đến tại bến để đi m&agrave; kh&ocirc;ng cần gọi đặt chỗ. Qu&yacute; h&agrave;nh kh&aacute;ch c&oacute; thể mua v&eacute; bằng c&aacute;c h&igrave;nh thức linh hoạt nhưng vẫn đảm bảo</span></p>\r\n',NULL,NULL,'1',0,NULL,NULL),(2,'VẬN CHUYỂN HÀNG HÓA',0,NULL,'<p style=\"text-align:justify\">Mỗi h&agrave;nh kh&aacute;ch chỉ được mang theo 1 vali với k&iacute;ch thước trung b&igrave;nh v&agrave; 1 t&uacute;i h&agrave;nh l&yacute; với k&iacute;ch cỡ x&aacute;ch tay. H&agrave;nh l&yacute; được chấp thuận bao gồm vali, t&uacute;i vải, th&ugrave;ng với những kh&oacute;a an to&agrave;n, mọi sự mất m&aacute;t b&ecirc;n trong h&agrave;nh l&yacute; khi c&oacute; kh&oacute;a an to&agrave;n ch&uacute;ng t&ocirc;i kh&ocirc;ng chịu tr&aacute;ch nhiệm..</p>\r\n',NULL,NULL,'2',0,NULL,NULL),(3,'THÔNG TIN HÀNH LÝ',0,NULL,'<p>Để đảm bảo sự an to&agrave;n cho c&aacute;c h&agrave;ng h&oacute;a vận chuyển v&agrave; an to&agrave;n cho sự di chuyển của tất cả qu&yacute; h&agrave;nh kh&aacute;ch tr&ecirc;n chuyến xe, qu&yacute; h&agrave;nh kh&aacute;ch c&oacute; nhu cầu với dịch vụ vận chuyển h&agrave;ng h&oacute;a vui l&ograve;ng đọc kĩ c&aacute;c quy định li&ecirc;n quan đến h&agrave;ng h&oacute;a cần vận chuyển b&ecirc;n dưới đ&acirc;y...</p>\r\n',NULL,NULL,'3',0,NULL,NULL),(5,'Tạo khung khổ pháp lý đối với việc ứng dụng KHCN trong hoạt động vận tải',0,NULL,'<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Theo đ&oacute;, Bộ GTVT nhận được Văn bản số 1155/VPCP-CN ng&agrave;y 13/02/2019 của Văn ph&ograve;ng Ch&iacute;nh phủ về việc xử l&yacute; th&ocirc;ng tin b&aacute;o n&ecirc;u, trong đ&oacute; c&oacute; nội dung phản &aacute;nh về &quot;Nhiều ch&iacute;nh s&aacute;ch th&uacute;c đẩy doanh nghiệp đổi mới, ứng dụng c&ocirc;ng nghệ chưa thật sự sẵn s&agrave;ng. </span></span></p>\r\n\r\n<div style=\"page-break-after: always\"><span style=\"display:none\">&nbsp;</span></div>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">R&otilde; nhất l&agrave; c&aacute;c ứng dụng gọi xe c&ocirc;ng nghệ, đ&atilde; hơn 2 năm nghi&ecirc;n cứu với 6 lần chỉnh sửa, Bộ GTVT vẫn chưa đưa ra được khung ph&aacute;p l&yacute; r&otilde; r&agrave;ng. Sự chậm trễ về ch&iacute;nh s&aacute;ch l&agrave; nguy&ecirc;n nh&acirc;n g&acirc;y ra nhiều x&aacute;o động trong m&ocirc;i trường kinh doanh vận tải, dẫn đến vụ kiện Vinasun v&agrave; Grab. C&oacute; &yacute; kiến cho rằng, &quot;n&oacute;ng&quot; chủ trương nhưng &quot;nguội&quot; thực tế&quot;. Bộ GTVT k&iacute;nh b&aacute;o c&aacute;o Thủ tướng Ch&iacute;nh phủ như sau:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Đối với việc ứng dụng khoa học c&ocirc;ng nghệ trong hoạt động vận tải, Bộ GTVT lu&ocirc;n ủng hộ việc ứng dụng khoa học c&ocirc;ng nghệ nhằm hỗ trợ hoạt động kinh doanh vận tải (h&agrave;ng h&oacute;a, h&agrave;nh kh&aacute;ch), trong đ&oacute; khuyến kh&iacute;ch c&aacute;c đơn vị cung cấp phần mềm, doanh nghiệp vận tải chủ động ứng dụng để n&acirc;ng cao chất lượng dịch vụ vận tải v&agrave; tạo sự thuận lợi cho người d&acirc;n. Điều đ&oacute; được thể hiện ngay từ năm 2009 Bộ GTVT đ&atilde; &aacute;p dụng quy định lắp thiết bị gi&aacute;m s&aacute;t h&agrave;nh tr&igrave;nh xe &ocirc; t&ocirc; để quản l&yacute; hoạt động, điều h&agrave;nh hoạt động taxi th&ocirc;ng qua bộ đ&agrave;m, cải c&aacute;ch thủ tục h&agrave;nh ch&iacute;nh (thực hiện thủ tục h&agrave;nh ch&iacute;nh cấp độ 3, cấp độ 4), b&aacute;n v&eacute; online, quản l&yacute; hoạt động vận tải c&ocirc;ng cộng th&ocirc;ng qua phần mềm, v&eacute; điện tử; đặc biệt từ năm 2015 trở lại đ&acirc;y, Bộ GTVT đ&atilde; tham mưu tr&igrave;nh Thủ tướng Ch&iacute;nh phủ cho ph&eacute;p thực hiện Đề &aacute;n th&iacute; điểm &ldquo;Triển khai ứng dụng khoa học c&ocirc;ng nghệ hỗ trợ quản l&yacute; v&agrave; kết nối hoạt động vận tải h&agrave;nh kh&aacute;ch theo hợp đồng&rdquo; tr&ecirc;n địa b&agrave;n 05 địa phương gồm: H&agrave; Nội, Quảng Ninh, Đ&agrave; Nẵng, Kh&aacute;nh H&ograve;a, th&agrave;nh phố Hồ Ch&iacute; Minh (05 địa phương đ&atilde; được Thủ tưởng Ch&iacute;nh phủ cho ph&eacute;p th&iacute; điểm tại Văn bản số 1850/TTg-KTN ng&agrave;y 19/10/2015 của Thủ tướng Ch&iacute;nh phủ, thời gian th&iacute; điểm từ 2016-2018).</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Trong qu&aacute; tr&igrave;nh triển khai th&iacute; điểm, Bộ GTVT thấy rằng: Việc ứng dụng khoa học c&ocirc;ng nghệ trong cuộc c&aacute;ch mạng c&ocirc;ng nghiệp lần thứ 4 (4.0) v&agrave;o trong c&aacute;c lĩnh vực đời sống x&atilde; hội v&agrave; ng&agrave;nh dịch vụ (trong đ&oacute; c&oacute; dịch vụ vận tải đường bộ) l&agrave; một vấn đề mới ph&aacute;t sinh v&agrave; li&ecirc;n quan đến quy định của nhiều ph&aacute;p luật như: d&acirc;n sự, thuế, thương mại, giao th&ocirc;ng đường bộ, giao dịch điện tử, ... B&ecirc;n cạnh đ&oacute;, đ&acirc;y l&agrave; vấn đề kh&ocirc;ng chỉ kh&oacute; với Việt Nam m&agrave; l&agrave; vấn đề kh&oacute; với cả c&aacute;c nước tr&ecirc;n thế giới cũng gặp phải như: Mỹ, Ph&aacute;p, Anh, Đức, T&acirc;y Ban Nha, Nhật Bản, H&agrave;n Quốc, Th&aacute;i Lan,,.. Do đ&oacute; cần c&oacute; sự v&agrave;o cuộc của nhiều Bộ, ng&agrave;nh v&agrave; địa phương c&ugrave;ng v&agrave;o cuộc v&agrave; việc x&acirc;y dựng, bổ sung quy định mới th&igrave; cần c&oacute; một qu&aacute; tr&igrave;nh nghi&ecirc;n cứu v&agrave; ho&agrave;n thiện.</span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><a class=\"view-gallery\" href=\"https://drvn.gov.vn/upload/20830/fck/amh5_3.png\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(46, 46, 46); text-decoration-line: none;\"><img alt=\"\" src=\"https://drvn.gov.vn/upload/20830/fck/amh5_3.png\" style=\"box-sizing:border-box; height:401px; max-width:100%; vertical-align:middle; width:600px\" /></a></p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">(Ảnh minh họa)</span></span></em></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Ch&iacute;nh v&igrave; vậy, ngay từ khi triển khai Bộ GTVT đ&atilde; x&aacute;c định đ&acirc;y l&agrave; một nhiệm vụ kh&oacute; v&agrave; thường xuy&ecirc;n phối hợp c&ugrave;ng với Bộ, ng&agrave;nh v&agrave; địa phương thực hiện nghi&ecirc;m chỉ đạo của Thủ tướng Ch&iacute;nh phủ tại Văn bản số 1850/TTg-KTN.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Đối với việc tạo khung khổ ph&aacute;p l&yacute; cho việc ứng dụng khoa học c&ocirc;ng nghệ trong hoạt động vận tải trong qu&aacute; tr&igrave;nh x&acirc;y dựng dự thảo Nghị định thay thế Nghị định số 86/2014/NĐ-CP ng&agrave;y 10/9/2014 của Ch&iacute;nh phủ quy định về kinh doanh v&agrave; điều kiện kinh doanh vận tải bằng xe &ocirc; t&ocirc;. Thực hiện &yacute; kiến của Ph&oacute; Thủ tướng Trịnh Đ&igrave;nh Dũng tại C&ocirc;ng văn số 349/VPCP-CN ng&agrave;y 11/01/2018 của Văn ph&ograve;ng Ch&iacute;nh phủ, trong đ&oacute; đề nghị c&aacute;c Bộ: C&ocirc;ng an, Tư ph&aacute;p, C&ocirc;ng Thương, T&agrave;i ch&iacute;nh, Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng, Khoa học v&agrave; C&ocirc;ng nghệ v&agrave; Ủy ban nh&acirc;n d&acirc;n c&aacute;c th&agrave;nh phố: H&agrave; Nội, th&agrave;nh phố Hồ Ch&iacute; Minh khẩn trương c&oacute; &yacute; kiến đối với dự thảo Nghị định (Bộ GTVT tr&igrave;nh tại văn bản số 14725/TTr- BGTVT ng&agrave;y 29/12/2017) gửi Bộ GTVT trước ng&agrave;y 20/01/2018 để nghi&ecirc;n cứu, ho&agrave;n thiện dự thảo Nghị định, gửi Bộ Tư ph&aacute;p thẩm định lại, tr&igrave;nh Thủ tướng Ch&iacute;nh phủ trước ng&agrave;y 31/01/2018 theo đ&uacute;ng quy định của Luật ban h&agrave;nh văn bản quy phạm ph&aacute;p luật; Văn bản số 1498/VPCP-CN ng&agrave;y 09/02/2018 của Văn ph&ograve;ng Ch&iacute;nh phủ; tiếp thu &yacute; kiến của Bộ Tư ph&aacute;p tại B&aacute;o c&aacute;o thẩm định số 97/BC-BTP ng&agrave;y 19/4/2018. Thực hiện Nghị quyết số 19-2018/NQ-CP ng&agrave;y 15/5/2018 của Ch&iacute;nh phủ về tiếp tục thực hiện những nhiệm vụ, giải ph&aacute;p chủ yếu cải thiện m&ocirc;i trường kinh doanh, n&acirc;ng cao năng lực cạnh tranh năm 2018 v&agrave; c&aacute;c năm tiếp theo; thực hiện Th&ocirc;ng b&aacute;o số 242/TB- VPCP ng&agrave;y 12/7/2018 của Văn ph&ograve;ng Ch&iacute;nh phủ về Kết luận của Ph&oacute; Thủ tướng Thường trực Ch&iacute;nh phủ Trương H&ograve;a B&igrave;nh - Chủ tịch ủy ban ATGT Quốc gia tại Hội nghị sơ kết c&ocirc;ng t&aacute;c đảm bảo trật tự, an to&agrave;n giao th&ocirc;ng 6 th&aacute;ng đầu năm; nhiệm vụ trọng t&acirc;m qu&yacute; II v&agrave; 6 th&aacute;ng cuối năm 2018.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Ng&agrave;y 31/7/2018, Bộ GTVT đ&atilde; c&oacute; Tờ tr&igrave;nh số 8354/TTr-BGTVT tr&igrave;nh dự thảo Nghị định gửi k&egrave;m theo Hồ sơ tr&igrave;nh gồm: dự thảo Nghị định; B&aacute;o c&aacute;o số 8355/BC- BGTVT ng&agrave;y 31/7/2018 của Bộ GTVT b&aacute;o c&aacute;o tiếp thu &yacute; kiến thẩm định của Bộ Tư ph&aacute;p đối với dự thảo Nghị định; B&aacute;o c&aacute;o số 8356/BC-BGTVT ng&agrave;y 31/7/2018 của Bộ GTVT b&aacute;o c&aacute;o đ&aacute;nh gi&aacute; t&aacute;c động của dự thảo Nghị định; b&aacute;o c&aacute;o tổng họp, Bảng tổng hợp v&agrave; tiếp thu c&aacute;c &yacute; kiến của bộ, ng&agrave;nh, tổ chức, hiệp hội v&agrave; cơ quan hữu quan li&ecirc;n quan; c&aacute;c &yacute; kiến g&oacute;p &yacute; của cơ quan, đơn vị, tổ chức, c&aacute; nh&acirc;n; một số văn bản kh&aacute;c c&oacute; li&ecirc;n quan. Đồng thời, Bộ GTVT cũng đ&atilde; đăng tải c&ocirc;ng khai to&agrave;n bộ Hồ sơ tr&igrave;nh dự thảo Nghị định tr&ecirc;n cổng Th&ocirc;ng tin điện tử của Bộ GTVT.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Tiếp tục thực hiện chỉ đạo tại Văn bản số 8165/VPCP-CN ng&agrave;y 29/8/2018 của Văn ph&ograve;ng Ch&iacute;nh phủ v&agrave; chỉ đạo của Thủ tướng Ch&iacute;nh phủ, Bộ GTVT nghi&ecirc;m t&uacute;c nghi&ecirc;n cứu, tiếp thu giải tr&igrave;nh c&aacute;c &yacute; kiến g&oacute;p &yacute; của c&aacute;c cơ quan, đơn vị li&ecirc;n quan v&agrave; b&aacute;o c&aacute;o Thủ tướng Ch&iacute;nh phủ việc r&agrave; so&aacute;t, ho&agrave;n chỉnh lại dự thảo Nghị định, bảo đảm quản l&yacute; chặt chẽ hoạt động vận tải, cạnh tranh l&agrave;nh mạnh tr&ecirc;n cơ sở đẩy mạnh ứng dụng khoa học c&ocirc;ng nghệ trong quản l&yacute; hoạt động vận tải; đặc biệt l&agrave; c&aacute;c nội dung m&agrave; Văn ph&ograve;ng ch&iacute;nh phủ đ&atilde; định hướng xem x&eacute;t tại Văn bản số 8165/VPCP-CN ng&agrave;y 29/8/2018.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Tiếp tục thực hiện chỉ đạo của Thủ tướng Ch&iacute;nh phủ tại Văn bản số 10494/VPCP-CN ng&agrave;y 29/10/2018, Văn bản số 10593/VPCP-CN ng&agrave;y 01/11/2018, Văn bản số 506/VPCP-CN ng&agrave;y 17/01/2019 của Văn ph&ograve;ng Ch&iacute;nh phủ, Th&ocirc;ng b&aacute;o kết luận số 23/TB-VPCP ng&agrave;y 16/01/2019 của Ph&oacute; Thủ tướng Thường trực Ch&iacute;nh phủ, Chủ tịch Ủy ban An to&agrave;n giao th&ocirc;ng Quốc gia Trương H&ograve;a B&igrave;nh, Bộ Giao th&ocirc;ng vận tải (GTVT) đ&atilde; tổng hợp, tiếp thu, giải tr&igrave;nh &yacute; kiến Th&agrave;nh vi&ecirc;n Ch&iacute;nh phủ đối với dự thảo Nghị định quy định về kinh doanh v&agrave; điều kiện kinh doanh vận tải bằng xe &ocirc; t&ocirc;. Dự thảo Nghị định đ&atilde; được nghi&ecirc;n cứu v&agrave; đưa nội dung quy định đối với việc ứng đụng khoa học c&ocirc;ng nghệ trong hoạt động vận tải, dịch vụ hỗ trợ vận tải để đ&aacute;p ứng y&ecirc;u cầu về bảo đảm trật tự an to&agrave;n giao th&ocirc;ng v&agrave; trật tự vận tải, tạo thuận lợi cho người d&acirc;n v&agrave; doanh nghiệp.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Ng&agrave;y 30/01/2019, Bộ GTVT đ&atilde; c&oacute; Văn bản số 1077/BGTVT-VT gửi b&aacute;o c&aacute;o Thủ tướng Ch&iacute;nh phủ, trong đ&oacute; Bộ GTVT đ&atilde; b&aacute;o c&aacute;o r&otilde; việc quản l&yacute; xe hợp đồng ứng dụng hợp đồng điện tử v&agrave; xe taxi.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Nguồn: Cổng TTĐT Bộ GTVT</span></span></p>\r\n',NULL,NULL,'5',0,NULL,NULL),(6,'Tăng cường bảo đảm TTATGT và chống ùn tắc giao thông',0,NULL,'<p><strong><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Ch&iacute;nh phủ vừa ban h&agrave;nh Nghị quyết&nbsp;</span></span></span></strong><strong><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><a href=\"http://vanban.chinhphu.vn/portal/page/portal/chinhphu/hethongvanban?class_id=509&amp;_page=1&amp;mode=detail&amp;document_id=196240\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(46, 46, 46); text-decoration-line: none;\">12/NQ-CP</a><span style=\"color:rgb(0, 0, 0)\">&nbsp;về tăng cường bảo đảm trật tự an to&agrave;n giao th&ocirc;ng v&agrave; chống &ugrave;n tắc giao th&ocirc;ng giai đoạn 2019 - 2021.</span></span></span></strong></p>\r\n\r\n<div style=\"page-break-after: always\"><span style=\"display:none\">&nbsp;</span></div>\r\n\r\n<p><span style=\"color:rgb(0, 0, 0); font-family:arial,sans-serif; font-size:14px\">Sau 10 năm triển khai thực hiện Nghị quyết số 16/2008/NQ-CP ng&agrave;y 31/7/2008 của Ch&iacute;nh phủ về từng bước khắc phục &ugrave;n tắc giao th&ocirc;ng tại th&agrave;nh phố H&agrave; Nội v&agrave; Th&agrave;nh phố Hồ Ch&iacute; Minh (Nghị quyết số 16)&nbsp;</span><span style=\"color:rgb(0, 0, 0); font-family:arial,sans-serif; font-size:14px\">v&agrave; 7 năm thực hiện Nghị quyết số 88/2011/NQ-CP ng&agrave;y 24/8/2011 của Ch&iacute;nh phủ về tăng cường thực hiện c&aacute;c giải ph&aacute;p cấp b&aacute;ch trong c&ocirc;ng t&aacute;c bảo đảm tr&acirc;t tự an to&agrave;n giao th&ocirc;ng (Nghị quyết số 88), c&ocirc;ng t&aacute;c bảo đảm trật tự an to&agrave;n giao th&ocirc;ng đ&atilde; đạt được những kết quả quan trọng, tai nạn giao th&ocirc;ng li&ecirc;n tục giảm cả về số vụ, số người chết v&agrave; số người bị thương, cụ thể năm 2011 số người chết do tai nạn giao th&ocirc;ng l&agrave; 11.349 người th&igrave; năm 2014 đ&atilde; giảm xuống dưới 9.000 người v&agrave; năm 2018 giảm c&ograve;n l&agrave; 8.279 người; &ugrave;n tắc giao th&ocirc;ng tr&ecirc;n c&aacute;c trục giao th&ocirc;ng ch&iacute;nh cũng như tại Thủ đ&ocirc; H&agrave; Nội v&agrave; Th&agrave;nh phố Hồ Ch&iacute; Minh đ&atilde; từng bước được kiềm chế trong điều kiện nhu cầu giao th&ocirc;ng, số lượng phương tiện gia tăng nhanh, vượt xa tốc độ ph&aacute;t triển kết cấu hạ tầng v&agrave; năng lực vận tải c&ocirc;ng cộng.</span></p>\r\n\r\n<p style=\"text-align:center\"><a class=\"view-gallery\" href=\"https://drvn.gov.vn/upload/20830/fck/photo25_2.jpg\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(46, 46, 46); text-decoration-line: none;\"><img alt=\"\" src=\"https://drvn.gov.vn/upload/20830/fck/photo25_2.jpg\" style=\"box-sizing:border-box; height:407px; max-width:100%; vertical-align:middle; width:650px\" /></a></p>\r\n\r\n<p style=\"text-align:center\">Ảnh minh họa</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Tổng kết thực hiện Nghị quyết số 16 v&agrave; Nghị quyết số 88 cho thấy, b&ecirc;n cạnh c&aacute;c kết quả đạt được, vẫn c&ograve;n một số mặt tồn tại, hạn chế trong c&ocirc;ng t&aacute;c bảo đảm trật tự an to&agrave;n giao th&ocirc;ng, chống &ugrave;n tắc giao th&ocirc;ng; tai nạn giao th&ocirc;ng tuy c&oacute; giảm nhưng số người thương vong do tai nạn giao th&ocirc;ng g&acirc;y ra c&ograve;n ở mức cao; &ugrave;n tắc giao th&ocirc;ng tr&ecirc;n c&aacute;c trục giao th&ocirc;ng ch&iacute;nh, đầu mối giao th&ocirc;ng trọng điểm, c&aacute;c đ&ocirc; thị loại 1, đặc biệt l&agrave; tr&ecirc;n địa b&agrave;n Thủ đ&ocirc; H&agrave; Nội, Th&agrave;nh phố Hồ Ch&iacute; Minh ng&agrave;y c&agrave;ng trở n&ecirc;n phức tạp, c&oacute; xu hướng lan rộng cả kh&ocirc;ng gian v&agrave; thời gian; &ocirc; nhiễm m&ocirc;i trường từ hoạt động của phương tiện giao th&ocirc;ng ng&agrave;y c&agrave;ng nghi&ecirc;m trọng, g&acirc;y ra những thiệt hại kh&ocirc;ng nhỏ kinh tế, ảnh hưởng xấu đến sức khoẻ v&agrave; đời sống của nh&acirc;n d&acirc;n, t&aacute;c động kh&ocirc;ng tốt đến m&ocirc;i trường du lịch v&agrave; bạn b&egrave; quốc tế.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Để tiếp tục k&eacute;o giảm tai nạn giao th&ocirc;ng, phấn đấu giảm số thương vong do tai nạn giao th&ocirc;ng mỗi năm từ 5% đến 10%; khắc phục t&igrave;nh trạng &ugrave;n tắc giao th&ocirc;ng tr&ecirc;n c&aacute;c trục giao th&ocirc;ng ch&iacute;nh, c&aacute;c đ&ocirc; thị lớn v&agrave; tr&ecirc;n địa b&agrave;n th&agrave;nh phố H&agrave; Nội, Th&agrave;nh phố Hồ Ch&iacute; Minh v&agrave; c&aacute;c đ&ocirc; thị lớn, kh&ocirc;ng để xảy ra c&aacute;c vụ &ugrave;n tắc giao th&ocirc;ng k&eacute;o d&agrave;i tr&ecirc;n 30 ph&uacute;t, Ch&iacute;nh phủ y&ecirc;u cầu c&aacute;c bộ, ng&agrave;nh, địa phương tiếp tục thực hiện nghi&ecirc;m c&aacute;c quy định ph&aacute;p luật, đẩy mạnh triển khai c&aacute;c đề &aacute;n, dự &aacute;n về bảo đảm trật tự an to&agrave;n giao th&ocirc;ng, chống &ugrave;n tắc giao th&ocirc;ng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Cụ thể, nhiệm vụ trọng t&acirc;m l&agrave; ho&agrave;n thiện hệ thống ph&aacute;p luật v&agrave; ch&iacute;nh s&aacute;ch về bảo đảm trật tự an to&agrave;n giao th&ocirc;ng, chống &ugrave;n tắc giao th&ocirc;ng; lồng gh&eacute;p mục ti&ecirc;u an to&agrave;n giao th&ocirc;ng, tr&aacute;nh &ugrave;n tắc giao th&ocirc;ng trong c&aacute;c đề &aacute;n chiến lược, quy hoạch cấp quốc gia, cấp tỉnh, th&agrave;nh phố, c&aacute;c dự &aacute;n đầu tư tạo ra nhu cầu giao th&ocirc;ng, vận tải lớn; n&acirc;ng cao hiệu quả c&ocirc;ng t&aacute;c tuy&ecirc;n truyền, phổ biến gi&aacute;o dục ph&aacute;p luật về trật tự an to&agrave;n giao th&ocirc;ng, trật tự đ&ocirc; thị; ki&ecirc;n tr&igrave; x&acirc;y dựng văn ho&aacute; giao th&ocirc;ng an to&agrave;n v&agrave; th&acirc;n thiện m&ocirc;i trường cho mọi tầng lớp nh&acirc;n d&acirc;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">N&acirc;ng cao chất lượng, hiệu quả quản l&yacute; nh&agrave; nước về giao th&ocirc;ng vận tải, x&acirc;y dựng, đất đai v&agrave; du lịch c&oacute; li&ecirc;n quan đến nhiệm vụ bảo đảm an to&agrave;n v&agrave; chống &ugrave;n tắc giao th&ocirc;ng; n&acirc;ng cao năng lực, hiệu lực của lực lượng tuần tra, kiểm so&aacute;t, xử l&yacute; vi phạm về trật tự an to&agrave;n giao th&ocirc;ng; đẩy nhanh tiến độ v&agrave; n&acirc;ng cao chất lượng c&ocirc;ng t&aacute;c đầu tư x&acirc;y dựng, n&acirc;ng cấp hệ thống kết cấu hạ tầng giao th&ocirc;ng gắn với duy tu, bảo đảm an to&agrave;n giao th&ocirc;ng v&agrave; khai th&aacute;c c&oacute; hiệu quả c&aacute;c c&ocirc;ng tr&igrave;nh hạ tầng hiện hữu; n&acirc;ng cao hiệu quả c&ocirc;ng t&aacute;c bảo vệ h&agrave;nh lang an to&agrave;n giao th&ocirc;ng đường bộ, đường sắt, đường thuỷ nội địa.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Đẩy mạnh t&aacute;i cơ cấu hệ thống dịch vụ vận tải, n&acirc;ng cao năng lực, chất lượng v&agrave; giảm gi&aacute; để thu h&uacute;t h&agrave;nh kh&aacute;ch, h&agrave;ng ho&aacute; sử dụng vận tải đường sắt, đường thuỷ nội địa, h&agrave;ng hải, h&agrave;ng kh&ocirc;ng; đẩy nhanh tiến độ đầu tư, ph&aacute;t triển hệ thống vận tải c&ocirc;ng cộng trong đ&ocirc; thị, v&ugrave;ng tỉnh v&agrave; li&ecirc;n tỉnh; đẩy mạnh ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin, c&aacute;c th&agrave;nh tựu của c&aacute;ch mạng c&ocirc;ng nghiệp lần thứ 4 đối với c&ocirc;ng t&aacute;c quản l&yacute;, điều h&agrave;nh, tổ chức giao th&ocirc;ng, hướng dẫn người tham gia giao th&ocirc;ng cũng như hỗ trợ c&ocirc;ng t&aacute;c tuần tra, kiểm so&aacute;t, xử l&yacute; vi phạm ph&aacute;p luật về trật tự an to&agrave;n giao th&ocirc;ng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Thực hiện đồng bộ c&aacute;c giải ph&aacute;p hạn chế sử dụng phương tiện cơ giới c&aacute; nh&acirc;n, kiểm so&aacute;t chặt điều kiện về an to&agrave;n kỹ thuật v&agrave; bảo vệ m&ocirc;i trường của c&aacute;c phương tiện cơ giới, đặc biệt l&agrave; trong c&aacute;c khu vực trung t&acirc;m đ&ocirc; thị; quản l&yacute; chặt chẽ hoạt động x&acirc;y dựng bảo đảm việc đầu tư, x&acirc;y dựng mới hoặc điều chỉnh, sắp xếp c&aacute;c khu c&ocirc;ng nghiệp, đ&ocirc; thị, c&aacute;c trung t&acirc;m thương mại, trường học, bệnh viện... trong c&aacute;c đ&ocirc; thị ph&ugrave; hợp với năng lực kết cấu hạ tầng giao th&ocirc;ng v&agrave; vận tải c&ocirc;ng cộng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Đẩy nhanh tiến độ ho&agrave;n th&agrave;nh c&aacute;c dự &aacute;n đầu tư hạ tầng giao th&ocirc;ng</span></span></span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Ch&iacute;nh phủ y&ecirc;u cầu Bộ Giao th&ocirc;ng vận tải x&acirc;y dựng v&agrave; tr&igrave;nh sửa đổi Luật giao th&ocirc;ng đường bộ năm 2008, nghi&ecirc;n cứu đề xuất sửa đổi Luật giao th&ocirc;ng đường thủy nội địa v&agrave; c&aacute;c quy định ph&aacute;p luật c&oacute; li&ecirc;n quan đến trật tự an to&agrave;n giao th&ocirc;ng v&agrave; chống &ugrave;n tắc giao th&ocirc;ng; ban h&agrave;nh quy định bắt buộc chủ đầu tư khi lập dự &aacute;n đầu tư c&ocirc;ng tr&igrave;nh x&acirc;y dựng lớn phải thiết kế phương &aacute;n kết nối giao th&ocirc;ng v&agrave;o đường quốc lộ, đường ch&iacute;nh trong đ&ocirc; thị, t&iacute;nh to&aacute;n nhu cầu giao th&ocirc;ng ph&aacute;t sinh của c&ocirc;ng tr&igrave;nh, đảm bảo an to&agrave;n giao th&ocirc;ng v&agrave; kh&ocirc;ng g&acirc;y &ugrave;n tắc giao th&ocirc;ng; khẩn trương x&acirc;y dựng v&agrave; tr&igrave;nh cấp thẩm quyền ph&ecirc; duyệt quy hoạch to&agrave;n ng&agrave;nh v&agrave; c&aacute;c quy hoạch chuy&ecirc;n ng&agrave;nh thuộc lĩnh vực giao th&ocirc;ng vận tải; cập nhật v&agrave; tiếp tục triển khai Chiến lược quốc gia bảo đảm trật tự an to&agrave;n giao th&ocirc;ng đường bộ đến năm 2020 v&agrave; tầm nh&igrave;n đến năm 2030.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">B&ecirc;n cạnh đ&oacute;, đẩy nhanh tiến độ ho&agrave;n th&agrave;nh c&aacute;c dự &aacute;n đầu tư hạ tầng giao th&ocirc;ng đ&atilde; được ph&ecirc; duyệt; khẩn trương đưa tuyến đường sắt đ&ocirc; thị C&aacute;t Linh - H&agrave; Đ&ocirc;ng v&agrave;o vận h&agrave;nh thương mại v&agrave; chuyển giao để Ủy ban nh&acirc;n d&acirc;n th&agrave;nh phố H&agrave; Nội quản l&yacute;, vận h&agrave;nh; tăng cường c&ocirc;ng t&aacute;c bảo tr&igrave; kết cấu hạ tầng giao th&ocirc;ng, nhất l&agrave; hệ thống kết cấu hạ tầng giao th&ocirc;ng đường bộ; nghi&ecirc;n cứu b&aacute;o c&aacute;o Ch&iacute;nh phủ về việc tăng tỷ trọng vốn cho c&ocirc;ng t&aacute;c bảo tr&igrave; kết cấu hạ tầng giao th&ocirc;ng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Đồng thời, trao đổi, chia sẻ th&ocirc;ng tin với Bộ C&ocirc;ng an v&agrave; c&aacute;c bộ, ng&agrave;nh, địa phương nhằm tăng cường c&ocirc;ng t&aacute;c phối hợp trong hoạt động đảm bảo an to&agrave;n giao th&ocirc;ng v&agrave; chống &ugrave;n tắc giao th&ocirc;ng; ban h&agrave;nh quy định bắt buộc đối với tổ chức, c&aacute; nh&acirc;n ứng dụng khoa học c&ocirc;ng nghệ trong hoạt động vận tải v&agrave; tổ chức giao th&ocirc;ng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Lập v&agrave; triển khai thực hiện c&aacute;c đề &aacute;n, dự &aacute;n bảo đảm trật tự an to&agrave;n giao th&ocirc;ng cho tất cả c&aacute;c lĩnh vực, tập trung v&agrave;o n&acirc;ng cao điều kiện an to&agrave;n của kết cấu hạ tầng, an to&agrave;n kỹ thuật phương tiện v&agrave; người điều khiển phương tiện; xử l&yacute; dứt điểm c&aacute;c điểm đen, điểm tiềm ẩn tai nạn giao th&ocirc;ng tr&ecirc;n quốc lộ v&agrave;o năm 2020; đưa ra lộ tr&igrave;nh cụ thể để xo&aacute; bỏ c&aacute;c lối đi tự mở qua đường sắt v&agrave;o năm 2025, giảm tối thiểu 30% lối đi tự mở v&agrave;o năm 2021; khẩn trương sửa chữa, bảo đảm an to&agrave;n đường cất hạ c&aacute;nh, đường lăn Cảng h&agrave;ng kh&ocirc;ng quốc tế Nội B&agrave;i, T&acirc;n Sơn Nhất.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">X&acirc;y dựng, ban h&agrave;nh theo thẩm quyền hoặc tr&igrave;nh cơ quan c&oacute; thẩm quyền ban h&agrave;nh c&aacute;c quy định, ch&iacute;nh s&aacute;ch khuyến kh&iacute;ch sử dụng phương tiện vận tải c&ocirc;ng cộng, hạn chế sử dụng phương tiện cơ giới c&aacute; nh&acirc;n nhằm k&eacute;o giảm tai nạn giao th&ocirc;ng, &ugrave;n tắc giao th&ocirc;ng v&agrave; &ocirc; nhiễm m&ocirc;i trường; lồng gh&eacute;p v&agrave;o c&aacute;c quy định ph&aacute;p luật, chiến lược, quy hoạch của ng&agrave;nh giao th&ocirc;ng vận tải; phối hợp với Ủy ban nh&acirc;n d&acirc;n th&agrave;nh phố H&agrave; Nội v&agrave; Th&agrave;nh phố Hồ Ch&iacute; Minh thực hiện c&aacute;c Đề &aacute;n đ&atilde; được ph&ecirc; duyệt của hai th&agrave;nh phố về hạn chế sử dụng phương tiện c&aacute; nh&acirc;n nhằm bảo đảm an to&agrave;n giao th&ocirc;ng, chống &ugrave;n tắc giao th&ocirc;ng v&agrave; &ocirc; nhiễm m&ocirc;i trường.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Chủ tr&igrave;, phối hợp với Bộ C&ocirc;ng an nghi&ecirc;n cứu, đề xuất cải tiến Giấy ph&eacute;p điều khiển phương tiện giao th&ocirc;ng được t&iacute;ch hợp c&aacute;c th&ocirc;ng tin cần thiết cho c&ocirc;ng t&aacute;c quản l&yacute; đối với người điều khiển phương tiện giao th&ocirc;ng, đồng thời đảm bảo kết nối, chia sẻ dữ liệu d&ugrave;ng chung trong c&ocirc;ng t&aacute;c cấp Giấy ph&eacute;p l&aacute;i xe, phục vụ c&ocirc;ng t&aacute;c quản l&yacute; l&aacute;i xe v&agrave; xử l&yacute; vi phạm trật tự an to&agrave;n giao th&ocirc;ng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Tăng cường tuần tra kiểm so&aacute;t, ch&uacute; trọng tuần tra lưu động</span></span></span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Bộ C&ocirc;ng an ho&agrave;n thiện, tr&igrave;nh ban h&agrave;nh quy định về thống k&ecirc; v&agrave; chia sẻ dữ liệu về tai nạn giao th&ocirc;ng; r&agrave; so&aacute;t, hệ thống h&oacute;a v&agrave; sửa đổi, bổ sung để tr&igrave;nh cấp thẩm quyền ban h&agrave;nh hoặc ban h&agrave;nh theo thẩm quyền c&aacute;c văn bản quy phạm ph&aacute;p luật về bảo đảm trật tự an to&agrave;n giao th&ocirc;ng cho ph&ugrave; hợp với t&igrave;nh h&igrave;nh mới; x&acirc;y dựng Quy chế phối hợp giữa Bộ C&ocirc;ng an v&agrave; Bộ Giao th&ocirc;ng vận tải để trao đổi c&aacute;c th&ocirc;ng tin phục vụ quản l&yacute; nh&agrave; nước v&agrave; kiểm tra, xử l&yacute; vi phạm ph&aacute;p luật về trật tự an to&agrave;n giao th&ocirc;ng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Lập đề &aacute;n đầu tư lắp đặt camera để gi&aacute;m s&aacute;t, chỉ huy điều h&agrave;nh giao th&ocirc;ng v&agrave; xử l&yacute; vi phạm giao th&ocirc;ng tr&ecirc;n địa b&agrave;n cả nước; x&acirc;y dựng v&agrave; vận h&agrave;nh Trung t&acirc;m chỉ huy điều khiển giao th&ocirc;ng đảm bảo kết nối v&agrave; xử l&yacute; dữ liệu camera từ c&aacute;c cơ quan, tổ chức kh&aacute;c để phục vụ c&ocirc;ng t&aacute;c bảo đảm an to&agrave;n giao th&ocirc;ng gắn với an ninh trật tự; trước mắt đến năm 2020 triển khai th&iacute; điểm tại H&agrave; Nội v&agrave; Th&agrave;nh phố Hồ Ch&iacute; Minh v&agrave; tiến tới triển khai tr&ecirc;n to&agrave;n quốc từ năm 2022.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">X&acirc;y dựng cơ sở dữ liệu quốc gia về tai nạn giao th&ocirc;ng v&agrave; vi phạm trật tự an to&agrave;n giao th&ocirc;ng; &aacute;p dụng thống nhất việc định danh c&aacute;c khoản thu phạt vi phạm h&agrave;nh ch&iacute;nh trong lĩnh vực trật tự an to&agrave;n giao th&ocirc;ng; kết nối với cơ sở dữ liệu quốc gia về xử l&yacute; vi phạm h&agrave;nh ch&iacute;nh v&agrave; chia sẻ th&ocirc;ng tin với Bộ Giao th&ocirc;ng vận tải, Bộ Y tế, Bộ Tư ph&aacute;p v&agrave; Ủy ban nh&acirc;n d&acirc;n c&aacute;c tỉnh, th&agrave;nh phố trực thuộc trung ương, ho&agrave;n th&agrave;nh trong năm 2020.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Tăng cường tuần tra kiểm so&aacute;t, ch&uacute; trọng tuần tra lưu động; đẩy mạnh ứng dụng c&ocirc;ng nghệ cao, thiết bị kỹ thuật nghiệp vụ cũng như phương &aacute;n sử dụng thiết bị ghi h&igrave;nh của c&aacute;c tổ chức được giao quản l&yacute;, khai th&aacute;c hệ thống kết cấu hạ tầng giao th&ocirc;ng để ph&aacute;t hiện v&agrave; xử l&yacute; c&aacute;c h&agrave;nh vi vi phạm quy định về trật tự, an to&agrave;n giao th&ocirc;ng, trong đ&oacute; ưu ti&ecirc;n xử l&yacute; h&agrave;nh vi điều khiển phương tiện vi phạm nồng độ cồn, vi phạm quy định tốc độ, sử dụng điện thoại di động, kh&ocirc;ng thắt d&acirc;y an to&agrave;n khi ngồi tr&ecirc;n &ocirc; t&ocirc;, kh&ocirc;ng đội mũ bảo hiểm khi đi m&ocirc; t&ocirc;, xe m&aacute;y, vi phạm quy định về an to&agrave;n, kỹ thuật v&agrave; tải trọng của xe &ocirc; t&ocirc; chở kh&aacute;ch từ 9 chỗ trở l&ecirc;n v&agrave; xe &ocirc; t&ocirc; tải c&oacute; trọng tải từ 10 tấn trở l&ecirc;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Bộ Y tế x&acirc;y dựng v&agrave; triển khai c&aacute;c văn bản hướng dẫn thi h&agrave;nh Luật ph&ograve;ng chống t&aacute;c hại của rượu, bia ngay sau khi Quốc hội th&ocirc;ng qua; sửa đổi, bổ sung v&agrave; ban h&agrave;nh quy định bắt buộc về kiểm tra nồng độ cồn, c&aacute;c chất k&iacute;ch th&iacute;ch thần kinh trong m&aacute;u đối với tất cả người bị tai nạn giao th&ocirc;ng v&agrave;o cấp cứu, kh&aacute;m chữa bệnh tại c&aacute;c cơ sở y tế, cung cấp th&ocirc;ng tin cho ng&agrave;nh C&ocirc;ng an v&agrave; c&aacute;c cơ quan tư ph&aacute;p.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">B&ecirc;n cạnh đ&oacute;, lập đề &aacute;n tăng cường năng lực hoạt động cấp cứu tai nạn giao th&ocirc;ng cho hệ thống cấp cứu 115 v&agrave; Bệnh viện đa khoa cấp huyện tr&ecirc;n to&agrave;n quốc, đảm bảo khả năng tiếp cận nạn nh&acirc;n trong thời gian dưới 30 ph&uacute;t sau khi nhận th&ocirc;ng tin y&ecirc;u cầu cấp cứu, bắt đầu đưa v&agrave;o triển khai từ năm 2020, ho&agrave;n th&agrave;nh v&agrave;o năm 2025; lập đề &aacute;n n&acirc;ng cao năng lực sơ cấp cứu tai nạn giao th&ocirc;ng cho c&aacute;n bộ y tế cấp x&atilde;, th&ocirc;n, bản, bắt đầu đưa v&agrave;o triển khai từ năm 2020; x&acirc;y dựng t&agrave;i liệu hướng dẫn sơ cấp cứu tại hiện trường tai nạn cho người tham gia giao th&ocirc;ng; ban h&agrave;nh hướng dẫn trang bị dụng bộ cụ cứu thương tr&ecirc;n c&aacute;c phương tiện giao th&ocirc;ng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Siết chặt kỷ cương c&ocirc;ng t&aacute;c đ&agrave;o tạo, s&aacute;t hạch, cấp giấy ph&eacute;p l&aacute;i xe</span></span></span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Ủy ban nh&acirc;n d&acirc;n c&aacute;c tỉnh, th&agrave;nh phố trực thuộc trung ương x&acirc;y dựng Kế hoạch bảo đảm trật tự an to&agrave;n giao th&ocirc;ng v&agrave; chống &ugrave;n tắc giao th&ocirc;ng tr&ecirc;n địa b&agrave;n giai đoạn 2019 - 2021 v&agrave; kế hoạch triển khai, dự to&aacute;n ng&acirc;n s&aacute;ch thực hiện h&agrave;ng năm tr&igrave;nh Hội đồng nh&acirc;n d&acirc;n th&ocirc;ng qua để thực hiện ph&ugrave; hợp điều kiện thực tế của địa phương; sử dụng nguồn kinh ph&iacute; xử phạt vi phạm h&agrave;nh ch&iacute;nh về trật tự an to&agrave;n giao th&ocirc;ng cho nhiệm vụ bảo đảm an to&agrave;n giao th&ocirc;ng, chống &ugrave;n tắc giao th&ocirc;ng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">X&aacute;c định mục ti&ecirc;u về bảo đảm trật tự an to&agrave;n giao th&ocirc;ng, chống &ugrave;n tắc giao th&ocirc;ng v&agrave; giảm &ocirc; nhiễm m&ocirc;i trường từ hoạt động giao th&ocirc;ng vận tải. Gi&aacute;m s&aacute;t, tổng hợp t&igrave;nh h&igrave;nh vi phạm quy định trong quản l&yacute; quy hoạch v&agrave; x&acirc;y dựng c&oacute; li&ecirc;n quan đến trật tự an to&agrave;n giao th&ocirc;ng, chống &ugrave;n tắc giao th&ocirc;ng tr&ecirc;n địa b&agrave;n tỉnh, th&agrave;nh phố.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Chỉ đạo c&aacute;c sở, ng&agrave;nh v&agrave; địa phương tr&ecirc;n địa b&agrave;n trao đổi, chia sẻ th&ocirc;ng tin nhằm tăng cường c&ocirc;ng t&aacute;c phối hợp trong c&ocirc;ng t&aacute;c bảo đảm trật tự an to&agrave;n giao th&ocirc;ng v&agrave; tổ chức giao th&ocirc;ng; đồng thời, ứng dụng mạnh mẽ khoa học c&ocirc;ng nghệ trong quản l&yacute; hoạt động vận tải v&agrave; tổ chức giao th&ocirc;ng tr&ecirc;n địa b&agrave;n, kết nối, t&iacute;ch hợp với hệ thống điều h&agrave;nh chung của bộ, ng&agrave;nh ở trung ương để triển khai đồng bộ kể từ năm 2022.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Đẩy mạnh tuy&ecirc;n truyền gi&aacute;o dục, phổ biến quy định ph&aacute;p luật về an to&agrave;n giao th&ocirc;ng, chống &ugrave;n tắc giao th&ocirc;ng v&agrave; &ocirc; nhiễm m&ocirc;i trường; ph&aacute;t huy vai tr&ograve; của c&aacute;c tổ chức ch&iacute;nh trị x&atilde; hội, c&aacute;c loại h&igrave;nh văn h&oacute;a nghệ thuật d&acirc;n tộc, c&aacute;c c&ocirc;ng cụ truyền th&ocirc;ng hiện đại để tạo chuyển biến trong h&agrave;nh vi của người tham gia giao th&ocirc;ng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Chỉ đạo c&aacute;c ng&agrave;nh chức năng tăng cường tuần tra, kiểm so&aacute;t xử l&yacute; nghi&ecirc;m c&aacute;c vi phạm quy định về trật tự an to&agrave;n giao th&ocirc;ng; kiểm tra, siết chặt kỷ cương c&ocirc;ng t&aacute;c đ&agrave;o tạo, s&aacute;t hạch, cấp giấy ph&eacute;p l&aacute;i xe; quản l&yacute; an to&agrave;n kỹ thuật v&agrave; bảo vệ m&ocirc;i trường đối với phương tiện cơ giới đường bộ, phương tiện thuỷ nội địa; an to&agrave;n trong hoạt động vận tải đường bộ; đường thủy nội địa; tăng cường c&ocirc;ng t&aacute;c kiểm so&aacute;t tải trọng phương tiện giao th&ocirc;ng v&agrave; c&aacute;c giải ph&aacute;p nhằm n&acirc;ng cao hiệu quả thực hiện quy định bắt buộc đội mũ bảo hiểm đối với người đi m&ocirc; t&ocirc;, xe gắn m&aacute;y, xe đạp điện.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Chỉ đạo Ủy ban nh&acirc;n d&acirc;n c&aacute;c cấp vận động c&aacute;c đo&agrave;n thể ch&iacute;nh trị - x&atilde; hội tr&ecirc;n địa b&agrave;n tham gia cảnh giới, bảo đảm an to&agrave;n giao th&ocirc;ng tại điểm giao cắt đường bộ với đường sắt kh&ocirc;ng c&oacute; r&agrave;o chắn; sử dụng quỹ bảo tr&igrave; đường bộ v&agrave; huy động nh&acirc;n d&acirc;n tham gia l&agrave;m gờ giảm tốc v&agrave; cắm biển cảnh b&aacute;o nguy hiểm từ đường phụ ra đường ch&iacute;nh, lối đi tự mở qua đường sắt; gắn tr&aacute;ch nhiệm v&agrave; xử l&yacute; kỷ luật đối với người đứng đầu cấp huyện, x&atilde; nếu để ph&aacute;t sinh lối đi tự mở qua đường sắt hoặc đấu nối c&ocirc;ng tr&igrave;nh v&agrave;o đường bộ tr&aacute;i ph&eacute;p.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Kh&ocirc;ng xem x&eacute;t c&aacute;c h&igrave;nh thức thi đua, khen thưởng nh&agrave; nước đối với tập thể Ủy ban nh&acirc;n d&acirc;n cấp tỉnh, huyện, x&atilde; v&agrave; Chủ tịch Ủy ban nh&acirc;n d&acirc;n - Trưởng Ban An to&agrave;n giao th&ocirc;ng tỉnh, huyện nếu trong năm để xảy ra tai nạn giao th&ocirc;ng đặc biệt nghi&ecirc;m trọng tr&ecirc;n địa b&agrave;n m&agrave; nguy&ecirc;n nh&acirc;n đ&atilde; được Ch&iacute;nh phủ, Thủ tướng Ch&iacute;nh phủ chỉ đạo nhưng chưa khắc phục (điểm đen về tai nạn giao th&ocirc;ng, đường d&acirc;n sinh, lối đi tự mở, đường ngang đường sắt...).</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Khẩn trương ho&agrave;n thiện, cụ thể h&oacute;a đồ &aacute;n Quy hoạch giao th&ocirc;ng vận tải</span></span></span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Ủy ban nh&acirc;n c&aacute;c th&agrave;nh phố: H&agrave; Nội, Hải Ph&ograve;ng, Đ&agrave; Nẵng, Cần Thơ, Th&agrave;nh phố Hồ Ch&iacute; Minh chủ tr&igrave;, phối hợp với c&aacute;c bộ, ng&agrave;nh c&oacute; li&ecirc;n quan khẩn trương ho&agrave;n thiện, cụ thể h&oacute;a đồ &aacute;n Quy hoạch giao th&ocirc;ng vận tải đến năm 2030 tầm nh&igrave;n đến năm 2050 đ&atilde; được Thủ tướng Ch&iacute;nh phủ ph&ecirc; duyệt; x&acirc;y dựng phương &aacute;n quy hoạch kh&ocirc;ng gian ngầm, mặt đất v&agrave; kh&ocirc;ng gian tr&ecirc;n cao, tr&igrave;nh Ch&iacute;nh phủ ph&ecirc; duyệt; tổ chức lập, ph&ecirc; duyệt c&aacute;c đồ &aacute;n quy hoạch sử dụng c&aacute;c quỹ đất sau khi di dời c&aacute;c cơ quan trung ương, trường học, bệnh viện&hellip; khỏi khu vực nội th&agrave;nh, gắn với bố tr&iacute; quỹ đất v&agrave; hạ tầng phục vụ c&aacute;c cơ sở mới trước khi phải di dời ra khỏi khu vực nội th&agrave;nh.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Chỉ ph&ecirc; duyệt đầu tư c&aacute;c khu chung cư, nh&agrave; cao tầng, trung t&acirc;m thương mại khi ph&ugrave; hợp với quy hoạch, đ&aacute;p ứng y&ecirc;u cầu v&agrave; quy định về b&atilde;i đỗ xe, hạ tầng giao th&ocirc;ng v&agrave; kết nối giao th&ocirc;ng với c&aacute;c trục đường ch&iacute;nh trong đ&ocirc; thị; ưu ti&ecirc;n ng&acirc;n s&aacute;ch v&agrave; huy động c&aacute;c nguồn x&atilde; hội qu&aacute; để đẩy nhanh tiến độ triển khai thực hiện c&aacute;c Chương tr&igrave;nh, Kế hoạch bảo đảm trật tự an to&agrave;n giao th&ocirc;ng, chống &ugrave;n tắc giao th&ocirc;ng v&agrave; &ocirc; nhiễm m&ocirc;i trường đ&atilde; được cấp thẩm quyền ph&ecirc; duyệt.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Đẩy nhanh tiến độ đầu tư x&acirc;y dựng c&aacute;c c&ocirc;ng tr&igrave;nh kết cấu hạ tầng giao th&ocirc;ng đ&ocirc; thị, đặc biệt l&agrave; c&aacute;c trục hướng t&acirc;m, kh&eacute;p k&iacute;n c&aacute;c đường v&agrave;nh đai, c&aacute;c dự &aacute;n đường sắt đ&ocirc; thị tại H&agrave; Nội, Th&agrave;nh phố Hồ Ch&iacute; Minh, bến, b&atilde;i đỗ xe, cảng thuỷ nội địa; c&aacute;c c&ocirc;ng tr&igrave;nh hạ tầng giao th&ocirc;ng kết nối cảng h&agrave;ng kh&ocirc;ng, cảng biển trọng điểm tr&ecirc;n địa b&agrave;n; tăng cường c&ocirc;ng t&aacute;c kiểm tra duy tu, duy tr&igrave;, đảm bảo cầu, đường &ecirc;m thuận phục giao th&ocirc;ng th&ocirc;ng suốt, an to&agrave;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Khẩn trương x&acirc;y dựng trung t&acirc;m quản l&yacute; điều h&agrave;nh giao th&ocirc;ng đ&ocirc; thị gắn với cơ sở dữ liệu kết nối to&agrave;n bộ hệ thống gi&aacute;m s&aacute;t h&agrave;nh tr&igrave;nh xe &ocirc; t&ocirc;, camera giao th&ocirc;ng, đ&egrave;n t&iacute;n hiệu giao th&ocirc;ng trong đ&ocirc; thị tr&ecirc;n c&aacute;c tuyến đường để quản l&yacute; điều h&agrave;nh giao th&ocirc;ng, xử l&yacute; vi phạm; cung cấp th&ocirc;ng tin trực tuyến v&agrave; hướng dẫn tham gia giao th&ocirc;ng; kết hợp bảo vệ an ninh trật tự, ph&ograve;ng chống tội phạm.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Thường xuy&ecirc;n r&agrave; so&aacute;t, điều chỉnh tổ chức giao th&ocirc;ng tại c&aacute;c n&uacute;t giao th&ocirc;ng, c&aacute;c tuyến đường theo thẩm quyền; cấm, hạn chế hoạt động phương tiện cơ giới c&aacute; nh&acirc;n để ưu ti&ecirc;n hoạt động của phương tiện cứu hộ v&agrave; phương tiện giao th&ocirc;ng c&ocirc;ng cộng khi cần thiết; điều chỉnh, đồng bộ bề rộng l&ograve;ng đường, vỉa h&egrave; nhằm tối ưu h&oacute;a khả năng th&ocirc;ng qua, tr&aacute;nh &ugrave;n tắc; tăng cường bảo đảm tr&acirc;̣t tự giao th&ocirc;ng đ&ocirc; thị, quản l&yacute; sử dụng vỉa h&egrave; đảm bảo quyền ưu ti&ecirc;n d&agrave;nh cho người đi bộ; sắp xếp hợp l&yacute; c&aacute;c hoạt động sử dụng tạm thời vỉa h&egrave;, l&ograve;ng đường để ưu ti&ecirc;n l&agrave;m điểm tr&ocirc;ng giữ xe, hoặc kinh doanh, bu&ocirc;n b&aacute;n... đảm bảo trật tự, an to&agrave;n giao th&ocirc;ng v&agrave; hạn chế l&agrave;m ảnh hưởng đến đời sống của bộ phận người d&acirc;n ngh&egrave;o sống phụ thuộc kinh doanh, bu&ocirc;n b&aacute;n nhỏ; r&agrave; so&aacute;t, tổ chức hợp l&yacute; c&aacute;c khu vực gầm cầu, gầm đường tr&ecirc;n cao để sử dụng tạm thời l&agrave;m b&atilde;i đỗ xe.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">N&acirc;ng cao năng lực phản ứng với sự cố giao th&ocirc;ng, khả năng ứng ph&oacute; với biến đổi kh&iacute; hậu (mưa lớn, ngập lụt, triều cường v&agrave; c&aacute;c hiện tượng biến đổi kh&iacute; hậu kh&aacute;c) của hệ thống kế cấu hạ tầng n&oacute;i chung v&agrave; hệ thống kết cấu hạ tầng giao th&ocirc;ng n&oacute;i ri&ecirc;ng; nghi&ecirc;n cứu c&aacute;c phương &aacute;n tổ chức giao th&ocirc;ng ứng ph&oacute; t&igrave;nh trạng &uacute;ng ngập cục bộ, đặc biệt l&agrave; đối với địa b&agrave;n Th&agrave;nh phố Hồ Ch&iacute; Minh, Cần Thơ.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Tiếp tục x&atilde; hội h&oacute;a ph&aacute;t triển vận tải h&agrave;nh kh&aacute;ch c&ocirc;ng cộng v&agrave; dịch vụ hỗ trợ vận tải; mở c&aacute;c tuyến bu&yacute;t kế cận từ trung t&acirc;m đ&ocirc; thị kết nối với địa phương xung quanh; lựa chọn xe bu&yacute;t c&oacute; sức chứa ph&ugrave; hợp với hạ tầng v&agrave; nhu cầu đi lại; tổ chức xe bu&yacute;t kết nối với c&aacute;c nh&agrave; ga đường sắt quốc gia, bến xe kh&aacute;ch, ga đường sắt đ&ocirc; thị; sắp x&ecirc;́p hợp lý lu&ocirc;̀ng tuy&ecirc;́n v&acirc;̣n tải hành khách cố định li&ecirc;n tỉnh vào các b&ecirc;́n xe khách; tiếp tục bố tr&iacute; kinh ph&iacute; để trợ gi&aacute; cho h&agrave;nh kh&aacute;ch v&agrave; hỗ trợ ph&aacute;t triển xe bu&yacute;t th&acirc;n thiện với m&ocirc;i trường theo quy định hiện h&agrave;nh. Ủy ban nh&acirc;n d&acirc;n th&agrave;nh phố H&agrave; Nội, Th&agrave;nh phố Hồ Ch&iacute; Minh phối hợp với Bộ T&agrave;i ch&iacute;nh, Bộ Giao th&ocirc;ng vận tải x&acirc;y dựng kế hoạch, bố tr&iacute; nguồn kinh ph&iacute; để hỗ trợ ph&aacute;t triển phương tiện vận tải h&agrave;nh kh&aacute;ch c&ocirc;ng cộng bằng xe bu&yacute;t sử dụng năng lượng điện v&agrave; nhi&ecirc;n liệu kh&aacute;c th&acirc;n thiện với m&ocirc;i trường.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Khuyến kh&iacute;ch c&aacute;c bến xe x&atilde; hội h&oacute;a đầu tư (bến xe hiện hữu v&agrave; c&aacute;c bến xe mới) n&acirc;ng cấp x&acirc;y dựng th&agrave;nh bến xe cao tầng, gắn với tổ hợp thương mại, đồng thời tổ chức c&aacute;c loại h&igrave;nh vận tải h&agrave;nh kh&aacute;ch kh&aacute;c như: Xe bu&yacute;t, taxi, xe du lịch, xe bốn b&aacute;nh c&oacute; gắn động cơ (chạy điện hoặc xăng sinh học) để kết nối với điểm đầu, cuối xe bu&yacute;t khối lượng lớn, ga đường sắt đ&ocirc; thị, ga đường sắt chở kh&aacute;ch, cảng h&agrave;ng kh&ocirc;ng.</span></span></span></p>\r\n\r\n<p>Nguồn: Chinhphu.vn</p>\r\n',NULL,NULL,'6',0,NULL,NULL),(7,'Sửa quy định về sử dụng kinh phí bảo đảm trật tự an toàn giao thông',0,NULL,'<p><span style=\"color:rgb(0, 0, 0); font-family:arial,sans-serif; font-size:14px\">Theo đ&oacute;, dự thảo bổ sung: Nội dung chi đặc th&ugrave; bảo đảm TTATGT của Ủy ban An to&agrave;n giao th&ocirc;ng Quốc gia, Bộ Giao th&ocirc;ng vận tải (bổ sung điểm e, g, Khoản 2, Điều 3) th&ecirc;m c&aacute;c chi ph&iacute; sau: </span></p>\r\n\r\n<div style=\"page-break-after: always\"><span style=\"display:none\">&nbsp;</span></div>\r\n\r\n<p><span style=\"color:rgb(0, 0, 0); font-family:arial,sans-serif; font-size:14px\">Chi ph&iacute; vận h&agrave;nh số điện thoại đường d&acirc;y n&oacute;ng về TTATGT; chi thực hiện c&aacute;c nhiệm vụ phối hợp tăng cường bảo đảm TTATGT ph&aacute;t sinh đột xuất trong năm với c&aacute;c Bộ, ng&agrave;nh, cơ quan trực thuộc trung ương.</span></p>\r\n\r\n<p style=\"text-align:center\"><a class=\"view-gallery\" href=\"https://drvn.gov.vn/upload/20830/fck/dam-bao-trat-tu.jpg\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(46, 46, 46); text-decoration-line: none;\"><img alt=\"\" src=\"https://drvn.gov.vn/upload/20830/fck/dam-bao-trat-tu.jpg\" style=\"box-sizing:border-box; height:259px; max-width:100%; vertical-align:middle; width:460px\" /></a></p>\r\n\r\n<p style=\"text-align:center\">Ảnh minh họa</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Đồng thời dự thảo bổ sung: Nội dung chi đặc th&ugrave; bảo đảm TTATGT của địa phương (bổ sung điểm h, i, Khoản 4, Điều 3) như sau: Chi ph&iacute; vận h&agrave;nh số điện thoại đường d&acirc;y n&oacute;ng về TTATGT; chi xử l&yacute; điểm đen, điểm tiềm ẩn tai nạn giao th&ocirc;ng theo quy định của Bộ trưởng Bộ Giao th&ocirc;ng vận tải về việc x&aacute;c định v&agrave; xử l&yacute; vị tr&iacute; nguy hiểm tr&ecirc;n đường bộ đang khai th&aacute;c, đảm bảo kh&ocirc;ng tr&ugrave;ng lắp với c&aacute;c nguồn kinh ph&iacute; kh&aacute;c.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">B&ecirc;n cạnh đ&oacute;, dự thảo sửa đổi, bổ sung quy định về &ldquo;Lập, ph&acirc;n bổ, chấp h&agrave;nh v&agrave; quyết to&aacute;n kinh ph&iacute; bảo đảm TTATGT&rdquo;.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Cụ thể, đối với địa phương: Sở Giao th&ocirc;ng vận tải, C&ocirc;ng an tỉnh, th&agrave;nh phố trực thuộc trung ương, Ban An to&agrave;n giao th&ocirc;ng c&aacute;c cấp lập dự to&aacute;n kinh ph&iacute; đảm bảo TTATGT theo nhiệm vụ được giao gửi cơ quan t&agrave;i ch&iacute;nh c&ugrave;ng cấp xem x&eacute;t, tổng hợp chung v&agrave;o dự to&aacute;n chi ng&acirc;n s&aacute;ch h&agrave;ng năm b&aacute;o c&aacute;o Ủy ban nh&acirc;n d&acirc;n cấp tỉnh, để tr&igrave;nh Hội đồng nh&acirc;n d&acirc;n tỉnh theo quy định. Ri&ecirc;ng đối với C&ocirc;ng an tỉnh, th&agrave;nh phố trực thuộc trung ương lập dự to&aacute;n bao gồm cả kinh ph&iacute; thực hiện nhiệm vụ đảm bảo TTATGT của C&ocirc;ng an huyện, quận, thị x&atilde;, th&agrave;nh phố thuộc tỉnh, C&ocirc;ng an phường, thị trấn gửi cơ quan t&agrave;i ch&iacute;nh c&ugrave;ng cấp địa phương, đồng gửi Bộ C&ocirc;ng an để theo d&otilde;i, quản l&yacute; chung lực lượng của ng&agrave;nh C&ocirc;ng an.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Ủy ban nh&acirc;n d&acirc;n cấp tỉnh tr&igrave;nh Hội đồng nh&acirc;n d&acirc;n c&ugrave;ng cấp quyết định sử dụng nguồn kinh ph&iacute; ng&acirc;n s&aacute;ch trung ương bổ sung c&oacute; mục ti&ecirc;u cho ng&acirc;n s&aacute;ch địa phương từ nguồn thu xử phạt vi phạm h&agrave;nh ch&iacute;nh trong lĩnh vực an to&agrave;n giao th&ocirc;ng v&agrave; nguồn ng&acirc;n s&aacute;ch địa phương để thực hiện c&aacute;c nhiệm vụ bảo đảm TTATGT tr&ecirc;n địa b&agrave;n; quyết định cụ thể mức hỗ trợ cho lực lượng c&ocirc;ng an tỉnh, th&agrave;nh phố trực thuộc trung ương để thực hiện nhiệm vụ đảm bảo TTATGT tr&ecirc;n địa b&agrave;n theo quy định tại điểm b, khoản 9, Điều 9 Luật Ng&acirc;n s&aacute;ch nh&agrave; nước.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Ri&ecirc;ng năm 2019, Hội đồng nh&acirc;n d&acirc;n cấp tỉnh chủ động sử dụng nguồn kinh ph&iacute; xử phạt vi phạm h&agrave;nh ch&iacute;nh về TTATGT được điều tiết từ ng&acirc;n s&aacute;ch trung ương về ng&acirc;n s&aacute;ch địa phương để thực hiện c&aacute;c nhiệm vụ bảo đảm TTATGT, ưu ti&ecirc;n cho lực lượng C&ocirc;ng an, Thanh tra giao th&ocirc;ng vận tải v&agrave; xử l&yacute; điểm đen, điểm tiềm ẩn tai nạn giao th&ocirc;ng theo quy định tại Quyết định số 1629/QĐ-TTg ng&agrave;y 23/11/2018 của Thủ tướng Ch&iacute;nh phủ về việc giao dự to&aacute;n ng&acirc;n s&aacute;ch năm 2019.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Kinh ph&iacute; địa phương bố tr&iacute; để lực lượng C&ocirc;ng an tỉnh, th&agrave;nh phố trực thuộc trung ương thực hiện c&aacute;c nhiệm vụ bảo đảm TTATGT tr&ecirc;n địa b&agrave;n được thực hiện bằng h&igrave;nh thức r&uacute;t dự to&aacute;n.</span></span></span></p>\r\n\r\n<p>Nguồn: Chinhphu.vn</p>\r\n',NULL,NULL,'7',0,NULL,NULL),(8,'Tăng cường công tác bảo đảm TTATGT sau Tết',0,NULL,'<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Thứ trưởng Bộ GTVT L&ecirc; Đ&igrave;nh Thọ vừa k&yacute; văn bản số 1175/ BGTVT-ATGTgửi Tổng cục Đường bộ Việt Nam, c&aacute;c Cục Quản l&yacute; chuy&ecirc;n ng&agrave;nh, c&aacute;c Sở Giao th&ocirc;ng vận tải v&agrave; Tổng c&ocirc;ng ty Đường sắt Việt Nam về việc tăng cường c&ocirc;ng t&aacute;c bảo đảm TTATGT sau Tết Nguy&ecirc;n đ&aacute;n Kỷ Hợi 2019 v&agrave; Lễ hội Xu&acirc;n 2019.</span></span></strong></p>\r\n\r\n<div style=\"page-break-after: always\"><span style=\"display:none\">&nbsp;</span></div>\r\n\r\n<p style=\"text-align:center\"><a class=\"view-gallery\" href=\"https://drvn.gov.vn/upload/20830/fck/bao_dam_an_toan_giao_thong.jpg\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(46, 46, 46); text-decoration-line: none;\"><img alt=\"\" src=\"https://drvn.gov.vn/upload/20830/fck/bao_dam_an_toan_giao_thong.jpg\" style=\"box-sizing:border-box; height:400px; max-width:100%; vertical-align:middle; width:600px\" /></a></p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Ảnh minh họa</span></span></em></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Văn bản n&ecirc;u r&otilde;, trong 6 ng&agrave;y nghỉ, Tết Nguy&ecirc;n đ&aacute;n Kỷ Hợi 2019, t&igrave;nh h&igrave;nh TTATGT c&oacute; chuyển biến t&iacute;ch cực, TNGT giảm cả 3 ti&ecirc;u ch&iacute; về số vụ, số người chết v&agrave; số người bị thương so với c&ugrave;ng kỳ năm 2018, điều đ&oacute; chứng tỏ sự v&agrave;o cuộc quyết liệt của cả hệ thống ch&iacute;nh trị từ Trung ương đến địa phương, sự phối hợp chặt chẽ giữa c&aacute;c lực lượng c&ocirc;ng an v&agrave; c&aacute;c lực lượng của ng&agrave;nh GTVT đ&atilde; đem lại kết quả r&otilde; dệt.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Trong Văn bản, Thứ trưởng L&ecirc; Đ&igrave;nh Thọ y&ecirc;u cầu: Để phục vụ tốt nhu cầu đi lại của nh&acirc;n d&acirc;n trong c&aacute;c ng&agrave;y c&ograve;n lại của Tết Nguy&ecirc;n đ&aacute;n Kỷ Hợi 2019 v&agrave; Lễ hội Xu&acirc;n 2019 v&agrave; &yacute; kiến của Ph&oacute; Thủ tướng Thường trực Ch&iacute;nh phủ, Chủ tịch Ủy ban ATGT Quốc gia Trương H&ograve;a B&igrave;nh, Bộ GTVT y&ecirc;u cầu Tổng cục Đường bộ Việt Nam chỉ đạo c&aacute;c Cục Quản l&yacute; đường bộ kiểm tra, chỉ đạo c&aacute;c Trạm thu ph&iacute; đường bộ tổ chức ph&acirc;n l&agrave;n giao th&ocirc;ng hợp l&yacute; kịp, thời xử l&yacute; giải tỏa phương ph&aacute;p tiện, kh&ocirc;ng để xảy ra t&igrave;nh trạng &ugrave;n tắc tắc giao th&ocirc;ng tại c&aacute;c Trạm thu ph&iacute;; chủ động mở barie để giải tỏa phương tiện khi xảy ra &ugrave;n tắc giao th&ocirc;ng k&eacute;o d&agrave;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Chỉ đạo c&aacute;c doanh nghiệp vận tải tu&acirc;n thủ quy tắc giao th&ocirc;ng, ph&ograve;ng ngừa tai nạn đường đ&egrave;o dốc, đường ngang đường sắt; phối hợp với c&aacute;c Sở Giao th&ocirc;ng vận tải để quản l&yacute; hoạt động của phương tiện, xử l&yacute; vi phạm theo quy định.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Bộ GTVT y&ecirc;u cầu c&aacute;c Cục quản l&yacute; chuy&ecirc;n ng&agrave;nh căn cứ chức năng, nhiệm vụ được giao chỉ đạo tổ chức thực hiện tốt c&aacute;c n&ocirc;̣i dung cụ thể n&ecirc;u trong văn bản.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Bộ GTVT y&ecirc;u cầu c&aacute;c Sở Giao th&ocirc;ng vận tải tăng cường kiểm tra ph&aacute;t hiện xử l&yacute; nghi&ecirc;m c&aacute;c đơn vị vận tải c&oacute; h&agrave;nh vi vi phạm trong hoạt động vận tải.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Chỉ đạo lực lượng Thanh tra giao th&ocirc;ng, c&aacute;c đơn vị Đăng kiểm trực thuộc phối hợp với c&aacute;c đơn vị li&ecirc;n quan phương tiện, người điều khiển phương tiện tại c&aacute;c bến xe, nhắc nhở l&aacute;i xe, h&agrave;nh kh&aacute;ch thắt d&acirc;y an to&agrave;n trước khi xuất bến, xử l&yacute; nghi&ecirc;m trường hợp vi phạm, kh&ocirc;ng đủ điều kiện an to&agrave;n, người điều khiển phương tiện kh&ocirc;ng c&oacute; giấy ph&eacute;p l&aacute;i xe theo quy định, sử dụng rượu, bia, ma t&uacute;y khi điề khiển phương tiện, phối hợp với CSGT kiểm tra điều kiện an to&agrave;n kỹ thuật của phương tiện, điều khiển phương tiện tham gia giao th&ocirc;ng tr&ecirc;n c&aacute;c tuyến đường bộ.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Đồng thời chủ động phối hợp với c&aacute;c cơ quan chức năng của Tổng cục Đường bộ Việt Nam để quản l&yacute; hoạt động của phương tiện, xử l&yacute; vi phạm theo quy định.</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Nguồn: Bộ GTVT</span></span></p>\r\n',NULL,NULL,'8',0,NULL,NULL),(9,'Thực hiện nghiêm quy định về khám sức khoẻ định kỳ cho lái xe',0,NULL,'<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">C&aacute;c đơn vị kinh doanh vận tải bằng xe &ocirc; t&ocirc; thực hiện nghi&ecirc;m quy định về kh&aacute;m sức khoẻ định kỳ cho l&aacute;i xe, trước hết l&agrave; l&aacute;i xe c&oacute; khối lượng lớn, xe c&ocirc;ng-ten-nơ (ho&agrave;n th&agrave;nh trong qu&yacute; I năm 2019).</span></span></strong></p>\r\n\r\n<div style=\"page-break-after: always\"><span style=\"display:none\">&nbsp;</span></div>\r\n\r\n<p style=\"text-align:center\"><a class=\"view-gallery\" href=\"https://drvn.gov.vn/upload/20830/fck/%E1%BA%A2nh%20minh%20h%E1%BB%8Dask28_1.jpg\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(46, 46, 46); text-decoration-line: none;\"><img alt=\"\" src=\"https://drvn.gov.vn/upload/20830/fck/%E1%BA%A2nh%20minh%20h%E1%BB%8Dask28_1.jpg\" style=\"box-sizing:border-box; height:488px; max-width:100%; vertical-align:middle; width:650px\" /></a></p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Ảnh minh họa</span></span></em></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Văn ph&ograve;ng Ch&iacute;nh phủ vừa c&oacute;&nbsp;<a href=\"http://vanban.chinhphu.vn/portal/page/portal/chinhphu/hethongvanban?class_id=2&amp;_page=1&amp;mode=detail&amp;document_id=195985\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(46, 46, 46); text-decoration-line: none;\">Th&ocirc;ng b&aacute;o</a>&nbsp;kết luận của Ph&oacute; Thủ tướng thường trực Trương H&ograve;a B&igrave;nh - Chủ tịch Ủy ban An to&agrave;n giao th&ocirc;ng Quốc gia tại Hội nghị tổng kết c&ocirc;ng t&aacute;c bảo đảm trật tự, an to&agrave;n giao th&ocirc;ng năm 2018; nhiệm vụ năm 2019.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Về nhiệm vụ trọng t&acirc;m năm 2019, Ph&oacute; Thủ tướng đề nghị c&aacute;c Bộ, ng&agrave;nh, địa phương căn cứ v&agrave;o chức năng, nhiệm vụ x&acirc;y dựng v&agrave; thưc hiện Kế hoạch Năm An to&agrave;n giao th&ocirc;ng 2019 với chủ đề &quot;An to&agrave;n giao th&ocirc;ng cho h&agrave;nh kh&aacute;ch v&agrave; người đi m&ocirc; t&ocirc;, xe gắn m&aacute;y&quot; của Ủy ban An to&agrave;n giao th&ocirc;ng Quốc gia nhằm tiếp tục giảm tai nạn giao th&ocirc;ng từ 5-10% về số vụ, số người chết, bị thương so với năm 2018; giảm 10% con số thương vong do tai nạn giao th&ocirc;ng li&ecirc;n quan đến vận tải h&agrave;nh kh&aacute;ch v&agrave; xe m&ocirc; t&ocirc;, xe gắn m&aacute;y; tiếp tục kiểm so&aacute;t v&agrave; k&eacute;o giảm t&igrave;nh trạng &ugrave;n tắc giao th&ocirc;ng tr&ecirc;n c&aacute;c trục giao th&ocirc;ng ch&iacute;nh, c&aacute;c đầu mối giao th&ocirc;ng trọng điểm v&agrave; tại c&aacute;c đ&ocirc; thị lớn, đặc biệt l&agrave; H&agrave; Nội v&agrave; Th&agrave;nh phố Hồ Ch&iacute; Minh. Tập trung v&agrave;o 8 nhiệm vụ trọng t&acirc;m.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Trong đ&oacute;, n&acirc;ng cao hiệu quả c&ocirc;ng t&aacute;c tuy&ecirc;n truyền, phổ biến gi&aacute;o dục ph&aacute;p luật về trật tự an to&agrave;n giao th&ocirc;ng, trật tự đ&ocirc; thị; ki&ecirc;n tr&igrave; x&acirc;y dựng văn ho&aacute; giao th&ocirc;ng an to&agrave;n v&agrave; th&acirc;n thiện m&ocirc;i trường cho mọi tầng lớp nh&acirc;n d&acirc;n; lấy x&acirc;y dựng văn ho&aacute; an to&agrave;n trong cơ quan quản l&yacute; nh&agrave; nước, lực lượng thực thi c&ocirc;ng vụ v&agrave; doanh nghiệp giao th&ocirc;ng vận tải l&agrave;m động lực, tập trung n&acirc;ng cao chất lượng, hiệu quả gi&aacute;o dục an to&agrave;n giao th&ocirc;ng trong trường học với vai tr&ograve; n&ecirc;u gương của cha mẹ học sinh v&agrave; thầy c&ocirc; gi&aacute;o; đồng thời ph&aacute;t huy vai tr&ograve; của văn ho&aacute;, nghệ thuật truyền thống gắn với c&aacute;c k&ecirc;nh th&ocirc;ng tin hiện đại, mạng x&atilde; hội, ph&ugrave; hợp với nhu cầu v&agrave; thị hiếu của thanh, thiếu ni&ecirc;n. Ph&aacute;t huy vai tr&ograve; c&aacute;c đo&agrave;n thể ch&iacute;nh trị, x&atilde; hội trong tuy&ecirc;n truyền, vận động c&aacute;c tầng lớp nh&acirc;n d&acirc;n tham gia bảo đảm trật tự, an to&agrave;n giao th&ocirc;ng (TTATGT), n&acirc;ng cao hiệu quả c&aacute;c m&ocirc; h&igrave;nh tự quản về bảo đảm TTATGT tại cơ sở.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">N&acirc;ng cao hiệu lực, hiệu quả c&ocirc;ng quản l&yacute; nh&agrave; nước về bảo đảm an to&agrave;n giao th&ocirc;ng. Trong đ&oacute;, tập trung n&acirc;ng cao năng lực, hiệu lực của lực lượng tuần tra, kiểm so&aacute;t xử l&yacute; vi phạm; đ&agrave;o tạo, s&aacute;t hạch cấp giấy ph&eacute;p l&aacute;i xe, thuyền vi&ecirc;n; đăng kiểm phương tiện; an to&agrave;n, an ninh h&agrave;ng kh&ocirc;ng; quản l&yacute; quy hoạch - trật tự x&acirc;y dựng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Đẩy nhanh tiến độ v&agrave; n&acirc;ng cao chất lượng c&ocirc;ng t&aacute;c đầu tư x&acirc;y dựng, n&acirc;ng cấp hệ thống kết cấu hạ tầng giao th&ocirc;ng trọng điểm gắn với duy tu, bảo đảm an to&agrave;n giao th&ocirc;ng v&agrave; khai th&aacute;c c&oacute; hiệu quả c&aacute;c c&ocirc;ng tr&igrave;nh hạ tầng hiện hữu, tổ chức t&aacute;ch l&agrave;n xe hai b&aacute;nh với xe &ocirc; t&ocirc; tại những đoạn tuyến quốc lộ c&oacute; lưu lượng giao th&ocirc;ng lớn; n&acirc;ng cao hiệu quả c&ocirc;ng t&aacute;c bảo vệ h&agrave;nh lang an to&agrave;n giao th&ocirc;ng đường bộ, đường sắt, đường thuỷ nội địa. Đối với c&ocirc;ng t&aacute;c bảo tr&igrave; kết cấu hạ tầng giao th&ocirc;ng cần tập trung r&agrave; so&aacute;t, xử l&yacute; dứt điểm c&aacute;c điểm đen, điểm tiềm ẩn nguy cơ xảy ra tai nạn giao th&ocirc;ng, r&agrave; so&aacute;t thu hẹp tiến tới x&oacute;a bỏ lối đi tự mở qua đường sắt. Bộ Giao th&ocirc;ng vận tải v&agrave; Ủy ban nh&acirc;n d&acirc;n c&aacute;c tỉnh, th&agrave;nh phố phải x&acirc;y dựng danh mục, số liệu cụ thể về xo&aacute; điểm đen tai nạn giao th&ocirc;ng, xo&aacute; lối đi tự mở tr&aacute;i ph&eacute;p qua đường sắt trong năm 2019 v&agrave; c&aacute;c năm tiếp theo;...</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><em><strong><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Xử l&yacute; nghi&ecirc;m c&aacute;c đối tượng cố t&igrave;nh ph&aacute; hoại t&agrave;i sản kết cấu hạ tầng giao th&ocirc;ng</span></span></strong></em></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Về một số nhiệm vụ cấp b&aacute;ch, b&ecirc;n cạnh việc x&acirc;y dựng, tổ chức triển khai Kế hoạch năm an to&agrave;n giao th&ocirc;ng 2019, Ph&oacute; Thủ tướng y&ecirc;u cầu c&aacute;c Bộ, ng&agrave;nh, địa phương thực hiện nghi&ecirc;m chỉ đạo của Thủ tướng Ch&iacute;nh phủ tại C&ocirc;ng điện số&nbsp;<a href=\"http://vanban.chinhphu.vn/portal/page/portal/chinhphu/hethongvanban?class_id=2&amp;_page=1&amp;mode=detail&amp;document_id=195538\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(46, 46, 46); text-decoration-line: none;\">1793/CĐ-TTg</a>&nbsp;ng&agrave;y 13/12/2018 về bảo đảm trật tự, an to&agrave;n giao th&ocirc;ng trong dịp Tết Dương lịch, Tết Nguy&ecirc;n đ&aacute;n Kỷ Hợi v&agrave; Lễ hội Xu&acirc;n 2019; tổ chức tốt c&ocirc;ng t&aacute;c vận tải h&agrave;nh kh&aacute;ch, chỉnh trang ho&agrave;n thiện kết cấu hạ tầng giao th&ocirc;ng trong dịp Tết; tuần tra kiểm so&aacute;t v&agrave; xử l&yacute; vi phạm; thực hiện c&aacute;c giải ph&aacute;p chống &ugrave;n tắc giao th&ocirc;ng tại H&agrave; Nội, Th&agrave;nh phố Hồ Ch&iacute; Minh v&agrave; c&aacute;c tuyến quốc lộ trọng điểm. Trong đ&oacute;, tăng cường tuy&ecirc;n truyền vận động gắn với xử l&yacute; nghi&ecirc;m đối với người vi phạm nồng độ cồn khi l&aacute;i xe v&agrave; kh&ocirc;ng đội mũ bảo hiểm đi m&ocirc; t&ocirc;, xe m&aacute;y, xe đạp điện.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Tr&ecirc;n cơ sở đề nghị của Bộ Giao th&ocirc;ng vận tải, Ph&oacute; Thủ tướng y&ecirc;u cầu c&aacute;c Bộ (nhất l&agrave; c&aacute;c Bộ: C&ocirc;ng an, Giao th&ocirc;ng vận tải, Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng) v&agrave; Ủy ban nh&acirc;n d&acirc;n c&aacute;c địa phương tiếp tục thực hiện nghi&ecirc;m chỉ đạo của Thủ tướng Ch&iacute;nh phủ tại C&ocirc;ng điện số&nbsp;<a href=\"http://vanban.chinhphu.vn/portal/page/portal/chinhphu/hethongvanban?class_id=2&amp;_page=1&amp;mode=detail&amp;document_id=192673\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(46, 46, 46); text-decoration-line: none;\">82/CĐ-TTg</a>&nbsp;ng&agrave;y 18/1/2018 về bảo đảm an ninh trật tự tại c&aacute;c trạm thu ph&iacute; BOT; c&oacute; biện ph&aacute;p xử l&yacute; nghi&ecirc;m theo quy định của ph&aacute;p luật đối với c&aacute;c đối tượng c&oacute; h&agrave;nh vi k&iacute;ch động, g&acirc;y rối, cố t&igrave;nh ph&aacute; hoại t&agrave;i sản kết cấu hạ tầng giao th&ocirc;ng, g&acirc;y mất an ninh trật tự v&agrave; an to&agrave;n giao th&ocirc;ng, l&agrave;m ảnh hưởng xấu đến m&ocirc;i trường đầu tư kinh doanh, trong đ&oacute; c&oacute; đầu tư theo h&igrave;nh thức BOT.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Bộ Giao th&ocirc;ng vận tải khẩn trương ho&agrave;n thiện Nghị định quy định về kinh doanh v&agrave; điều kiện kinh doanh vận tải, trong đ&oacute; ch&uacute; &yacute; siết chặt c&aacute;c điều kiện về bảo đảm an to&agrave;n giao th&ocirc;ng đối với hoạt động kinh doanh vận tải bằng xe &ocirc; t&ocirc;, đặc biệt l&agrave; quy định tr&aacute;ch nhiệm của đơn vị kinh doanh vận tải về quản l&yacute; thời gian lao động v&agrave; kh&aacute;m sức khỏe của l&aacute;i xe &ocirc; t&ocirc; kinh doanh vận tải, tr&igrave;nh Ch&iacute;nh phủ trước ng&agrave;y 31/1/2019.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Ủy ban nh&acirc;n d&acirc;n c&aacute;c tỉnh, th&agrave;nh phố trực thuộc Trung ương thực hiện nghi&ecirc;m chỉ đạo của Thủ tướng Ch&iacute;nh phủ tại văn bản số<a href=\"http://www.chinhphu.vn/portal/page/portal/chinhphu/hethongvanban?class_id=2&amp;_page=1&amp;mode=detail&amp;document_id=195774\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(46, 46, 46); text-decoration-line: none;\">25/VPCP-CN</a>&nbsp;ng&agrave;y 3/1/2019 của Văn ph&ograve;ng Ch&iacute;nh phủ li&ecirc;n quan đến c&aacute;c vụ tai nạn giao th&ocirc;ng đặc biệt nghi&ecirc;m trọng; ki&ecirc;n quyết kh&ocirc;ng để xảy ra tai nạn giao th&ocirc;ng đặc biệt nghi&ecirc;m trọng tr&ecirc;n địa b&agrave;n, cả tr&ecirc;n đường bộ, đường sắt v&agrave; đường thủy. Y&ecirc;u cầu c&aacute;c đơn vị kinh doanh vận tải bằng xe &ocirc; t&ocirc; thực hiện nghi&ecirc;m quy định về kh&aacute;m sức khoẻ định kỳ cho l&aacute;i xe, trước hết l&agrave; l&aacute;i xe c&oacute; khối lượng lớn, xe c&ocirc;ng-ten-nơ (ho&agrave;n th&agrave;nh trong qu&yacute; I năm 2019). Đẩy mạnh vận động c&aacute;c đo&agrave;n thể ch&iacute;nh trị, x&atilde; hội tại địa phương tổ chức m&ocirc; h&igrave;nh tự quản tham gia cảnh b&aacute;o, điều tiết, hướng dẫn giao th&ocirc;ng tại c&aacute;c vị tr&iacute; thường xảy ra &ugrave;n tắc, tai nạn giao th&ocirc;ng, c&aacute;c lối đi tự mở qua đường sắt; đồng thời xử l&yacute; tr&aacute;ch nhiệm của tổ chức, c&aacute; nh&acirc;n kh&ocirc;ng thực hiện nghi&ecirc;m quy định ph&aacute;p luật v&agrave; chỉ đạo của Thủ tướng Ch&iacute;nh phủ về bảo đảm an to&agrave;n giao th&ocirc;ng, để xảy ra tai nạn tại c&aacute;c lối đi tự mở qua đường sắt.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Nguồn: Chinhphu.vn</span></span></p>\r\n',NULL,NULL,'9',0,NULL,NULL),(10,'Công bố Hotline phản ánh về tình hình TTATGT',0,NULL,'<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(51, 51, 51)\">Ủy ban An to&agrave;n giao th&ocirc;ng Quốc gia c&ocirc;ng bố đường d&acirc;y n&oacute;ng phản &aacute;nh về t&igrave;nh h&igrave;nh trật tự an to&agrave;n giao th&ocirc;ng trong dịp nghỉ Tết Dương lịch, Tết Nguy&ecirc;n đ&aacute;n v&agrave; m&ugrave;a Lễ hội xu&acirc;n Kỷ Hợi năm 2019</span></span></span></strong></p>\r\n\r\n<div style=\"page-break-after: always\"><span style=\"display:none\">&nbsp;</span></div>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Thực hiện C&ocirc;ng điện 1793/CĐ-TTg của Thủ tướng Ch&iacute;nh phủ về việc bảo đảm trật tự, an to&agrave;n giao th&ocirc;ng trong dịp Tết Dương lịch, Tết Nguy&ecirc;n đ&aacute;n Kỷ Hợi v&agrave; Lễ hội Xu&acirc;n 2019, Ủy ban An to&agrave;n giao th&ocirc;ng Quốc gia th&ocirc;ng b&aacute;o hệ thống số điện thoại đường d&acirc;y n&oacute;ng để tiếp nhận c&aacute;c &yacute; kiến phản &aacute;nh của người d&acirc;n về hoạt động vận tải, t&igrave;nh h&igrave;nh trật tự an to&agrave;n giao th&ocirc;ng xảy ra trong dịp nghỉ lễ:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">1. Phản &aacute;nh về xử l&yacute; vi phạm h&agrave;nh ch&iacute;nh trong lĩnh vực giao th&ocirc;ng đường bộ, đường sắt, đường thủy nội địa; t&igrave;nh h&igrave;nh trật tự an to&agrave;n giao th&ocirc;ng, &ugrave;n tắc giao th&ocirc;ng li&ecirc;n hệ: Cục Cảnh s&aacute;t giao th&ocirc;ng &ndash; 0995.67.67.67; 0692.342.608</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">2. Phản &aacute;nh c&aacute;c th&ocirc;ng tin về lĩnh vực giao th&ocirc;ng đường bộ:</span></span></p>\r\n\r\n<div class=\"table-responsive\" style=\"box-sizing: border-box; overflow-x: auto; min-height: 0.01%; font-family: arial;\">\r\n<table class=\"Table\" style=\"background-color:transparent; border-collapse:collapse; border-spacing:0px; box-sizing:border-box; margin-left:5.4pt\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:top; width:149.4pt\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">0983.60.8989</span></span></p>\r\n\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">(Vụ Vận tải &ndash; Tổng cục đường bộ Việt Nam)</span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:165.6pt\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">0916.60.8085</span></span></p>\r\n\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">(Vụ An to&agrave;n giao th&ocirc;ng&ndash; Tổng cục đường bộ VN )</span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:138.6pt\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">0903.479.808</span></span></p>\r\n\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">(Vụ Kết cấu hạ tầng đường bộ&ndash; Tổng cục đường bộ Việt Nam)</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:top; width:149.4pt\">\r\n			<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">0964.045.445</span></span></p>\r\n\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">(Vụ Vận tải Bộ GTVT &ndash; Bộ Giao th&ocirc;ng vận tải)</span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:165.6pt\">\r\n			<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">0962.665.953</span></span></p>\r\n\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">(Thanh tra Bộ GTVT &ndash; Bộ Giao th&ocirc;ng vận tải)</span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:138.6pt\">\r\n			<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">0915.86.9900</span></span></p>\r\n\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">(Kiểm so&aacute;t tải trọng xe&ndash; Tổng cục đường bộ VN)</span></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n</div>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">3. Để phản &aacute;nh v&agrave; được giải đ&aacute;p trong lĩnh vực đường sắt, li&ecirc;n hệ theo c&aacute;c số: Cục Đường sắt VN: 0865367565</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">4. Để phản &aacute;nh v&agrave; được giải đ&aacute;p những th&ocirc;ng tin li&ecirc;n quan đến lĩnh vực h&agrave;ng kh&ocirc;ng, li&ecirc;n hệ theo số: 0916.562.119</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">5. Để phản &aacute;nh v&agrave; được giải đ&aacute;p những th&ocirc;ng tin li&ecirc;n quan đến lĩnh vực đường thủy nội địa, li&ecirc;n hệ theo c&aacute;c số:</span></span></p>\r\n\r\n<div class=\"table-responsive\" style=\"box-sizing: border-box; overflow-x: auto; min-height: 0.01%; font-family: arial;\">\r\n<table align=\"left\" class=\"Table\" style=\"background-color:transparent; border-collapse:collapse; border-spacing:0px; border:1pt inset; box-sizing:border-box; margin-left:6.75pt; margin-right:6.75pt\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:top; width:149.4pt\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">0243.5481888</span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:154.8pt\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">0942.10.74.74</span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:149.4pt\">\r\n			<p style=\"text-align:center\">&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n</div>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">6. Phản &aacute;nh về t&igrave;nh h&igrave;nh trật tự an to&agrave;n giao th&ocirc;ng, tai nạn giao th&ocirc;ng li&ecirc;n hệ c&aacute;c số:</span></span></p>\r\n\r\n<div class=\"table-responsive\" style=\"box-sizing: border-box; overflow-x: auto; min-height: 0.01%; font-family: arial;\">\r\n<table class=\"Table\" style=\"background-color:transparent; border-collapse:collapse; border-spacing:0px; box-sizing:border-box; margin-left:5.4pt\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:top; width:149.4pt\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">0989.088.719</span></span></p>\r\n\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">(Văn ph&ograve;ng Ủy ban ATGT QG)</span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:154.8pt\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">0977.497.891</span></span></p>\r\n\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">(Vụ ATGT &ndash; Bộ GTVT)</span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:149.4pt\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">0981.759.328</span></span></p>\r\n\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">(Văn ph&ograve;ng Ủy ban ATGT QG)</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:top; width:149.4pt\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">0941.329.634</span></span></p>\r\n\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">(Văn ph&ograve;ng Ủy ban ATGT QG)</span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:154.8pt\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">0936.198.387</span></span></p>\r\n\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">(Văn ph&ograve;ng Ủy ban</span></span></p>\r\n\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">ATGT QG)</span></span></p>\r\n			</td>\r\n			<td style=\"vertical-align:top; width:149.4pt\">\r\n			<p style=\"text-align:center\">&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n</div>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Đề nghị người tham gia giao th&ocirc;ng khi phản &aacute;nh tới đường d&acirc;y n&oacute;ng cần c&oacute; đầy đủ th&ocirc;ng tin: h&agrave;nh tr&igrave;nh, địa điểm, biển kiểm so&aacute;t phương tiện, số hiệu chuyến bay, thời gian, lỗi vi phạm hoặc vấn đề g&acirc;y mất trật tự an to&agrave;n giao th&ocirc;ng, ưu ti&ecirc;n dung tin nhắn để đảm bảo ch&iacute;nh x&aacute;c, thuận tiện v&agrave; an to&agrave;n.</span></span></li>\r\n</ul>\r\n\r\n<p><em><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Nguồn: antoangiaothong.gov.vn</span></span></em></p>\r\n',NULL,NULL,'10',0,NULL,NULL),(11,'Giảm chi phí logistics nhằm nâng cao năng lực cạnh tranh và phát triển dịch vụ logistics',0,NULL,'<p><span style=\"color:rgb(119, 119, 119); font-family:arial\">Để cắt giảm chi ph&iacute; logistics, trước hết ch&uacute;ng ta phải hiểu đầy đủ hiện trạng của ng&agrave;nh dịch vụ logistics Việt Nam v&agrave; cơ cấu chi ph&iacute; logistics. </span></p>\r\n\r\n<div style=\"page-break-after: always\"><span style=\"display:none\">&nbsp;</span></div>\r\n\r\n<p><span style=\"color:rgb(119, 119, 119); font-family:arial\">Tại Hội nghị Ph&aacute;t triển ng&agrave;nh dịch vụ logistics,triển khai Kế hoạch năm 2018 của Hiệp hội Doanh nghiệp dịch vụ Logistics Việt Nam (VLA) mới đ&acirc;y, nhiều nội dung đ&atilde; được đưa ra b&agrave;n luận để hướng đến sự ph&aacute;t triển ng&agrave;nh dịch vụ logistics của đất nước.</span></p>\r\n\r\n<p style=\"text-align:center\"><a class=\"view-gallery\" href=\"https://drvn.gov.vn/upload/20830/20180629/3e022be97d165e712ae7900342e35df3logistic4.5.jpg_t%3D1525395226321.jpg\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(46, 46, 46); text-decoration-line: none;\"><img alt=\"\" src=\"https://drvn.gov.vn/upload/20830/20180629/3e022be97d165e712ae7900342e35df3logistic4.5.jpg_t%3D1525395226321.jpg\" style=\"box-sizing:border-box; height:375px; max-width:100%; vertical-align:middle; width:600px\" /></a></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><em>Ảnh</em></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12px\"><strong>T&igrave;nh h&igrave;nh ph&aacute;t triển ng&agrave;nh dịch vụ logistics&nbsp;Việt Nam</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\">Tốc độ ph&aacute;t triển v&agrave; xếp hạng LPI của ng&agrave;nh dịch vụ logistics Việt Nam: Ng&agrave;nh dịch vụ logistics Việt Nam đang ph&aacute;t triển mạnh với tốc độ từ 15 - 16%/năm. Theo chỉ số hoạt động LPI của Ng&acirc;n h&agrave;ng Thế giới 2014, Việt Nam xếp hạng 53 v&agrave; năm 2016 xếp hạng 64/160 nước, đứng thứ 4 trong c&aacute;c nước ASEAN sau Singapore, Malaysia v&agrave; Th&aacute;i Lan. Quyết định 200/TTg của Thủ tướng Ch&iacute;nh phủ ng&agrave;y 14/02/2017 (QĐ200) về việc ph&ecirc; duyệt Kế hoạch h&agrave;nh động n&acirc;ng cao năng lực cạnh tranh v&agrave; ph&aacute;t triển dịch vụ logistics Việt Nam đến năm 2025 đ&atilde; đề ra mục ti&ecirc;u đến 2025: &quot;Tốc độ tăng trưởng dịch vụ đạt 15 - 20%&quot;, &quot;Xếp hạng theo chỉ số năng lực quốc gia về logistics (LPI) tr&ecirc;n thế giới đạt thứ 50 trở l&ecirc;n&quot;.</span></p>\r\n\r\n<p><span style=\"font-size:12px\">Tỷ lệ thu&ecirc; ngo&agrave;i (outsourcing) của ng&agrave;nh dịch vụ logistics Việt Nam: Hiện nay, khoảng 35 - 40% nguy&ecirc;n nh&acirc;n chủ yếu l&agrave; chưa c&oacute; sự phối hợp, hợp t&aacute;c giữa c&aacute;c nh&agrave; cung cấp dịch vụ logistics với c&aacute;c nh&agrave; sản xuất v&agrave; c&aacute;c nh&agrave; xuất nhập khẩu. QĐ200 đ&atilde; đề ra mục ti&ecirc;u đến 2025: &quot;Tỷ lệ thu&ecirc; ngo&agrave;i dịch vụ logistics đạt 50 - 60%&quot;.</span></p>\r\n\r\n<p><span style=\"font-size:12px\">Chi ph&iacute; logistics của Việt Nam - c&aacute;ch t&iacute;nh: Trong B&aacute;o c&aacute;o cuối kỳ &quot;Dịch vụ tư vấn: Hỗ trợ Bộ GTVT về Ph&aacute;t triển Vận tải đa phương thức&quot; th&aacute;ng 3/2014, C&ocirc;ng ty Tư vấn ALG của Ng&acirc;n h&agrave;ng Thế giới (WB) tại Việt Nam đ&atilde; th&ocirc;ng tin chi ph&iacute; logistics của Việt Nam tương đương 20,9% GDP. ALG đ&atilde; d&ugrave;ng phương ph&aacute;p t&iacute;nh chi ph&iacute; logistics cụ thể như sau: Cho 12 chuỗi ng&agrave;nh h&agrave;ng (h&agrave;ng điện tử v&agrave; linh kiện, thiết bị điện, dệt may, c&ocirc;ng nghiệp &ocirc; t&ocirc;, dược phẩm, rau quả, gi&agrave;y d&eacute;p, hải sản, gạo, c&agrave; ph&ecirc;, đồ uống v&agrave; nội thất), rồi nội suy ước t&iacute;nh v&agrave; r&uacute;t ra kết luận chi ph&iacute; logistics quốc gia của Việt Nam tương ứng với tất cả c&aacute;c ng&agrave;nh sản xuất gi&aacute; trị gia tăng trong nước l&agrave; 413.141 tỷ đồng. Con số n&agrave;y tương đương 20,9% tổng GDP của cả nước (1.980.914 tỷ đồng trong năm 2010). Tổng chi ph&iacute; logistics l&agrave; 413.141 tỷ đồng trong năm 2010, bao gồm: Chi ph&iacute; vận tải: 60% (gồm Transport 59% + Port charge: 01%); chi ph&iacute; tồn kho (Inventory Costs) v&agrave; chi ph&iacute; quản l&yacute; (Administration costs): 40% (bao gồm: Storage 11%, Handling 21% v&agrave; Packing 8%). Đ&acirc;y l&agrave; nghi&ecirc;n cứu với c&aacute;ch t&iacute;nh khoa học đ&aacute;ng tin cậy nhất cho đến hiện nay về chi ph&iacute; logistics của Việt Nam.</span></p>\r\n\r\n<p><span style=\"font-size:12px\">VLA t&aacute;n th&agrave;nh phương ph&aacute;p t&iacute;nh chi ph&iacute; logistics của ALG đ&atilde; thực hiện v&agrave; cho rằng ở thời điểm năm 2016, một c&aacute;ch tương đối c&oacute; thể dựa v&agrave;o tăng trưởng về khối lượng h&agrave;ng h&oacute;a vận chuyển (tăng 1,55 so với năm 2010), khối lượng h&agrave;ng h&oacute;a lu&acirc;n chuyển (tăng 1,09 so với năm 2010), GDP (tăng 2,0 so với năm 2010) để từ đ&oacute; ngoại suy chi ph&iacute; logistics ở Việt Nam c&oacute; thể đang ở mức 14,5 - 19,2%, hay trung b&igrave;nh l&agrave; 16,8%. Đối chiếu so s&aacute;nh với chi ph&iacute; logistics/GDP với ASEAN 6, năm 2016: Singapore 8,5%, Indonesia 24%, Th&aacute;i Lan 15%, Malaysia 13%, Philippines 13% v&agrave; trung b&igrave;nh của Asia Pacific 12,7% (nguồn: Amstrong &amp; Asociate). QĐ200 đ&atilde; đề ra mục ti&ecirc;u đến 2025: &quot;Chi ph&iacute; logistics giảm xuống tương đương 16 - 20% GDP&quot;.</span></p>\r\n\r\n<p><span style=\"font-size:12px\">Về số lượng doanh nghiệp kinh doanh dịch vụ logistics của Việt Nam hiện nay: Theo th&ocirc;ng tin do Trung t&acirc;m Hỗ trợ nghiệp vụ đăng k&yacute; kinh doanh (thuộc Cục Quản l&yacute; Đăng k&yacute; kinh doanh - Bộ Kế hoạch v&agrave; Đầu tư), hiện cả nước c&oacute; khoảng 3.000 c&ocirc;ng ty tham gia cung cấp c&aacute;c loại h&igrave;nh dịch vụ logistics, trong số đ&oacute; 70% c&oacute; trụ sở ở khu vực TP. Hồ Ch&iacute; Minh v&agrave; c&oacute; khoảng 30 c&ocirc;ng ty logistics đa quốc gia. C&aacute;c c&ocirc;ng ty đa quốc gia c&oacute; thế mạnh về hợp đồng chuy&ecirc;n chở ngoại với c&aacute;c h&atilde;ng t&agrave;u lớn do c&ocirc;ng ty mẹ k&yacute; với c&aacute;c chủ h&agrave;ng lớn c&oacute; mạng lưới to&agrave;n cầu, mức độ đầu tư ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin v&agrave;o hoạt động logistics cao, tr&igrave;nh độ quản l&yacute; ti&ecirc;n tiến v&agrave; đặc biệt c&oacute; quan hệ tốt với c&aacute;c chủ h&agrave;ng to&agrave;n cầu n&ecirc;n c&oacute; nhiều lợi thế so với c&aacute;c doanh nghiệp cung cấp dịch vụ logistics Việt Nam, do đ&oacute; chiếm thị phần đ&aacute;ng kể. Trong khi đ&oacute;, thế mạnh của c&aacute;c doanh nghiệp logistics Việt Nam l&agrave; đầu tư - khai th&aacute;c cảng, vận tải nội địa, nổi bật l&agrave; vận tải bộ, đại l&yacute; thủ tục hải quan, đầu tư - khai th&aacute;c kho, b&atilde;i v&agrave; c&oacute; đội ngũ nh&acirc;n sự dồi d&agrave;o, đ&ocirc;ng đảo.</span></p>\r\n\r\n<p><span style=\"font-size:12px\">Khoảng 15% tổng số doanh nghiệp tham gia cung cấp dịch vụ logistics của Việt Nam l&agrave; hội vi&ecirc;n của Hiệp hội VLA nhưng đại diện tr&ecirc;n 60% thị phần cả nước. Trong số c&aacute;c hội vi&ecirc;n VLA c&oacute; nhiều doanh nghiệp logistics h&agrave;ng đầu trong ng&agrave;nh như: SNP, Gemadept, Transimex, Indotrans, TBS Logistics, BK Logistics, U&amp;I Logistics, TBS Logistics, Sotrans, Vinalink Logistics, Vinafco&hellip; T&iacute;nh đến ng&agrave;y 20/3/2018, VLA c&oacute; 369 hội vi&ecirc;n, trong đ&oacute; c&oacute; 318 hội vi&ecirc;n ch&iacute;nh thức v&agrave; 51 hội vi&ecirc;n li&ecirc;n kết với 34 hội vi&ecirc;n l&agrave; c&aacute;c c&ocirc;ng ty FDI, hoạt động tr&ecirc;n cả nước nhưng tập trung chủ yếu l&agrave; khu vực ph&iacute;a Nam (228 hội vi&ecirc;n).</span></p>\r\n\r\n<p><span style=\"font-size:12px\">C&oacute; khoảng 30 doanh nghiệp cung cấp dịch vụ logistics xuy&ecirc;n quốc gia đang hoạt động tại Việt Nam. C&aacute;c doanh nghiệp lớn như: DHL, FedEx, UPS, Maersk Logistics, APL Logistics, Schenker, Nippon Express, KWE, CJ Logistics, KMTC Logistics, Log Win&hellip; C&aacute;c doanh nghiệp cung cấp dịch vụ logistics nước ngo&agrave;i v&agrave; c&aacute;c c&ocirc;ng ty cung cấp dịch vụ logistics xuy&ecirc;n quốc gia tại Việt Nam l&agrave; một bộ phận quan trọng trong việc ph&aacute;t triển thị trường logistics Việt Nam, trong điều kiện Việt Nam hội nhập kinh tế thế giới ng&agrave;y c&agrave;ng s&acirc;u rộng.</span></p>\r\n\r\n<p><span style=\"font-size:12px\">Về thị phần của dịch vụ logistics Việt Nam, l&acirc;u nay c&aacute;c cơ quan truyền th&ocirc;ng đưa tin l&agrave; c&aacute;c doanh nghiệp dịch vụ logistics nước ngo&agrave;i tuy số lượng chỉ chiếm 20% nhưng nắm giữ 80% thị phần. Đ&acirc;y l&agrave; th&ocirc;ng tin ho&agrave;n to&agrave;n kh&ocirc;ng đ&uacute;ng. Bởi v&igrave;, n&oacute;i đến logistics l&agrave; ch&uacute;ng ta phải n&oacute;i đến từng chuỗi vụ dịch ri&ecirc;ng của logistics, qua đ&oacute; sẽ thấy được r&otilde; năng lực cung cấp dịch vụ của c&aacute;c nh&agrave; cung cấp dịch vụ logistics Việt Nam. Về vấn đề khai th&aacute;c cảng biển th&igrave; hiện nay đến 90% thị phần nằm trong tay c&aacute;c doanh nghiệp khai th&aacute;c cảng biển Việt Nam. Về vận tải đường bộ v&agrave; đại l&yacute; hải quan th&igrave; chắc chắn thuộc về c&aacute;c doanh nghiệp logistics Việt Nam. Về cung cấp kho b&atilde;i, dịch vụ kho th&igrave; hiện nay thị phần vẫn nằm trong tay c&aacute;c doanh nghiệp dịch vụ logistics Việt Nam. Ch&uacute;ng ta cần phải hiểu ch&iacute;nh x&aacute;c như vậy.</span></p>\r\n\r\n<p><span style=\"font-size:12px\">Về năng lực cung cấp dịch vụ logistics, VLA tin rằng c&aacute;c doanh nghiệp logistics Việt Nam đủ khả năng cung cấp c&aacute;c dịch vụ thuộc chuỗi dịch vụ logistics cho tất cả c&aacute;c doanh nghiệp sử dụng dịch vụ logistics. Tuy nhi&ecirc;n, ch&uacute;ng ta kh&ocirc;ng phủ nhận c&aacute;c yếu k&eacute;m về một số lĩnh vực như: Vận tải h&agrave;ng kh&ocirc;ng, vận tải đường biển quốc tế, c&aacute;c doanh nghiệp dịch vụ logistics Việt Nam kh&ocirc;ng thể đầu tư với nguồn vốn lớn v&igrave; c&ograve;n nhiều c&ocirc;ng ty l&agrave; doanh nghiệp vừa v&agrave; nhỏ. Việc n&agrave;y cần phải c&oacute; sự hỗ trợ t&iacute;ch cực của Nh&agrave; nước ngo&agrave;i việc x&acirc;y dựng ph&aacute;t triển kết cấu hạ tầng GTVT. Bởi v&igrave;, cũng như tất cả c&aacute;c nước tr&ecirc;n thế giới khi m&agrave; c&aacute;c c&ocirc;ng ty vận tải biển lớn đều c&oacute; xuất ph&aacute;t điểm từ sự hỗ trợ n&agrave;y.</span></p>\r\n\r\n<p><span style=\"font-size:12px\">Quan điểm của VLA về doanh nghiệp cung cấp dịch vụ logistics nước ngo&agrave;i đang cung cấp dịch vụ tại Việt Nam l&agrave; c&aacute;c doanh nghiệp kinh doanh ở Việt Nam c&oacute; sử dụng lao động Việt Nam, đ&oacute;ng thuế cho Việt Nam. C&aacute;c doanh nghiệp n&agrave;y l&agrave; đối t&aacute;c của ch&uacute;ng ta, cạnh tranh l&agrave;nh mạnh chứ kh&ocirc;ng phải gi&agrave;nh giật. C&aacute;c doanh nghiệp n&agrave;y c&oacute; ưu điểm để c&aacute;c doanh nghiệp dịch vụ logistics Việt Nam học hỏi kinh nghiệm về tr&igrave;nh độ quản l&yacute;, nghiệp vụ, khoa học c&ocirc;ng nghệ logistics để c&ugrave;ng nhau ph&aacute;t triển ng&agrave;nh dịch vụ logistics Việt Nam.</span></p>\r\n\r\n<p><span style=\"font-size:12px\"><strong>Những khuyến nghị về cắt giảm chi ph&iacute; logistics trong ng&agrave;nh GTVT của nước ta</strong></span></p>\r\n\r\n<p><span style=\"font-size:12px\">- Ch&iacute;nh phủ c&oacute; một nghị quyết ri&ecirc;ng về vấn đề cắt giảm chi ph&iacute; logistics, y&ecirc;u cầu c&aacute;c bộ, ng&agrave;nh chức năng, hiệp hội ng&agrave;nh nghề li&ecirc;n quan triển khai hiệu quả QĐ200 v&agrave; c&oacute; nhiệm vụ, mục ti&ecirc;u cụ thể nhằm giảm chi ph&iacute; dịch vụ logistics.</span></p>\r\n\r\n<p><span style=\"font-size:12px\">- Cắt giảm chi ph&iacute; vận tải, trước hết l&agrave; vận tải đường bộ, minh bạch h&oacute;a ph&iacute; BOT, x&oacute;a bỏ chi ph&iacute; ngầm trong vận tải, x&acirc;y dựng c&aacute;c biện ph&aacute;p cụ thể kết hợp vận chuyển h&agrave;ng hai chiều. Năm 2015, vận tải h&agrave;ng h&oacute;a bằng đường bộ chiếm 77,08% thị phần vận tải cả nước, phấn đấu đến năm 2020 giảm xuống c&ograve;n 54,39%. Cho n&ecirc;n, trước hết phải tập trung v&agrave;o việc giảm chi ph&iacute; vận tải đường bộ trong tổng số 60% chi ph&iacute; vận tải của chi ph&iacute; logistics nước ta.</span></p>\r\n\r\n<p><span style=\"font-size:12px\">- Phối hợp chặt chẽ với c&aacute;c chủ h&agrave;ng nhằm giảm &ugrave;n tắc h&agrave;ng h&oacute;a tại cảng v&agrave; giao th&ocirc;ng đ&ocirc; thị. V&iacute; dụ tại cảng C&aacute;t L&aacute;i, người sử dụng dịch vụ vận tải phải c&ugrave;ng chung tay cắt giảm chi ph&iacute; logistics; n&acirc;ng cao năng lực sử dụng cảng C&aacute;i M&eacute;p - Thị Vải; loại bỏ việc thu phụ ph&iacute; bất hợp l&yacute; của c&aacute;c h&atilde;ng t&agrave;u nước ngo&agrave;i tại cảng biển.</span></p>\r\n\r\n<p><span style=\"font-size:12px\">- Tiến h&agrave;nh cơ cấu lại vận tải một c&aacute;ch hiệu quả, ph&aacute;t triển vận tải đường biển, vận tải đường sắt Bắc - Nam v&agrave; li&ecirc;n vận quốc tế phục vụ chuy&ecirc;n chở h&agrave;ng h&oacute;a nội địa v&agrave; vận tải xuy&ecirc;n quốc gia, qua đ&oacute; ph&aacute;t triển hiệu quả vận tải đa phương thức.</span></p>\r\n\r\n<p><span style=\"font-size:12px\">- Ph&aacute;t triển logistics khu vực đồng bằng s&ocirc;ng Cửu Long, gắn chặt với vận tải thủy, chủ yếu l&agrave; vận tải container dựa tr&ecirc;n thế mạnh luồng lạch của khu vực về C&aacute;t L&aacute;i v&agrave; C&aacute;i M&eacute;p - Thị Vải; x&acirc;y dựng trung t&acirc;m logistics; thu h&uacute;t c&aacute;c h&atilde;ng h&agrave;ng kh&ocirc;ng bay trực tiếp đến Cần Thơ, tạo kết nối h&agrave;ng kh&ocirc;ng trực tiếp với c&aacute;c trung t&acirc;m khu vực như Singapore, Bangkok, Kualalumpur, Hồng K&ocirc;ng, T&acirc;n Sơn Nhất. Hiện nay, khoảng 80% h&agrave;ng h&oacute;a xuất khẩu của khu vực đồng bằng s&ocirc;ng Cửu Long được thực hiện qua khu vực TP. Hồ Ch&iacute; Minh v&agrave; B&agrave; Rịa - Vũng T&agrave;u.</span></p>\r\n\r\n<p><span style=\"font-size:12px\">- Nghi&ecirc;n cứu, xem x&eacute;t bổ sung nhiệm vụ điều phối hoạt động logistics cho Ủy ban Quốc gia tạo thuận lợi h&oacute;a thương mại; lập cơ quan quản l&yacute; nh&agrave; nước chuy&ecirc;n tr&aacute;ch về logistics như cục/vụ tại Bộ C&ocirc;ng thương, Bộ GTVT; mở rộng sự tham gia đ&oacute;ng g&oacute;p &yacute; kiến của doanh nghiệp v&agrave;o qu&aacute; tr&igrave;nh quản l&yacute; hoạt động dịch vụ logistics nhằm n&acirc;ng cao năng lực quản l&yacute; nh&agrave; nước đối với ng&agrave;nh dịch vụ logistics nước ta.</span></p>\r\n\r\n<p><em><span style=\"font-size:12px\">Nguồn: Tạp ch&iacute; GTVT&nbsp;</span></em></p>\r\n',NULL,NULL,'11',0,NULL,NULL),(12,'Xây dựng chính phủ điện tử và nâng cao ứng dụng CNTT của Tổng cục ĐBVN',0,NULL,'<h1 style=\"text-align:justify\"><em><span style=\"font-size:13pt\"><span style=\"font-family:arial,sans-serif\">Chiều 15/11, tại trụ sở Tổng cục ĐBVN, Thứ trưởng Nguyễn Ngọc Đ&ocirc;ng đ&atilde; chủ tr&igrave; cuộc họp về kế hoạch x</span></span></em><em><span style=\"font-size:13pt\"><span style=\"font-family:arial,sans-serif\">&acirc;y dựng ch&iacute;nh phủ điện tử v&agrave; n&acirc;ng cao ứng dụng CNTT của Tổng cục ĐBVN. Tham dự cuộc họp về ph&iacute;a Tổng cục ĐBVN c&oacute; Tổng cục trưởng Nguyễn Văn Huyện, Ph&oacute; Tổng cục trưởng Nguyễn Mạnh Thắng v&agrave; L&atilde;nh đạo c&aacute;c Vụ, đơn vị.</span></span></em></h1>\r\n\r\n<div style=\"page-break-after: always\"><span style=\"display:none\">&nbsp;</span></div>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,sans-serif\">Thực hiện Quyết định số 2113/QĐ-BGTVT ng&agrave;y 28/9/2018 của Bộ GTVT về việc ban h&agrave;nh Kiến tr&uacute;c Ch&iacute;nh phủ điện tử của Bộ GTVT; Văn bản số 11595/BGTVT-TTCNTT ng&agrave;y 15/10/2018 của Bộ BGTVT về việc n&acirc;ng cao chỉ số Ch&iacute;nh phủ điện tử của Bộ GTVT; Văn bản số 12773/BGTVT-TTCNTT ng&agrave;y 09/11/2018 của Bộ GTVT về việc x&acirc;y dựng kế hoạch triển khai Kiến tr&uacute;c Ch&iacute;nh phủ điện tử Bộ GTVT; Tổng cục ĐBVN đang tiến h&agrave;nh x&acirc;y dựng kế hoạch triển khai Ch&iacute;nh phủ điện tử tại Tổng cục tr&ecirc;n c&aacute;c lĩnh vực: X&acirc;y dựng nền tảng cơ sở dữ liệu d&ugrave;ng chung; x&acirc;y dựng c&aacute;c hệ thống phục vụ cải c&aacute;ch h&agrave;nh ch&iacute;nh; x&acirc;y dựng c&aacute;c hệ thống nghiệp vụ chuy&ecirc;n ng&agrave;nh; x&acirc;y dựng hạ tầng kỹ thuật CNTT v&agrave; an to&agrave;n th&ocirc;ng tin. B&ecirc;n cạnh đ&oacute;,&nbsp;</span><span style=\"font-family:arial,sans-serif\">Tổng cục ĐBVN đ&atilde; th&agrave;nh lập ban chỉ đạo ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin v&agrave; an to&agrave;n th&ocirc;ng tin mạng do Tổng cục trưởng l&agrave;m trưởng ban, h&agrave;ng năm, ban h&agrave;nh kế hoạch ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin của trong đ&oacute; x&aacute;c định r&otilde; c&aacute;c nội dung cần triển khai trong năm.</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,sans-serif\">Theo Ph&oacute; Vụ trưởng Vụ KHCN, MT&amp;HTQT Nguyễn Viết Thanh, hệ thống CNTT của Tổng cục ĐBVN cơ bản đ&aacute;p ứng c&aacute;c y&ecirc;u cầu x&acirc;y dựng kiến tr&uacute;c Ch&iacute;nh phủ điện tử của Bộ GTVT</span><span style=\"font-family:arial,sans-serif\">. Tuy nhi&ecirc;n, hiện nay chưa c&oacute; c&aacute;c quy định, hướng dẫn việc kết nối, phạm vi, mức độ v&agrave; phương thức chia sẻ th&ocirc;ng tin khi x&acirc;y dựng nền tảng cơ sở dữ liệu d&ugrave;ng chung; b&ecirc;n cạnh đ&oacute;, khi triển khai x&acirc;y dựng ch&iacute;nh phủ điện tử cần phải đầu tư, n&acirc;ng cấp hạ tầng CNTT v&agrave; hệ thống bảo mật an ninh th&ocirc;ng tin,..do đ&oacute; cần phải c&oacute; nguồn lực kinh ph&iacute; để thực hiện.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><a class=\"view-gallery\" href=\"https://drvn.gov.vn/upload/20830/fck/toancanhhopcpdt_23_11.png\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(46, 46, 46); text-decoration-line: none;\"><img alt=\"\" src=\"https://drvn.gov.vn/upload/20830/fck/toancanhhopcpdt_23_11.png\" style=\"box-sizing:border-box; height:427px; max-width:100%; vertical-align:middle; width:640px\" /></a></p>\r\n\r\n<h1 style=\"text-align:center\"><em><span style=\"font-family:arial,sans-serif\">To&agrave;n cảnh buổi họp tại trụ sở Tổng cục ĐBVN chiều ng&agrave;y 15/11</span></em></h1>\r\n\r\n<h1 style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:arial,sans-serif\">B&aacute;o c&aacute;o tại cuộc họp, Vụ trưởng Vụ KHCN,MT&amp;HTQT T&ocirc; Nam To&agrave;n cho biết, về kế hoạch x</span><span style=\"font-family:arial,sans-serif\">&acirc;y dựng ch&iacute;nh phủ điện tử v&agrave; n&acirc;ng cao ứng dụng CNTT, Tổng cục ĐBVN đang ho&agrave;n thiện c&aacute;c Hệ thống cơ sở dữ liệu nền tảng d&ugrave;ng chung kết cấu hạ tầng giao th&ocirc;ng như: Hệ thống quản l&yacute; t&agrave;i sản đường bộ VRAMP; Hệ thống quản l&yacute; t&agrave;i sản đường địa phương LRAMP; Hệ thống quản l&yacute; mặt đường PMS; Hệ thống quản l&yacute; Cầu (bao gồm quản l&yacute; cầu quốc lộ VBMS, quản l&yacute; cầu địa phương LBMS; quản l&yacute; CSDL quan trắc cầu d&acirc;y văng SHMS).</span></span></h1>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,sans-serif\">Về x&acirc;y dựng c&aacute;c hệ thống phục vụ cải c&aacute;ch h&agrave;nh ch&iacute;nh:<strong>&nbsp;</strong></span><span style=\"font-family:arial,sans-serif\">Tổng cục ĐBVN đ&atilde; phối hợp với Trung t&acirc;m CNTT để cung cấp 65 dịch vụ c&ocirc;ng trực tuyến mức độ 4 thuộc lĩnh vực vận tải quốc tế tr&ecirc;n cổng dịch vụ h&agrave;nh ch&iacute;nh c&ocirc;ng theo cơ chế một cửa quốc gia;&nbsp;</span><span style=\"font-family:arial,sans-serif\">Đ&atilde; triển khai 01 dịch vụ c&ocirc;ng mức độ 3 (cấp đổi GPLX) v&agrave; 63 dịch vụ c&ocirc;ng mức độ 4 (trong đ&oacute; 62 DVC về lĩnh vực vận tải tr&ecirc;n trang dichvucong.mt.gov.vn v&agrave; 01 DVC đổi GPLX quốc tế tại trang gplx.vn).</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,sans-serif\">Tổng cục ĐBVN cũng đang phối hợp với Trung t&acirc;m CNTT để cung cấp 02 dịch vụ c&ocirc;ng trực tuyến mức độ 3 (chấp thuận x&acirc;y dựng v&agrave; cấp ph&eacute;p thi c&ocirc;ng x&acirc;y dựng c&ocirc;ng tr&igrave;nh thiết yếu) v&agrave; 02 dịch vụ c&ocirc;ng trực tuyến mức độ 4 (Chấp thuận v&agrave; gia hạn chấp thuận x&acirc;y dựng c&ocirc;ng tr&igrave;nh thiết yếu) trong năm 2018-2019 theo Quyết định 877/QĐ-TTg ng&agrave;y 18/7/2018 của Thủ tướng Ch&iacute;nh phủ.</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,sans-serif\">Ngo&agrave;i hệ thống quản l&yacute; cơ sở dữ liệu kết cấu hạ tầng đường bộ, Tổng cục ĐBVN đ&atilde; v&agrave; đang x&acirc;y dựng c&aacute;c hệ thống&nbsp;</span><span style=\"font-family:arial,sans-serif\">nghiệp vụ chuy&ecirc;n ng&agrave;nh</span><span style=\"font-family:arial,sans-serif\">&nbsp;như:<strong>&nbsp;</strong>Hệ thống Gi&aacute;m s&aacute;t h&agrave;nh tr&igrave;nh phương tiện vận tải đường bộ;<strong>&nbsp;</strong>Hệ thống quản l&yacute; giấy ph&eacute;p l&aacute;i xe;<strong>&nbsp;</strong>Hệ thống kiểm so&aacute;t tải trọng xe;<strong>&nbsp;</strong>Hệ thống thu ph&iacute; sử dụng dịch vụ đường bộ tự động kh&ocirc;ng dừng;<strong>&nbsp;</strong>Hệ thống quản l&yacute; gi&aacute;m s&aacute;t, khai th&aacute;c dữ liệu thu ph&iacute; dịch vụ sử dụng đường bộ; Hệ thống quản l&yacute; t&agrave;i sản c&ocirc;ng vụ; Hệ thống quản l&yacute; vốn;..</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,sans-serif\">B&ecirc;n cạnh đ&oacute;, c&aacute;c hệ thống nghiệp vụ h&agrave;nh ch&iacute;nh cũng được Tổng Cục đ&atilde; x&acirc;y dựng như&nbsp;</span><span style=\"font-family:arial,sans-serif\">Hệ thống Quản l&yacute; văn bản v&agrave; điều h&agrave;nh c&ocirc;ng việc;</span>&nbsp;<span style=\"font-family:arial,sans-serif\">Hệ thống quản l&yacute; hồ sơ số h&oacute;a;</span>&nbsp;<span style=\"font-family:arial,sans-serif\">Hệ thống quản l&yacute; nh&acirc;n sự.</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,sans-serif\">Về kết quả đ&aacute;nh gi&aacute; mức độ ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin năm 2017 của Bộ GTVT, Tổng cục ĐBVN được xếp loại tổng thể thứ&nbsp;<strong>02/07</strong>&nbsp;đơn vị trực thuộc Bộ. B&ecirc;n cạnh những mặt mạnh về cung cấp dịch vụ c&ocirc;ng trực tuyến, cơ chế ch&iacute;nh s&aacute;ch, nh&acirc;n lực CNTT th&igrave; vẫn c&ograve;n c&aacute;c tồn tại hạn chế về cơ sở hạ tầng c&ocirc;ng nghệ th&ocirc;ng tin, cổng th&ocirc;ng tin điện tử website.</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,sans-serif\">Trong giai đoạn 2018 &ndash; 2020, Tổng cục ĐBVN dự kiến sẽ triển khai một số nội dung về x<span style=\"color:rgb(34, 34, 34)\">&acirc;y dựng hệ thống th&ocirc;ng tin quản l&yacute; điều h&agrave;nh tập trung của Tổng cục Đường Bộ Việt Nam;</span></span>&nbsp;<span style=\"font-family:arial,sans-serif\"><span style=\"color:rgb(34, 34, 34)\">X&acirc;y dựng hệ thống th&ocirc;ng tin quản l&yacute; điều h&agrave;nh tập trung của Tổng cục ĐBVN;</span></span><span style=\"font-family:arial,sans-serif\">&nbsp;N&acirc;ng cấp, ho&agrave;n thiện hệ thống quản l&yacute; văn bản v&agrave; điều h&agrave;nh c&ocirc;ng việc kết nối với hệ thống quản l&yacute; văn bản v&agrave; điều h&agrave;nh c&ocirc;ng việc Bộ GTVT;</span>&nbsp;<span style=\"font-family:arial,sans-serif\">X&acirc;y dựng c&aacute;c hệ thống nghiệp vụ chuy&ecirc;n ng&agrave;nh đường bộ;</span>&nbsp;<span style=\"font-family:arial,sans-serif\">N&acirc;ng cấp hạ tầng CNTT đảm bảo kết nối chia sẻ dữ liệu d&ugrave;ng chung với hệ thống cơ sở dữ liệu của Bộ GTVT;</span>&nbsp;<span style=\"font-family:arial,sans-serif\">Thực hiện đ&aacute;nh gi&aacute;, đảm bảo an to&agrave;n, an ninh th&ocirc;ng tin mạng cho c&aacute;c hệ thống ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin của Tổng cục ĐBVN bảo đảm thực hiện Kiến tr&uacute;c Ch&iacute;nh phủ điện tử của Bộ GTVT.</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,sans-serif\">Ph&aacute;t biểu tại cuộc họp, Tổng Cục trưởng Tổng cục ĐBVN Nguyễn Văn Huyện cho biết, trong những năm qua, Tổng cục ĐBVN đ&atilde; tập trung đẩy mạnh x&acirc;y dựng c&aacute;c hệ thống ứng dụng CNTT chuy&ecirc;n ng&agrave;nh đường bộ như hệ thống quản l&yacute; t&agrave;i sản đường bộ RAMPS, hệ thống quản l&yacute; mặt đường PMS, quản l&yacute; cầu BMS, hệ thống GSHT, cấp đổi GPLX,..tiến tới tự động h&oacute;a trong việc khảo s&aacute;t thu thập t&agrave;i sản đường bộ, lập kế hoạch bảo tr&igrave; đường bộ,. trong c&ocirc;ng t&aacute;c cải c&aacute;ch h&agrave;nh ch&iacute;nh, b&ecirc;n cạnh triển khai c&aacute;c dịch vụ c&ocirc;ng trực tuyến, Tổng cục đ&atilde; triển khai mới hệ thống phần mềm quản l&yacute; văn bản v&agrave; điều h&agrave;nh c&ocirc;ng việc chủ yếu tr&ecirc;n m&ocirc;i trường điện tử đẩy nhanh tiến độ giải quyết c&ocirc;ng việc phục vụ người d&acirc;n v&agrave; doanh nghiệp; x&acirc;y dựng Trung t&acirc;m dữ liệu dự ph&ograve;ng hệ thống cấp đổi giấy ph&eacute;p l&aacute;i xe,..B&ecirc;n cạnh đ&oacute;, để triển khai Ch&iacute;nh phủ điện tử tại Tổng cục ĐBVN, Tổng Cục trưởng cũng kiến nghị c&aacute;c Vụ, Trung t&acirc;m CNTT Bộ GTVT sớm x&acirc;y dựng quy chế phối hợp quản l&yacute;, vận h&agrave;nh hệ thống CNTT; quy định về sử dụng, chia sẻ dữ liệu d&ugrave;ng chung thuộc kiến tr&uacute;c ch&iacute;nh phủ điện tử; đồng thời Bộ GTVT quan t&acirc;m bố tr&iacute; kinh ph&iacute; để thực hiện việc n&acirc;ng cấp c&aacute;c hệ thống cơ sở dữ liệu x&acirc;y dựng Ch&iacute;nh phủ điện tử tại Tổng cục ĐBVN.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><a class=\"view-gallery\" href=\"https://drvn.gov.vn/upload/20830/fck/TCT%20pb%20tai%20ch%20cpdt_23_11.png\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(46, 46, 46); text-decoration-line: none;\"><img alt=\"\" src=\"https://drvn.gov.vn/upload/20830/fck/TCT%20pb%20tai%20ch%20cpdt_23_11.png\" style=\"box-sizing:border-box; height:432px; max-width:100%; vertical-align:middle; width:640px\" /></a></p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-family:arial,sans-serif\">Tổng Cục trưởng Tổng cục ĐBVN Nguyễn Văn Huyện ph&aacute;t biểu tại buổi họp.</span></em></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12px\"><span style=\"font-family:arial,sans-serif\">Ph&aacute;t biểu chỉ đạo tại cuộc họp, Thứ trưởng Bộ GTVT Nguyễn Ngọc Đ&ocirc;ng ghi nhận những nỗ lực trong c&ocirc;ng t&aacute;c ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin của Tổng cục ĐBVN&nbsp; thời gian qua, đồng thời nhấn mạnh y&ecirc;u cầu Tổng cục cần ph&aacute;t huy thế mạnh sẵn c&oacute;, cần khắc phục kh&oacute; khăn, vận dụng s&aacute;ng tạo nhưng phải đ&uacute;ng quy định, x&aacute;c định nhiệm vụ theo thứ tự ưu ti&ecirc;n, r&agrave; so&aacute;t sắp xếp lại dữ liệu, đề xuất danh mục đầu mối, số h&oacute;a, tạo cơ sở dữ liệu d&ugrave;ng chung theo ph&acirc;n cấp quản l&yacute; v&agrave; bảo mật an to&agrave;n th&ocirc;ng tin; phối hợp với c&aacute;c Vụ tham mưu v&agrave; Trung t&acirc;m CNTT của Bộ sớm ho&agrave;n thiện c&aacute;c quy chế, quy định về quản l&yacute;, chia sẻ dữ liệu, vận h&agrave;nh hệ thống CNTT; tăng cường ứng dụng CNTT trong thời đại c&ocirc;ng nghiệp 4.0 v&agrave;o c&ocirc;ng t&aacute;c quản l&yacute; nh&agrave; nước, đ&aacute;p ứng y&ecirc;u cầu về n&acirc;ng cao chỉ số Ch&iacute;nh phủ điện tử của Bộ GTVT.</span></span></p>\r\n\r\n<p><span style=\"font-size:12px\"><em><span style=\"font-family:arial,sans-serif\">Nguồn: Tổng cục ĐBVN.</span></em></span></p>\r\n\r\n<div style=\"page-break-after: always\"><span style=\"display:none\">&nbsp;</span></div>\r\n\r\n<p>&nbsp;</p>\r\n',NULL,NULL,'12',0,NULL,NULL),(13,'Từ tháng 4, đổi giấy phép lái xe “siêu nhanh” đại trà',0,NULL,'<p><span style=\"color:rgb(119, 119, 119); font-family:arial\">Sau gần ba th&aacute;ng triển khai th&iacute; điểm đổi GPLX qua mạng, Tổng cục Đường bộ VN dự kiến th&aacute;ng 4/2015 sẽ mở rộng &aacute;p dụng tại c&aacute;c Sở GTVT trong cả nước.</span></p>\r\n\r\n<div style=\"page-break-after: always\"><span style=\"display:none\">&nbsp;</span></div>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:inherit\">Sau gần ba th&aacute;ng triển khai th&iacute; điểm đổi GPLX qua mạng, Tổng cục Đường bộ VN dự kiến th&aacute;ng 4/2015 sẽ mở rộng &aacute;p dụng tại c&aacute;c Sở GTVT trong cả nước.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12px\"><span style=\"font-family:inherit\"><a class=\"view-gallery\" href=\"https://drvn.gov.vn/upload/20830/20180629/8011bec4026d7585d8af0f2c7b51956edoi_20gplx_20_281_29.png_t%3D1425979007287.jpg\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(46, 46, 46); text-decoration-line: none;\"><img alt=\"\" src=\"https://drvn.gov.vn/upload/20830/20180629/8011bec4026d7585d8af0f2c7b51956edoi_20gplx_20_281_29.png_t%3D1425979007287.jpg\" style=\"box-sizing:border-box; height:409px; max-width:100%; vertical-align:middle; width:500px\" /></a></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:inherit\"><strong>Gần 1 ngh&igrave;n GPLX được cấp si&ecirc;u nhanh</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\">Theo Tổng cục Đường bộ VN, đến nay đ&atilde; c&oacute; gần 1 ngh&igrave;n GPLX được đổi bằng phương ph&aacute;p &quot;si&ecirc;u nhanh&quot;. Trung b&igrave;nh mỗi ng&agrave;y c&oacute; khoảng 25 - 30 người đăng k&yacute; v&agrave; thực hiện việc đổi GPLX theo cấp độ 3 (đăng k&yacute; qua mạng, đặt lịch hẹn v&agrave; đến ho&agrave;n thiện thủ tục rồi lấy GPLX sau hai giờ). Đến nay, do mới chỉ ở bước th&iacute; điểm, nhưng phương ph&aacute;p n&agrave;y đ&atilde; được người d&acirc;n đ&oacute;n nhận.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\">L&agrave; người đổi GPLX hạng B2 th&agrave;nh c&ocirc;ng qua h&igrave;nh thức nộp hồ sơ qua mạng, anh Trần V&otilde; Hạnh (H&agrave; Nội) cho biết, kh&ocirc;ng ngờ lại nhanh như vậy. Chỉ cần chờ đến lượt l&agrave;m thủ tục mất khoảng 30 ph&uacute;t v&agrave; ho&agrave;n thiện thủ tục mất 5 ph&uacute;t. Sau đ&oacute;, chờ khoảng một giờ nữa l&agrave; được lấy GPLX mới.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\">&quot;Nếu trước đ&acirc;y muốn đổi GPLX &iacute;t nhất phải mất hai lần đi lại. Điều n&agrave;y cho thấy, cơ quan quản l&yacute; v&agrave; cấp đổi GPLX đ&atilde; rất nỗ lực trong cải c&aacute;ch thủ tục h&agrave;nh ch&iacute;nh gi&uacute;p người d&acirc;n như ch&uacute;ng t&ocirc;i đỡ mất thời gian, c&ocirc;ng sức đi lại&quot;, anh Hạnh n&oacute;i. &nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\">Anh Nguyễn Mạnh Cường, một trong những người được cấp đổi th&agrave;nh c&ocirc;ng GPLX đầu ti&ecirc;n bất ngờ: &quot;Theo th&oacute;i quen, t&ocirc;i c&ograve;n tr&igrave;nh th&ecirc;m một số giấy tờ như: Đơn xin cấp đổi GPLX mới, CMND, GPLX (photo), ảnh thẻ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\">Tuy nhi&ecirc;n, nh&acirc;n vi&ecirc;n tiếp nhận hồ sơ đ&atilde; trả lại v&igrave; kh&ocirc;ng cần thiết khi thực hiện đổi GPLX theo c&aacute;ch thức mới. C&aacute;c th&ocirc;ng tin đ&atilde; được đăng k&yacute; qua mạng. Chỉ chưa đầy 5 ph&uacute;t đ&atilde; l&agrave;m xong thủ tục v&agrave; chụp ảnh, sau đ&oacute; chờ lấy GPLX&quot;.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><strong>Th&aacute;ng 4/2015, nh&acirc;n rộng ra c&aacute;c địa phương</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\">Thời gian qua, việc thực hiện đổi GPLX qua mạng được thực hiện ở một địa điểm duy nhất l&agrave; Tổng cục Đường bộ VN. Tuy nhi&ecirc;n, theo lộ tr&igrave;nh, c&ocirc;ng nghệ n&agrave;y sẽ được nh&acirc;n rộng ra to&agrave;n quốc trong thời gian tới. Để chuẩn bị cho c&ocirc;ng t&aacute;c n&agrave;y, Tổng cục Đường bộ VN sẽ thực hiện th&iacute; điểm tại một số địa phương c&oacute; lưu lượng đổi GPLX lớn như: H&agrave; Nội v&agrave; TP Hồ Ch&iacute; Minh, trước khi &aacute;p dụng đại tr&agrave;. Hiện c&aacute;c c&ocirc;ng việc chuẩn bị hệ thống trang thiết bị, đường truyền, nh&acirc;n lực, tập huấn, chuyển giao cho c&aacute;c địa phương đang được gấp r&uacute;t triển khai.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\">Trao đổi với PV B&aacute;o Giao th&ocirc;ng, &ocirc;ng Nguyễn Đ&igrave;nh Nghĩa, Trưởng ph&ograve;ng Quản l&yacute; Phương tiện người l&aacute;i (Sở GTVT H&agrave; Nội) cho biết, hiện mỗi ng&agrave;y H&agrave; Nội cấp, đổi khoảng tr&ecirc;n dưới 1 ngh&igrave;n GPLX tại tất cả c&aacute;c điểm. Dự kiến, trong th&aacute;ng 3/2015, Tổng cục Đường bộ VN sẽ b&agrave;n giao c&ocirc;ng nghệ n&agrave;y cho H&agrave; Nội.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\">&quot;Tuy nhi&ecirc;n, việc lắp đặt v&agrave; x&acirc;y dựng phần mềm sẽ phải cần c&oacute; thời gian chứ chưa thể triển khai ngay lập tức. Hiện nay H&agrave; Nội mỗi ng&agrave;y cấp, đổi h&agrave;ng ngh&igrave;n GPLX trong khi theo t&ocirc;i được biết m&aacute;y in mỗi ph&uacute;t chỉ in được một chiếc th&igrave; việc đổi GPLX qua mạng chắc chắn kh&ocirc;ng thể đ&aacute;p ứng nhu cầu n&ecirc;n việc thực hiện theo c&aacute;ch truyền thống vẫn l&agrave; chủ đạo&quot;, &ocirc;ng Nghĩa n&oacute;i v&agrave; cho biết, nếu hạ tầng chưa được đ&aacute;p ứng đầy đủ, cần kiến nghị đầu tư th&ecirc;m để đ&aacute;p ứng tốt hơn nhu cầu. Nếu kh&ocirc;ng, phải th&ocirc;ng b&aacute;o rộng r&atilde;i khả năng đ&aacute;p ứng để tr&aacute;nh t&igrave;nh trạng &ugrave;n tắc, x&aacute;o trộn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\">&Ocirc;ng V&otilde; Trọng Nh&acirc;n, Trưởng ph&ograve;ng Quản l&yacute; s&aacute;t hạch v&agrave; cấp GPLX TP Hồ Ch&iacute; Minh cho biết, c&ugrave;ng với việc thực hiện c&ocirc;ng t&aacute;c cấp, đổi GPLX qua tổng đ&agrave;i, hiện th&agrave;nh phố cũng đang gấp r&uacute;t thực hiện c&ocirc;ng t&aacute;c chuẩn bị như: M&aacute;y m&oacute;c, thiết bị, nh&acirc;n sự, trang bị đường truyền internet tốc độ cao...để triển khai phục vụ c&ocirc;ng t&aacute;c cấp, đổi GPLX qua mạng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\">&quot;Sau khi c&ocirc;ng t&aacute;c chuẩn bị ho&agrave;n th&agrave;nh, ch&uacute;ng t&ocirc;i sẽ đưa v&agrave;o chạy thử nghiệm. Dự kiến đầu qu&yacute; II/2015 người d&acirc;n c&oacute; thể đăng k&yacute;, cấp đổi GPLX qua mạng internet, giảm bớt thời gian đi lại, thuận tiện cho người d&acirc;n khi đổi, cấp GPLX mới&quot;, &ocirc;ng Nh&acirc;n cho hay.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\">Li&ecirc;n quan c&ocirc;ng t&aacute;c triển khai cấp đổi GPLX qua mạng, &ocirc;ng Nguyễn Hồng Kỳ, Gi&aacute;m đốc Sở GTVT Nghệ An cho biết, ngay từ đầu năm, Sở đ&atilde; chỉ đạo c&aacute;c ph&ograve;ng ban chuy&ecirc;n m&ocirc;n v&agrave; đơn vị li&ecirc;n quan t&iacute;ch cực đẩy nhanh c&ocirc;ng t&aacute;c chuẩn bị, sớm đưa v&agrave;o thử nghiệm hệ thống tiếp nhận hồ sơ đổi GPLX qua mạng. Dự kiến trong th&aacute;ng 3/2015, hệ thống n&agrave;y sẽ triển khai rộng r&atilde;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\">Chiều qua (4/3), Gi&aacute;m đốc Sở GTVT Đ&agrave; Nẵng L&ecirc; Văn Trung cho biết, Đ&agrave; Nẵng đ&atilde; sẵn s&agrave;ng &quot;chờ lệnh&quot; của Tổng cục Đường bộ VN chuyển giao phần mềm cấp đổi GPLX qua mạng để tiến h&agrave;nh thực hiện v&agrave;o đầu th&aacute;ng 4/2015.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\">Đ&acirc;y l&agrave; c&aacute;ch l&agrave;m đơn giản, nhanh gọn nhưng mang lại hiệu quả cao cho người d&acirc;n. &quot;Mới hay tin Tổng cục Đường bộ VN chạy thử nghiệm, nhiều doanh nghiệp vận tải tr&ecirc;n địa b&agrave;n đ&atilde; nhắn tin v&agrave;o hộp thư điện tử của Sở để hỏi thời gian thực hiện ở Đ&agrave; Nẵng&quot;, &ocirc;ng Trung n&oacute;i.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\">Nhiều địa phương kh&aacute;c như: Quảng Nam, Thừa Thi&ecirc;n - Huế, Quảng Trị, Thanh H&oacute;a,... cũng đang tất bật ho&agrave;n tất c&aacute;c thủ tục về giao dịch một cửa điện tử để kịp h&ograve;a c&ugrave;ng việc cấp đổi GPLX si&ecirc;u nhanh qua mạng từ th&aacute;ng 4/2015. Tất cả c&aacute;c c&ocirc;ng nghệ điện tử để phục vụ phần mềm cấp đổi GPLX qua mạng trong thời gian tới đều được &quot;t&acirc;n trang&quot; bằng c&aacute;c m&aacute;y m&oacute;c hiện đại.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:inherit\">Nguồn: baogiaothong.vn</span></span></p>\r\n',NULL,NULL,'13',0,NULL,NULL),(14,'Tập trung hoàn thành tốt nhiệm vụ từ những tháng đầu năm',0,NULL,'<p><strong><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">S&aacute;ng 28/2, Bộ trưởng Nguyễn Văn Thể đ&atilde; chủ tr&igrave; cuộc họp giao ban th&aacute;ng 2 năm 2019. C&ugrave;ng dự c&oacute; c&aacute;c Thứ trưởng, thủ trưởng c&aacute;c Vụ, Cục, Ban Quản l&yacute; dự &aacute;n thuộc Bộ.</span></span></strong></p>\r\n\r\n<div style=\"page-break-after: always\"><span style=\"display:none\">&nbsp;</span></div>\r\n\r\n<div class=\"content-detail font-size-text mb-20\" style=\"box-sizing: border-box; font-family: arial; margin-bottom: 20px !important;\">\r\n<p style=\"text-align:center\"><a class=\"view-gallery\" href=\"https://drvn.gov.vn/upload/20830/fck/B%E1%BB%99%20tr%C6%B0%E1%BB%9Fng5_3.png\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(46, 46, 46); text-decoration-line: none;\"><img alt=\"\" src=\"https://drvn.gov.vn/upload/20830/fck/B%E1%BB%99%20tr%C6%B0%E1%BB%9Fng5_3.png\" style=\"box-sizing:border-box; height:361px; max-width:100%; vertical-align:middle; width:542px\" /></a></p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Bộ trưởng Nguyễn Văn Thể chủ tr&igrave; cuộc họp</span></span></em></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Đảm bảo vận tải Tết</span></span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">B&aacute;o c&aacute;o tổng hợp của Văn ph&ograve;ng Bộ cho biết, trong th&aacute;ng 02/2019, Bộ GTVT v&agrave; c&aacute;c cơ quan, đơn vị trong to&agrave;n ng&agrave;nh đ&atilde; nỗ lực ho&agrave;n th&agrave;nh tốt c&aacute;c mặt c&ocirc;ng t&aacute;c, đặc biệt l&agrave; bảo đảm TTATGT, phục vụ tốt nhu cầu đi lại của nh&acirc;n d&acirc;n trong dịp Tết Nguy&ecirc;n đ&aacute;n 2019, Tổng cục ĐBVN, c&aacute;c Cục quản l&yacute; chuy&ecirc;n ng&agrave;nh v&agrave; c&aacute;c cơ quan, đơn vị thuộc Bộ đ&atilde; b&aacute;m s&aacute;t kế hoạch v&agrave; chỉ đạo của Bộ, triển khai c&aacute;c giải ph&aacute;p đồng bộ bảo đảm TTATGT v&agrave; phục vụ tốt nhu cầu đi lại của nh&acirc;n d&acirc;n, tổ chức giao th&ocirc;ng, bảo đảm an ninh, an to&agrave;n trong thời gian diễn ra Hội nghị thượng đỉnh Mỹ - Triều Ti&ecirc;n.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">So với 2 th&aacute;ng đầu năm 2018, TNGT giảm cả về số vụ, số người chết v&agrave; số người bị thương; cụ thể, số vụ TNGT giảm 523 vụ (giảm 15,64%), số người chết giảm 150 người (giảm 9,96%), số người bị thương giảm 348 người (giảm 13,83%).</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Sản lượng vận tải 02 th&aacute;ng đầu năm ước đạt 277,837 triệu tấn h&agrave;ng, tăng 8,7%; đạt 822,6 triệu h&agrave;nh kh&aacute;ch, tăng 10,2% so với c&ugrave;ng kỳ năm 2018; khối lượng lu&acirc;n chuyển h&agrave;ng h&oacute;a ước đạt 52,46 tỷ Tấn.km, lu&acirc;n chuyển h&agrave;nh kh&aacute;ch ước đạt 36,87 tỷ HK.km; tăng 6,4% về lu&acirc;n chuyển h&agrave;ng h&oacute;a v&agrave; tăng 10,5% về lu&acirc;n chuyển h&agrave;nh kh&aacute;ch so với c&ugrave;ng kỳ năm 2018. Ri&ecirc;ng th&aacute;ng 02/2018, sản lượng vận tải ước đạt 133,531 triệu tấn h&agrave;ng, 413,793 triệu h&agrave;nh kh&aacute;ch; khối lượng lu&acirc;n chuyển h&agrave;ng h&oacute;a đạt 25,148 tỷ Tấn.km, lu&acirc;n chuyển h&agrave;nh kh&aacute;ch đạt 18,714 tỷ HK.km.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">B&ecirc;n cạnh đ&oacute;, c&ocirc;ng t&aacute;c chuẩn bị c&aacute;c dự &aacute;n trọng điểm của ng&agrave;nh như tuyến đường bộ cao tốc Bắc - Nam ph&iacute;a Đ&ocirc;ng, Dự &aacute;n CHKQT Long Th&agrave;nh, mở rộng CHKQT T&acirc;n Sơn Nhất, nghi&ecirc;n cứu tiền khả thi đường sắt tốc độ cao Bắc - Nam, thu ph&iacute; tự động kh&ocirc;ng dừng... tiếp tục được Bộ tập trung triển khai b&aacute;m s&aacute;t kế hoạch đề ra. Kết quả giải ng&acirc;n 02 th&aacute;ng năm 2019 ước đạt 1.840 tỷ đồng, đạt 17,26% kế hoạch.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Ngo&agrave;i ra, Bộ cũng tiếp tục triển khai x&acirc;y dựng hệ thống phần mềm một cửa điện tử, một cửa điện tử li&ecirc;n th&ocirc;ng, n&acirc;ng cấp Cổng dịch vụ c&ocirc;ng trực tuyến của Bộ; triển khai x&acirc;y dựng hệ thống danh mục dữ liệu d&ugrave;ng chung phục vụ t&iacute;ch hợp dữ liệu theo quy định tại Kiến tr&uacute;c Ch&iacute;nh phủ điện tử. Triển khai kế hoạch h&agrave;nh động th&uacute;c đẩy Cơ chế một cửa quốc gia, Cơ chế một cửa ASEAN, cải c&aacute;ch c&ocirc;ng t&aacute;c kiểm tra chuy&ecirc;n ng&agrave;nh đối với h&agrave;ng h&oacute;a xuất khẩu, nhập khẩu v&agrave; tạo thuận lợi thương mại giai đoạn 2018-2020 của Bộ. Ho&agrave;n th&agrave;nh n&acirc;ng cấp phần mềm quản l&yacute; văn bản của Bộ, kết nối li&ecirc;n th&ocirc;ng với phần mềm quản l&yacute; văn bản của Văn ph&ograve;ng Ch&iacute;nh phủ.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Về nhiệm vụ c&ocirc;ng t&aacute;c th&aacute;ng 3, L&atilde;nh đạo Văn ph&ograve;ng Bộ đề nghị thủ trưởng c&aacute;c cơ quan, đơn vị cần b&aacute;m s&aacute;t chương tr&igrave;nh c&ocirc;ng t&aacute;c của Ch&iacute;nh phủ, của Bộ năm 2019, chủ động ho&agrave;n thiện c&aacute;c dự thảo văn bản QPPL, đề &aacute;n bảo đảm chất lượng v&agrave; tr&igrave;nh đ&uacute;ng hạn. Chủ động phối hợp chặt chẽ với c&aacute;c cơ quan chức năng của địa phương tập trung đẩy nhanh tiến độ triển khai Dự &aacute;n CHKQT Long Th&agrave;nh, mở rộng CHKQT T&acirc;n Sơn Nhất, triển khai hiệu quả c&ocirc;ng t&aacute;c GPMB c&aacute;c Dự &aacute;n đường bộ cao tốc Bắc - Nam ph&iacute;a Đ&ocirc;ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Tại cuộc họp l&atilde;nh đạo c&aacute;c Vụ, Cục quản l&yacute; chuy&ecirc;n ng&agrave;nh cũng đ&atilde; nghe b&aacute;o c&aacute;o chi tiết c&ocirc;ng t&aacute;c x&acirc;y dựng văn bản QPPL, t&igrave;nh h&igrave;nh bảo đảm TTATGT, tiến độ, chất lượng thi c&ocirc;ng c&aacute;c dự &aacute;n trọng điểm cũng như một số c&ocirc;ng t&aacute;c quản l&yacute; nh&agrave; nước của c&aacute;c đơn vị.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Ph&aacute;t biểu kết luận cuộc họp, Bộ trưởng Nguyễn Văn Thể cơ bản thống nhất với b&aacute;o c&aacute;o của văn ph&ograve;ng Bộ v&agrave; c&aacute;c cơ quan chuy&ecirc;n m&ocirc;n đ&atilde; tr&igrave;nh b&agrave;y. Bộ trưởng cho rằng &quot;nổi bật trong hai th&aacute;ng đầu năm ch&iacute;nh l&agrave; c&ocirc;ng t&aacute;c phục vụ vận tải Tết. C&ocirc;ng t&aacute;c n&agrave;y đ&atilde; được chuẩn bị chu đ&aacute;o, an to&agrave;n, bảo đảm đ&uacute;ng tinh thần kh&ocirc;ng để người d&acirc;n n&agrave;o phải ở lại do kh&ocirc;ng c&oacute; phương tiện vận tải về qu&ecirc; ăn Tết&quot;. C&ocirc;ng t&aacute;c bảo đảm TTATGT cũng c&oacute; sự chuyển biến tốt, theo Bộ trưởng, đ&acirc;y l&agrave; một c&aacute;i Tết c&oacute; số người bị chết, bị thương do TNGT &iacute;t nhất trong những năm gần đ&acirc;y. B&ecirc;n cạnh đ&oacute;, &quot;l&atilde;nh đạo Bộ đ&atilde; hết sức s&aacute;t sao trong c&ocirc;ng t&aacute;c chỉ đạo, kiểm tra c&ocirc;ng t&aacute;c phục vụ vận tải trước v&agrave; trong dịp Tết&quot;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Tập trung thự hiện v&agrave; ho&agrave;n th&agrave;nh sớm c&aacute;c dự &aacute;n trọng điểm</span></span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Về nhiệm vụ trong th&aacute;ng 3 v&agrave; năm 2019, Bộ trưởng y&ecirc;u cầu c&aacute;c cơ quan, đơn vị khẩn trương, thực hiện v&agrave; ho&agrave;n th&agrave;nh sớm c&aacute;c nhiệm vụ m&agrave; Ch&iacute;nh phủ đ&atilde; giao. Bộ trưởng nhấn mạnh &quot;c&ocirc;ng việc Ch&iacute;nh phủ đ&atilde; giao th&igrave; ch&uacute;ng ta nhất định phải thực hiện. Nếu để muộn, kh&ocirc;ng những ch&uacute;ng ta vẫn phải ho&agrave;n th&agrave;nh c&ocirc;ng việc đ&oacute; m&agrave; ch&uacute;ng ta c&ograve;n bị ph&ecirc; b&igrave;nh, do vậy t&ocirc;i đề nghị c&aacute;c đồng ch&iacute; tập trung thực hiện v&agrave; ho&agrave;n th&agrave;nh sớm&quot;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Về c&ocirc;ng t&aacute;c x&acirc;y dựng văn bản quy phạm ph&aacute;p luật, Bộ trưởng y&ecirc;u cầu Vụ Ph&aacute;p chế v&agrave; Tổng cục Đường bộ Việt Nam b&aacute;m s&aacute;t nhiệm vụ m&agrave; Ủy ban thường vụ quốc hội, Quốc hội giao, sớm sửa đổi Luật Giao th&ocirc;ng đường bộ cũng như r&agrave; so&aacute;t c&aacute;c luật chuy&ecirc;n ng&agrave;nh kh&aacute;c, c&aacute;c Th&ocirc;ng tư, t&igrave;m những điểm b&aacute;t cập để kiến nghị sửa đổi.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Bộ trưởng cũng đề nghị Vụ Vận tải, Tổng cục ĐBVN v&agrave; c&aacute;c cục chuy&ecirc;n ng&agrave;nh quan t&acirc;m triển khai kịp thời, hiệu quả c&ocirc;ng t&aacute;c bảo đảm TTATGT, đặc biệt c&ocirc;ng t&aacute;c đảm bảo an to&agrave;n an ninh h&agrave;ng kh&ocirc;ng; khắc phục t&igrave;nh trạng xe d&ugrave;, bến c&oacute;c, xe qu&aacute; tải; siết chặt c&ocirc;ng t&aacute;c s&aacute;t hạch v&agrave; cấp giấy ph&eacute;p l&aacute;i xe.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Đối với c&aacute;c dự &aacute;n trọng điểm, Bộ trưởng Nguyễn Văn Thể đề nghị Thứ trưởng L&ecirc; Đ&igrave;nh Thọ tiếp tục quan t&acirc;m chỉ đạo c&aacute;c dự &aacute;n h&agrave;ng kh&ocirc;ng như Dự &aacute;n cảng h&agrave;ng kh&ocirc;ng Long Thanh, mở rộng s&acirc;n bay T&acirc;n Sơn Nhất để đẩy nhanh tiến độ dự &aacute;n, đồng thời chuẩn bị b&aacute;o c&aacute;o Ch&iacute;nh phủ về c&aacute;c dự &aacute;n n&agrave;y. Vụ KHĐT, Cục QLXD &amp; CLCTGT theo d&otilde;i th&uacute;c đẩy tiến độ c&aacute;c dự &aacute;n. C&aacute;c Ban QLDA tập trung thực hiện tốt c&ocirc;ng t&aacute;c GPMB trước hết l&agrave; c&ocirc;ng t&aacute;c cắm mốc giới GPMB v&agrave; b&agrave;n giao cho c&aacute;c địa phương trước cuối th&aacute;ng 3 năm 2019.</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:arial,sans-serif\">Nguồn: mt.gov.vn</span></span></p>\r\n\r\n<div>&nbsp;</div>\r\n</div>\r\n',NULL,NULL,'14',0,NULL,NULL),(16,'Từ 1/1/2017, đưa vào vận hành giải pháp giao thông thông minh trên nền bản đồ số',0,NULL,'<p><span style=\"color:rgb(119, 119, 119); font-family:arial\">Đ&acirc;y l&agrave; chỉ đạo của Thứ trưởng Nguyễn Hồng Trường tại cuộc họp với l&atilde;nh đạo c&aacute;c cơ quan, đơn vị li&ecirc;n quan về t&igrave;nh h&igrave;nh thực hiện Đề &aacute;n &ldquo;Giải ph&aacute;p giao th&ocirc;ng th&ocirc;ng minh tr&ecirc;n nền bản đồ số&rdquo;, s&aacute;ng ng&agrave;y 19/10.</span></p>\r\n\r\n<div style=\"page-break-after: always\"><span style=\"display:none\">&nbsp;</span></div>\r\n\r\n<p><span style=\"font-size:14px\">Đề &aacute;n &quot;Giải ph&aacute;p giao th&ocirc;ng th&ocirc;ng minh tr&ecirc;n nền bản đồ số&quot; được Bộ GTVT chỉ định giao C&ocirc;ng ty TNHH MTV Hanel thực hiện Đề &aacute;n ở cả 5 lĩnh vực quản l&yacute; nh&agrave; nước của Bộ GTVT l&agrave; đường bộ, h&agrave;ng hải, đường thủy nội địa, h&agrave;ng kh&ocirc;ng v&agrave; đường sắt. Qu&aacute; tr&igrave;nh thực hiện Đề &aacute;n được chia th&agrave;nh hai giai đoạn: Giai đoạn 1, thực hiện trong lĩnh vực đường bộ; giai đoạn 2, thực hiện c&aacute;c lĩnh vực c&ograve;n lại. Bộ GTVT giao Trung t&acirc;m C&ocirc;ng nghệ th&ocirc;ng tin thay mặt Bộ thẩm tra, thẩm định Đề &aacute;n tổng thể.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><a class=\"view-gallery\" href=\"https://drvn.gov.vn/upload/20830/20180629/1d2caa06cdf151661f5719fd8432d92agiaiphap.jpg_t%3D1477126361352.jpg\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(46, 46, 46); text-decoration-line: none;\"><img alt=\"\" src=\"https://drvn.gov.vn/upload/20830/20180629/1d2caa06cdf151661f5719fd8432d92agiaiphap.jpg_t%3D1477126361352.jpg\" style=\"box-sizing:border-box; height:281px; max-width:100%; vertical-align:middle; width:500px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>Giải ph&aacute;p giao th&ocirc;ng th&ocirc;ng minh tr&ecirc;n nền bản đồ số sẽ được&nbsp;đưa v&agrave;o vận h&agrave;nh từ 1/1/2017.&nbsp;Ảnh minh họa</em></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">&Ocirc;ng Phạm Duy Ninh - Gi&aacute;m đốc Trung t&acirc;m C&ocirc;ng nghệ th&ocirc;ng tin Bộ GTVT cho biết, Bộ GTVT l&agrave; cơ quan quản l&yacute; duy nhất một bản đồ giao th&ocirc;ng kỹ thuật số thống nhất tr&ecirc;n to&agrave;n quốc. C&aacute;c cơ quan, đơn vị c&oacute; thể triển khai x&acirc;y dựng c&aacute;c ứng dụng tr&ecirc;n nền bản đồ số n&agrave;y theo c&aacute;c lĩnh vực chuy&ecirc;n ng&agrave;nh. Bộ sẽ x&acirc;y dựng quy chế cập nhật v&agrave; chia sẻ th&ocirc;ng tin, dữ liệu li&ecirc;n quan. Trung t&acirc;m C&ocirc;ng nghệ th&ocirc;ng tin được Bộ GTVT giao trực tiếp quản l&yacute; bản đồ số của Bộ v&agrave; l&agrave; đầu mối cung cấp bản đồ cho Tổng cục v&agrave; c&aacute;c Cục theo quy định.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Theo b&agrave; B&ugrave;i Thị Hải Yến - Ph&oacute; Tổng gi&aacute;m đốc C&ocirc;ng ty TNHH MTV Hanel, giải ph&aacute;p tổng thể quản l&yacute; giao th&ocirc;ng th&ocirc;ng minh tr&ecirc;n nền bản đồ số kết nối c&aacute;c ứng dụng, cơ sở dữ liệu từ cả 5 lĩnh vực tr&ecirc;n, nhằm gi&uacute;p Bộ GTVT c&oacute; một hệ thống quản l&yacute; tổng thể v&agrave; sẽ mang lại nhiều lợi &iacute;ch to lớn như tối ưu h&oacute;a hiệu quả quản l&yacute; nh&agrave; nước bằng ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin; giải quyết được c&aacute;c vấn đề nhức nhối nhất hiện nay cho c&aacute;c doanh nghiệp v&agrave; đơn vị vận tải; cung cấp cho người d&acirc;n c&aacute;c th&ocirc;ng tin chỉ dẫn giao th&ocirc;ng, điểm đen, điểm &ugrave;n tắc, cảnh b&aacute;o tốc độ v&agrave; tạo m&ocirc;i trường giao th&ocirc;ng an to&agrave;n; n&acirc;ng cao &yacute; thức tham gia giao th&ocirc;ng, tạo niềm tin v&agrave; sự an to&agrave;n cho người d&acirc;n.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Hiện tại, C&ocirc;ng ty TNHH MTV Hanel đ&atilde; x&acirc;y dựng xong c&aacute;c ứng dụng quản l&yacute; vận tải đường bộ theo y&ecirc;u cầu của Bộ GTVT, gồm hệ thống gi&aacute;m s&aacute;t h&agrave;nh tr&igrave;nh, hệ thống biển b&aacute;o tốc độ, hệ thống t&iacute;ch hợp ứng dụng quản l&yacute; tuyến cố định, hệ thống gi&aacute;m s&aacute;t h&agrave;nh tr&igrave;nh hai chiều, hệ thống s&agrave;n giao dịch vận tải v&agrave; hệ thống điều h&agrave;nh taxi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Nhấn mạnh về t&iacute;nh cấp thiết phải sớm ph&ecirc; duyệt Đề &aacute;n, Thứ trưởng Nguyễn Hồng Trường y&ecirc;u cầu Trung t&acirc;m C&ocirc;ng nghệ th&ocirc;ng tin của Bộ c&ugrave;ng Tổng cục Đường bộ Việt Nam v&agrave; C&ocirc;ng ty TNHH MTV Hanel phối hợp r&agrave; so&aacute;t lại c&aacute;c c&ocirc;ng việc li&ecirc;n quan để đẩy nhanh tiến độ Đề &aacute;n.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Thứ trưởng đồng &yacute; triển khai Đề &aacute;n theo hai giai đoạn, trong giai đoạn 1, tập trung v&agrave;o c&ocirc;ng t&aacute;c quản l&yacute; Nh&agrave; nước về hệ thống gi&aacute;m s&aacute;t h&agrave;nh tr&igrave;nh, quản l&yacute; tuyến cố định, quản l&yacute; cầu - đường, quản l&yacute; c&aacute;c vụ TNGT...; đồng thời lưu &yacute; trong Đề &aacute;n phải n&oacute;i r&otilde; về thực hiện quyết định của Nh&agrave; nước về thu&ecirc; dịch vụ hạ tầng c&ocirc;ng nghệ th&ocirc;ng tin, tr&ecirc;n cơ sở đảm bảo t&iacute;nh độc quyền.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Thứ trưởng giao Trung t&acirc;m C&ocirc;ng nghệ th&ocirc;ng tin tham mưu về gi&aacute; thu&ecirc;, Tổng cục Đường bộ Việt Nam tham mưu, thẩm định c&aacute;c thiết bị thu&ecirc;; tr&ecirc;n cơ sở đ&oacute; x&acirc;y dựng Đề &aacute;n về tổng thể, kinh ph&iacute; đầu tư, để Tổng cục Đường bộ Việt Nam đưa v&agrave;o kế hoạch bố tr&iacute; vốn h&agrave;ng năm, thanh to&aacute;n cho Nh&agrave; đầu tư.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Thứ trưởng cũng giao Tổng cục Đường bộ Việt Nam l&agrave; cơ quan chủ tr&igrave; soạn thảo Đề &aacute;n, phối hợp với c&aacute;c cơ quan, đơn vị li&ecirc;n quan ho&agrave;n thiện Đề &aacute;n trong th&aacute;ng 10/2016, để Bộ ph&ecirc; duyệt trong đầu th&aacute;ng 11/2016 v&agrave; ch&iacute;nh thức đưa v&agrave;o sử dụng từ 1/1/2017.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\">Nguồn: mt.gov.vn</span></p>\r\n',NULL,NULL,'16',0,NULL,NULL),(18,'Điều chỉnh các vị trí mở dải phân cách giữa QL.1 đoạn qua tỉnh Đồng Nai',0,NULL,'<p><span style=\"color:rgb(119, 119, 119); font-family:arial\">Ng&agrave;y 25/5/2015, Tổng cục Đường bộ Việt Nam vừa c&oacute; văn bản số 2535 /TCĐBVN-ATGT về việc điều chỉnh c&aacute;c vị tr&iacute; mở dải ph&acirc;n c&aacute;ch giữa QL.1 đoạn qua tỉnh Đồng Nai.</span></p>\r\n\r\n<div style=\"page-break-after: always\"><span style=\"display:none\">&nbsp;</span></div>\r\n\r\n<p style=\"text-align:justify\">Tổng cục Đường bộ Việt Nam nhận được văn bản số 62/BATGT ng&agrave;y 15/4/2015 của Ban An to&agrave;n giao th&ocirc;ng tỉnh Đồng Nai v/v &quot;tiếp tục kiến nghị c&aacute;c vị tr&iacute; đ&oacute;ng, mở dải ph&acirc;n c&aacute;ch giữa tr&ecirc;n tuyến QL1, địa b&agrave;n tỉnh Đồng Nai&quot; v&agrave; văn bản số 1054/CQLĐB IV-ATGT ng&agrave;y 14/4/2015 của Cục QLĐB IV v/v &quot;b&aacute;o c&aacute;o kết quả kiểm tra đề xuất xử l&yacute; c&aacute;c vị tr&iacute; đ&oacute;ng, mở DPC giữa tr&ecirc;n QL1 đoạn dự &aacute;n BOT qua địa phận Đồng Nai&quot;;</p>\r\n\r\n<p style=\"text-align:center\"><a class=\"view-gallery\" href=\"https://drvn.gov.vn/upload/20830/20180629/1c0c8055eca3acad5b59491b8e678b44phancachgiua.png_t%3D1432808313754.jpg\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(46, 46, 46); text-decoration-line: none;\"><img alt=\"\" src=\"https://drvn.gov.vn/upload/20830/20180629/1c0c8055eca3acad5b59491b8e678b44phancachgiua.png_t%3D1432808313754.jpg\" style=\"box-sizing:border-box; height:372px; max-width:100%; vertical-align:middle; width:450px\" /></a></p>\r\n\r\n<p style=\"text-align:justify\">Sau khi nghi&ecirc;n cứu, xem x&eacute;t, Tổng cục Đường bộ Việt Nam chấp thuận điều chỉnh c&aacute;c vị tr&iacute; đ&oacute;ng, mở dải ph&acirc;n c&aacute;ch giữa đ&atilde; được Ban ATGT tỉnh Đồng Nai, Cục QLĐB IV v&agrave; c&aacute;c đơn vị c&oacute; li&ecirc;n quan thống nhất đề xuất; cụ thể như sau:</p>\r\n\r\n<p style=\"text-align:justify\">1/ Mở dải ph&acirc;n c&aacute;ch giữ tại Km1817+750 ( đường v&agrave;o khu d&acirc;n cư, Trung t&acirc;m văn h&oacute;a v&agrave; Gi&aacute;o xứ Cẩm T&acirc;n)</p>\r\n\r\n<p style=\"text-align:justify\">2/ Tăng chiều rộng điểm mở tại Km1822+100 do c&oacute; nhiều xe Contener ra v&agrave;o (đường v&agrave;o khu c&ocirc;ng nghiệp Suối Tre) v&agrave; c&oacute; kết hợp đ&egrave;n điều khiển giao th&ocirc;ng.</p>\r\n\r\n<p style=\"text-align:justify\">3/ Mở dải ph&acirc;n c&aacute;ch giữa tại Km1824+340 &nbsp;(đường v&agrave;o Bệnh viện Đa khoa Thị x&atilde; Long Kh&aacute;nh).</p>\r\n\r\n<p style=\"text-align:justify\">4/ Mở dải ph&acirc;n c&aacute;ch giữa tại Km1824+650 &nbsp;(đường v&agrave;o Bệnh viện Suối Tre).</p>\r\n\r\n<p style=\"text-align:justify\">5/ Đ&oacute;ng dải ph&acirc;n c&aacute;ch giữa tại Km1824+375.</p>\r\n\r\n<p style=\"text-align:justify\">6/ Mở dải ph&acirc;n c&aacute;ch giữ tại Km1849+700.</p>\r\n\r\n<p style=\"text-align:justify\">Tổng cục Đường bộ Việt Nam đề nghị Ban ATGT lựa chọn đơn vị Tư vấn thiết kế v&agrave; tổ chức giao th&ocirc;ng tại c&aacute;c vị tr&iacute; điều chỉnh đ&oacute;ng, mở dải ph&acirc;n c&aacute;ch giữa v&agrave; bố tr&iacute; nguồn vốn đề thực hiện.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Đồng thời, Tổng cục Đường bộ giao Cục QLĐB IV phối hợp chặt chẽ với Ban ATGT tỉnh Đồng Nai v&agrave; c&aacute;c đơn vị c&oacute; li&ecirc;n quan trong qu&aacute; tr&igrave;nh thực hiện đ&oacute;ng, mở dải ph&acirc;n c&aacute;ch v&agrave; trong qu&aacute; tr&igrave;nh khai th&aacute;c, đảm bảo giao th&ocirc;ng lu&ocirc;n lan to&agrave;n, th&ocirc;ng suốt.</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:inherit\">Nguồn: Tổng cục ĐBVN</span></p>\r\n',NULL,NULL,'18',0,NULL,NULL),(20,'Thi bằng lái xe tại Việt Nam có \"phí chống trượt\"?',0,NULL,'<h2><span style=\"font-size:14px\">Với loại &quot;ph&iacute; chống trượt&quot;, nhiều người kh&ocirc;ng biết g&igrave; về luật giao th&ocirc;ng vẫn c&oacute; thể được cấp bằng l&aacute;i xe đ&agrave;ng ho&agrave;ng.</span></h2>\r\n\r\n<div style=\"page-break-after: always\"><span style=\"display:none\">&nbsp;</span></div>\r\n\r\n<p>Th&ocirc;ng tin về những tai nạn trong những ng&agrave;y nghỉ lễ thật l&agrave; đ&aacute;ng buồn. X&oacute;t thương cho những người đ&atilde; kh&ocirc;ng may tử nạn dưới b&aacute;nh xe cũng chỉ v&igrave; những &quot;ch&eacute;n ch&uacute;, ch&eacute;n anh&quot; v&agrave; rồi những người cầm l&aacute;i đến 13 năm cũng vẫn g&acirc;y ra tai nạn thương t&acirc;m.</p>\r\n\r\n<p>Trong việc l&aacute;i xe kh&ocirc;ng ai c&oacute; thể n&oacute;i &quot;hay&quot; được điều g&igrave;, tuy nhi&ecirc;n n&oacute;i đi th&igrave; cũng phải n&oacute;i lại nguy&ecirc;n nh&acirc;n một phần cũng l&agrave; sự quản l&yacute; kh&ocirc;ng chặt của cơ quan chức năng. Việc qu&aacute; dễ d&atilde;i trong việc cấp giấy ph&eacute;p l&aacute;i xe, cho đến việc hạ tầng giao th&ocirc;ng kh&ocirc;ng đi đ&ocirc;i với sự ph&aacute;t triển của phương tiện giao th&ocirc;ng.</p>\r\n\r\n<p>Về việc cấp giấy ph&eacute;p l&aacute;i xe t&ocirc;i thấy bất cập, đầu ti&ecirc;n l&agrave; bộ c&acirc;u hỏi &quot;đ&aacute;nh đố&quot; người đi thi lấy giấy ph&eacute;p, dẫn đến c&oacute; sự ti&ecirc;u cực l&agrave; &quot;đ&oacute;ng tiền chống trượt&quot;. Khi đ&oacute;ng tiền chống trượt c&oacute; nhiều học vi&ecirc;n chả phải học g&igrave;, khi đi thi &quot;thủ sẵn khẩu khuyết&quot; hoặc sẽ c&oacute; người đến gần nhắc c&acirc;u n&agrave;y t&iacute;ch A, c&acirc;u kia điền đ&aacute;p &aacute;n B... . Đến khi ra đường, những người n&agrave;y chả biết g&igrave; về luật giao th&ocirc;ng như thế n&agrave;o l&agrave; biển cấm rẽ tr&aacute;i, rẽ phải, vạch liền ...</p>\r\n\r\n<p>Đến b&agrave;i thi thực h&agrave;nh, nếu như &quot;quen biết&quot; bằng c&aacute;ch n&agrave;o đ&oacute; sẽ cho đỗ, c&ograve;n nếu kh&ocirc;ng quen biết th&igrave; c&oacute; thể đ&oacute;ng tiền thi lại đến khi n&agrave;o đỗ th&igrave; th&ocirc;i miễn l&agrave; c&oacute; đợt thi. T&ocirc;i đ&atilde; từng biết c&oacute; người thi lại đến 5-6 lần mới đỗ, thậm ch&iacute; cả chục lần, thử hỏi những người n&agrave;y c&oacute; n&ecirc;n cho ra đường l&aacute;i xe hay kh&ocirc;ng.</p>\r\n\r\n<p>Về giao th&ocirc;ng tại Việt Nam t&ocirc;i thấy thật sự kh&oacute; chịu, &ocirc;t&ocirc; v&agrave; xe m&aacute;y đi lẫn lộn v&agrave;o nhau, chẳng ph&acirc;n l&agrave;n, cũng thỉnh thoảng những tuyến đường c&oacute; ph&acirc;n l&agrave;n, nhưng c&aacute;i đấy chỉ để cho c&oacute;, chứ thực chất chẳng ai để &yacute; c&aacute;i biển đấy v&igrave; c&oacute; học luật đ&acirc;u m&agrave; biết, m&agrave; để &yacute;.</p>\r\n\r\n<p>Xe m&aacute;y th&igrave; cứ cắm đầu v&agrave;o trước &ocirc;t&ocirc; để đi, mặc cho &ocirc;t&ocirc; c&ograve;i inh ỏi l&ecirc;n. H&atilde;y tưởng tưởng tất cả c&aacute;c xe c&ugrave;ng c&ograve;i inh ỏi th&igrave; giao th&ocirc;ng sẽ như thế n&agrave;o. &Ocirc;t&ocirc; đi th&igrave; bon chen, chỉ muốn nhăm nhăm đ&egrave; đầu xe kh&aacute;c để đi cho nhanh, về cho sớm, c&agrave;ng bon chen th&igrave; c&agrave;ng bị tắc, c&agrave;ng bị tắc th&igrave; l&uacute;c đấy nhanh cũng chả được v&agrave; khi đường đ&ocirc;ng th&igrave; dễ xảy ra va chạm v&agrave; rồi lại xuống xe đ&aacute;nh nhau, c&atilde;i nhau, tắc th&igrave; vẫn ho&agrave;n tắc, nhanh th&igrave; cũng chả nhanh được.</p>\r\n\r\n<p>Tr&ecirc;n đ&acirc;y chỉ l&agrave; những &yacute; kiến c&aacute; nh&acirc;n của t&ocirc;i, m&agrave; thực chất t&ocirc;i đ&atilde; được chứng kiến, được nh&igrave;n tận mắt nh&igrave;n v&agrave; cảm nhận thực tế. Thiết nghĩ cơ quan chức năng cần xem x&eacute;t lại c&aacute;ch thức cấp giấy ph&eacute;p l&aacute;i xe, thi phải giống như thi quốc gi&aacute; ấy, coi thi phải thật chặt v&agrave;o (kể cả thi l&yacute; thuyết + sa h&igrave;nh) để bắt người thi giấy ph&eacute;p l&aacute;i xe phải học h&agrave;nh đ&agrave;ng ho&agrave;ng, kh&ocirc;ng c&oacute; chuyện &quot;quen biết&quot; v&agrave; c&oacute; tiền l&agrave;&nbsp; c&oacute; thể lấy được giấy ph&eacute;p l&aacute;i xe đơn giản như thế được.</p>\r\n\r\n<p style=\"text-align:right\"><em>Độc giả</em>&nbsp;Qu&acirc;n Decor</p>\r\n\r\n<p style=\"text-align:right\"><em>Nguồn vnexpress.net</em></p>\r\n',NULL,NULL,'20',0,NULL,NULL),(21,'Thông báo: di dời phòng vé số 12 tại bến xe Miền Tây',0,NULL,'<p><img alt=\"\" src=\"https://thanhcongbus.vn/images/uploads/slider-thanh-cong.jpg\" style=\"height:353px; width:750px\" /></p>\r\n\r\n<h2><strong>C&ocirc;ng ty TNHH Vận Tải Th&agrave;nh C&ocirc;ng xin tr&acirc;n trọng th&ocirc;ng b&aacute;o !</strong></h2>\r\n\r\n<p>Ph&ograve;ng v&eacute; Số 12 tại bến xe Miền T&acirc;y từ ng&agrave;y 11/6 đến hết 26/7 sẽ được di dời về ph&iacute;a đối diện ph&ograve;ng v&eacute; cũ (b&atilde;i giữ xe nh&acirc;n vi&ecirc;n).</p>\r\n\r\n<div style=\"page-break-after: always\"><span style=\"display:none\">&nbsp;</span></div>\r\n\r\n<p>Nhằm mục đ&iacute;ch sửa chữa, n&acirc;ng cấp Bến xe Miền t&acirc;y để phục vụ h&agrave;nh kh&aacute;ch tốt hơn. Xin ch&acirc;n th&agrave;nh cảm ơn Qu&yacute; kh&aacute;ch !<br />\r\n<span style=\"font-family:inherit\">---------------<br />\r\n<strong>Tổng đ&agrave;i đặt v&eacute;</strong>: <span style=\"color:#B22222\"><strong>02713.60 78 88</strong></span><br />\r\n<strong>Địa chỉ:</strong> Quầy v&eacute; 12, 395 Kinh Dương Vương, Phường An Lạc, B&igrave;nh T&acirc;n, TP.HCM<br />\r\n---------------<br />\r\n<strong>TỔNG Đ&Agrave;I CSKH</strong>: <span style=\"color:#B22222\"><strong>0918 81 87 88</strong></span></span></p>\r\n',NULL,NULL,'21',0,NULL,NULL),(23,'Phát hiện 5 ổ dịch tả lợn châu Phi tại Bình Phước',0,NULL,'<div class=\"entry-summary\" style=\"box-sizing: border-box; margin: 0px; padding: 10px 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 700; font-stretch: inherit; font-size: 15.4px; line-height: inherit; font-family: Arial; vertical-align: baseline;\">Cơ quan quản l&yacute; huyện Đồng Ph&uacute;, tỉnh B&igrave;nh Phước đ&atilde; ti&ecirc;u hủy lợn dương t&iacute;nh với dịch tả lợn ch&acirc;u Phi tr&ecirc;n địa b&agrave;n\r\n<div style=\"page-break-after: always\"><span style=\"display:none\">&nbsp;</span></div>\r\n\r\n<p>&nbsp;</p>\r\n</div>\r\n\r\n<div class=\"single-entry-summary-post-content\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 14px; line-height: inherit; font-family: Arial; vertical-align: baseline;\">\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\">\r\n<p>&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; text-align: center;\"><img alt=\"(Ảnh minh họa: TTXVN)\" src=\"http://media.doanhnghiepvn.vn/Images/Uploaded/Share/2019/06/25/f3941.jpg\" style=\"border:0px; box-sizing:border-box; display:inline-block; font:inherit; height:auto; margin:5px; max-width:100%; padding:0px; vertical-align:baseline\" title=\"(Ảnh minh họa: TTXVN)\" />\r\n<p style=\"margin-left:0px; margin-right:0px\">(Ảnh minh họa: TTXVN)</p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px\"><strong>TIN T&Agrave;I TRỢ</strong></p>\r\n</div>\r\n\r\n<p>Việc ti&ecirc;u hủy tr&ecirc;n được thực hiện tại 3 x&atilde; T&acirc;n Lập, T&acirc;n H&ograve;a v&agrave; T&acirc;n Phước với 5 ổ dịch tả lợn ch&acirc;u Phi.</p>\r\n\r\n<p>Qua ghi nhận, tại hộ gia đ&igrave;nh Chu Văn Thuận c&oacute; 9 con với tổng trọng lượng 54kg; hộ Nguyễn Văn Tiến, đ&agrave;n 12 con với tổng trọng lượng 335kg; hộ Nguyễn Văn Trọng, 19 con, tổng trọng lượng 1.153kg (c&ugrave;ng ngụ ấp 9, x&atilde; T&acirc;n Lập); hộ Trương Thị Phương, ở ấp Phước T&acirc;n, x&atilde; T&acirc;n Phước, đ&agrave;n 52 con, tổng trọng lượng hơn 4,3 tấn v&agrave; hộ L&ecirc; Thanh Nh&acirc;n, ở ấp Đồng Chắc, x&atilde; T&acirc;n H&ograve;a, đ&agrave;n 43 con, tổng trọng lượng gần 4,5 tấn.</p>\r\n\r\n<p>Sau khi lấy mẫu đưa đi x&eacute;t nghiệm v&agrave; c&oacute; kết quả dương t&iacute;nh với virus bệnh dịch tả lợn ch&acirc;u Phi, lực lượng chức năng địa phương đ&atilde; tiến h&agrave;nh tổ chức ti&ecirc;u hủy bằng biện ph&aacute;p ch&ocirc;n lấp to&agrave;n bộ số lợn tại 5 hộ gia đ&igrave;nh tr&ecirc;n. T&iacute;nh đến ng&agrave;y 23/6, tr&ecirc;n to&agrave;n tỉnh B&igrave;nh Phước đ&atilde; c&oacute; 25 phường, x&atilde; thuộc 6 huyện, thị, th&agrave;nh phố xuất hiện ổ dịch tả lợn ch&acirc;u Phi.</p>\r\n</div>\r\n\r\n<div id=\"AdAsia\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\">&nbsp;</div>\r\n\r\n<div id=\"bs_mobileinpage\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\">&nbsp;</div>\r\n\r\n<div id=\"7308d345ef9bada970e616ae1838a672_sync\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\">&nbsp;</div>\r\n</div>\r\n\r\n<div class=\"text-right font-weight-bold\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 14px; line-height: inherit; font-family: Arial; vertical-align: baseline; font-weight: 700 !important; text-align: right !important;\">Theo vtv.vn</div>\r\n',NULL,NULL,'23',0,NULL,NULL),(24,'Va chạm môtô, 2 người thương vong',0,NULL,'<p style=\"text-align:justify\"><strong>BP - Khoảng 13 giờ 45 ph&uacute;t, tr&ecirc;n đường ĐT741 đoạn qua ấp Thuận H&ograve;a 2, x&atilde; Thuận Lợi, huyện Đồng Ph&uacute; xảy ra vụ tai nạn giao th&ocirc;ng nghi&ecirc;m trọng.</strong></p>\r\n\r\n<div style=\"page-break-after: always\"><span style=\"display:none\">&nbsp;</span></div>\r\n\r\n<p style=\"text-align:justify\">Người đ&agrave;n &ocirc;ng điều khiển xe m&ocirc;t&ocirc; kh&ocirc;ng đội mũ bảo hiểm, lưu th&ocirc;ng ngược chiều t&ocirc;ng trực diện v&agrave;o xe m&ocirc;t&ocirc; kh&aacute;c khiến 1 người chết, 1 người bị thương nặng.</p>\r\n\r\n<p><img alt=\"\" src=\"http://baobinhphuoc.com.vn/Content/UploadFiles/EditorFiles/images/2019/Quy3/tngt11072019085442.jpg\" style=\"background:transparent; border-radius:4px; border:0px none; box-shadow:rgb(51, 51, 51) 0px 0px 3px; box-sizing:inherit; cursor:-webkit-zoom-in; height:727px; margin:0px 0px 3px; max-width:100%; outline:0px; padding:0px; vertical-align:middle; width:1200px\" />Hiện trường vụ tai nạn</p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px\">Theo th&ocirc;ng tin ban đầu, v&agrave;o thời điểm n&ecirc;u tr&ecirc;n, anh Nguyễn Trung Hiếu (45 tuổi, ngụ x&atilde; Thuận Lợi, huyện Đồng Ph&uacute;) điều khiển xe m&aacute;y biển số 93P1-953.83, kh&ocirc;ng đội mũ bảo hiểm, lưu th&ocirc;ng ngược chiều tr&ecirc;n đường ĐT741, hướng từ thị x&atilde; Phước Long đi TP. Đồng Xo&agrave;i. Khi đến đoạn ấp Thuận H&ograve;a 2, x&atilde; Thuận Lợi, do kh&ocirc;ng ch&uacute; &yacute; quan s&aacute;t n&ecirc;n anh Hiếu điều khiển xe đ&atilde; t&ocirc;ng trực diện v&agrave;o xe m&ocirc;t&ocirc; biển số 93P1-311.96 do anh L&ecirc; Duy Phương (37 tuổi, ngụ x&atilde; Tiến Hưng, TP. Đồng Xo&agrave;i), điều khiển lưu th&ocirc;ng đ&uacute;ng chiều theo hướng ngược lại.</p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px\">C&uacute; t&ocirc;ng qu&aacute; mạnh khiến cả 2 anh Hiếu v&agrave; Phương bị văng ra khỏi xe, bất tỉnh. Anh Phương bị thương nặng phải đưa đi cấp cứu tại Bệnh viện đa khoa tỉnh. Ri&ecirc;ng anh Hiếu tử vong ngay sau đ&oacute;. 2 phương tiện đều bị hư hỏng nặng.</p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px\">Nhận tin b&aacute;o, lực lượng cảnh s&aacute;t giao th&ocirc;ng C&ocirc;ng an huyện Đồng Ph&uacute; đ&atilde; c&oacute; mặt kh&aacute;m nghiệm hiện trường, điều tra l&agrave;m r&otilde; nguy&ecirc;n nh&acirc;n vụ tai nạn.-</p>\r\n',NULL,NULL,'24',0,NULL,NULL),(25,'Phổ biến pháp luật cho công nhân, người lao động KCN Bắc Đồng Phú',0,NULL,'<div class=\"sapo\" style=\"box-sizing: inherit; margin: 0px 0px 1.28em; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Roboto, sans-serif, Arial; font-weight: 700; line-height: 1.58; color: rgb(33, 37, 41); text-align: justify;\">B&igrave;nh Phước&nbsp;- S&aacute;ng 14-7, Li&ecirc;n đo&agrave;n Lao động tỉnh phối hợp ng&agrave;nh c&ocirc;ng an v&agrave; C&ocirc;ng ty Điện lực B&igrave;nh Phước tổ chức chương tr&igrave;nh truyền th&ocirc;ng an to&agrave;n giao th&ocirc;ng, ph&ograve;ng chống ma t&uacute;y, bạo lực gia đ&igrave;nh v&agrave; tiết kiệm điện trong c&ocirc;ng nh&acirc;n lao động tại Khu c&ocirc;ng nghiệp (KCN) Bắc Đồng Ph&uacute;, huyện Đồng Ph&uacute;.\r\n<div style=\"page-break-after: always\"><span style=\"display:none\">&nbsp;</span></div>\r\n\r\n<p>&nbsp;</p>\r\n</div>\r\n\r\n<div><img src=\"http://baobinhphuoc.com.vn/Content/UploadFiles/EditorFiles/images/2019/Quy3/pb15072019083610.jpg\" style=\"background:transparent; border-radius:4px; border:0px none; box-shadow:rgb(51, 51, 51) 0px 0px 3px; box-sizing:inherit; color:rgb(35, 31, 32); cursor:-webkit-zoom-in; font-family:roboto,sans-serif,arial; font-size:14px; font-style:italic; height:auto !important; margin:0px 0px 3px; max-width:100%; outline:0px; padding:0px; text-align:center; vertical-align:middle; width:542.563px\" /></div>\r\n\r\n<h4><span style=\"font-size:14px\"><em>Lưc lượng c&ocirc;ng an ph&aacute;t tờ rơi tuy&ecirc;n truyền về an to&agrave;n giao th&ocirc;ng, ph&ograve;ng chống ma t&uacute;y tại buổi lễ</em></span></h4>\r\n\r\n<p><span style=\"background-color:transparent; color:rgb(35, 31, 32); font-family:roboto,sans-serif,arial; font-size:16px\">Chương tr&igrave;nh thu h&uacute;t khoảng 100 đo&agrave;n vi&ecirc;n, c&ocirc;ng nh&acirc;n, người lao động l&agrave;m việc trong c&aacute;c doanh nghiệp tại KCN Bắc Đồng Ph&uacute; v&agrave; chủ nh&agrave; trọ đang sinh sống, kinh doanh tr&ecirc;n địa b&agrave;n tham dự. Tại buổi tuy&ecirc;n truyền, l&atilde;nh đạo Đồn c&ocirc;ng an KCN Bắc Đồng Ph&uacute; đ&atilde; n&oacute;i chuyện chuy&ecirc;n đề về &ldquo;An to&agrave;n giao th&ocirc;ng, ph&ograve;ng chống ma t&uacute;y, bạo lực gia đ&igrave;nh&rdquo;. Đồng thời, tổ chức c&aacute;c tr&ograve; chơi, đố vui c&oacute; thưởng li&ecirc;n quan kiến thức ph&aacute;p luật được tuy&ecirc;n truyền tại buổi lễ.</span></p>\r\n\r\n<p style=\"text-align:justify\">Cũng trong chương tr&igrave;nh, Điện lực Đồng Ph&uacute; tuy&ecirc;n truyền c&aacute;c nội dung về&nbsp;sử dụng&nbsp;an to&agrave;n, tiết kiệm điện, sử dụng năng lượng t&aacute;i tạo, năng lượng mặt trời &aacute;p m&aacute;i v&agrave; Chỉ thị số 02 của Ch&iacute;nh phủ về thanh to&aacute;n tiền điện kh&ocirc;ng d&ugrave;ng tiền mặt. Ng&agrave;nh điện c&ograve;n trả lời v&agrave; tư vấn trực tiếp những vấn đề li&ecirc;n quan đến lĩnh vực an to&agrave;n điện m&agrave; người tham dự quan t&acirc;m.</p>\r\n\r\n<p style=\"text-align:justify\">Với những nội dung của chương tr&igrave;nh đ&atilde; g&oacute;p phần n&acirc;ng cao hiểu biết v&agrave; &yacute; thức chấp h&agrave;nh ph&aacute;p luật cho đo&agrave;n vi&ecirc;n, c&ocirc;ng nh&acirc;n, người lao động, chủ nh&agrave; trọ tr&ecirc;n địa b&agrave;n. Qua đ&oacute;, ban tổ chức mong những người tham gia sẽ tuy&ecirc;n truyền rộng r&atilde;i trong cộng đồng d&acirc;n cư c&ugrave;ng c&ocirc;ng nh&acirc;n, người lao động hiểu v&agrave; thực hiện &ldquo;Sống v&agrave; l&agrave;m việc theo hiến ph&aacute;p, ph&aacute;p luật&rdquo;.</p>\r\n\r\n<p style=\"text-align:right\">Thanh Mảng</p>\r\n',NULL,NULL,'25',0,NULL,NULL),(26,'Sạt lở tại tuyến đường huyết mạch Bình Phước - Lâm Đồng',0,NULL,'<div class=\"post-summary\" style=\"box-sizing: border-box; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline; font-family: Roboto; text-align: justify;\">\r\n<div><strong>(ĐCSVN) - Tại tỉnh B&igrave;nh Phước, mưa k&eacute;o d&agrave;i li&ecirc;n tục trong nhiều ng&agrave;y qua kh&ocirc;ng chỉ g&acirc;y ảnh hưởng đến đời sống của b&agrave; con trong khu vực m&agrave; c&ograve;n khiến nhiều c&ocirc;ng tr&igrave;nh cầu, đường bị thiệt hại nghi&ecirc;m trọng.</strong></div>\r\n\r\n<div style=\"page-break-after: always\"><span style=\"display:none\">&nbsp;</span></div>\r\n\r\n<p>&nbsp;</p>\r\n</div>\r\n\r\n<div class=\"post-content\" style=\"box-sizing: border-box; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline; font-family: Roboto; text-align: justify; font-size: 15px !important; font-variant-numeric: normal !important; font-variant-east-asian: normal !important; font-stretch: normal !important; line-height: 25px !important;\">\r\n<div style=\"box-sizing: border-box; background: transparent; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline; letter-spacing: 0px; font-size: 13px; text-align: center;\"><img alt=\"\" class=\"img-responsive\" src=\"http://cpv.org.vn/DATA/0/2019/08/1a-15_35_40_077.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:auto; letter-spacing:0px; margin:0px; max-width:100%; padding:0px; vertical-align:baseline; width:748px\" /></div>\r\n\r\n<div style=\"box-sizing: border-box; background: transparent; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline; letter-spacing: 0px; font-size: 13px; text-align: center;\"><span style=\"color:rgb(0, 176, 240); font-size:15px !important\">Tuyến đường nối B&igrave;nh Phước &ndash; L&acirc;m Đồng sạt lở g&acirc;y nguy hiểm cho người tham gia giao th&ocirc;ng.</span></div>\r\n\r\n<div style=\"box-sizing: border-box; background: transparent; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline; letter-spacing: 0px; font-size: 13px; text-align: center;\"><span style=\"color:rgb(0, 176, 240); font-size:15px !important\">Ảnh: Đậu Tất Th&agrave;nh</span></div>\r\n\r\n<p>Tr&ecirc;n tuyến Sao Bọng &ndash; Đăng H&agrave;, tuyến đường huyết mạch nối liền B&igrave;nh Phước với L&acirc;m Đồng, mưa lớn cũng đ&atilde; g&acirc;y ra t&igrave;nh trạng sạt lở đất. Giao th&ocirc;ng qua khu vực n&agrave;y hiện đang gặp rất nhiều kh&oacute; khăn.</p>\r\n\r\n<p>Địa điểm xảy ra sạt lở thuộc đoạn qua khu vực đồi dốc Đỏ, th&ocirc;n 5, x&atilde; Đăng H&agrave;. Người d&acirc;n địa phương cho biết, đồi dốc đỏ l&agrave; điểm c&oacute; mạch nước ngầm. Theo đ&oacute;, mưa lớn đ&atilde; l&agrave;m ph&aacute; vỡ một phần kết cấu bề mặt đất tr&ecirc;n đỉnh đồi g&acirc;y sạt lở khiến đất đ&aacute; từ tr&ecirc;n đỉnh đồi rơi xuống, tr&agrave;n ra gần hết phần mặt đường. Rất may, tại thời điểm xảy ra sạt lở, đoạn đường tr&ecirc;n kh&ocirc;ng c&oacute; phương tiện lưu th&ocirc;ng n&ecirc;n kh&ocirc;ng g&acirc;y thiệt hại về người về t&agrave;i sản.</p>\r\n\r\n<p>Theo b&aacute;o c&aacute;o của Ban chỉ huy ph&ograve;ng, chống lụt b&atilde;o v&agrave; t&igrave;m kiếm cứu hộ, cứu nạn huyện B&ugrave; Đăng, tỉnh B&igrave;nh Phước, t&iacute;nh đến 11 giờ ng&agrave;y 11/8, c&oacute; 3 người bị nước lũ c&ocirc; lập, trong đ&oacute; 1 người tại th&ocirc;n Sơn T&acirc;n, x&atilde; Ph&uacute; Sơn v&agrave; 2 người tại th&ocirc;n 1, x&atilde; Đo&agrave;n Kết đ&atilde; được cứu hộ an to&agrave;n. C&oacute; 4 căn nh&agrave; cấp 4 bị ngập ho&agrave;n to&agrave;n; 7 ch&ograve;i rẫy của người d&acirc;n bị nước cuốn tr&ocirc;i; 3 căn nh&agrave; bị sạt lở nghi&ecirc;m trọng; 2 căn nh&agrave; bị tốc m&aacute;i; 100 ha điều, c&agrave; ph&ecirc; v&agrave; c&acirc;y ăn tr&aacute;i bị ngập &uacute;ng; 1 con tr&acirc;u bị nước lũ cuốn tr&ocirc;i (chết), 1,5 ha ao c&aacute; bị cuốn tr&ocirc;i...</p>\r\n\r\n<p>Lũ cũng cuốn tr&ocirc;i 2 c&acirc;y cầu treo d&acirc;n sinh bắc qua s&ocirc;ng Lấp, tr&ecirc;n địa b&agrave;n 2 x&atilde; Ph&uacute; Sơn, Thọ Sơn; 1 cầu treo thuộc dự &aacute;n LRAMD tại x&atilde; Ph&uacute; Sơn do th&agrave;nh phố Hồ Ch&iacute; Minh x&acirc;y tặng, trị gi&aacute; 2,1 tỷ đồng; 1 cầu d&acirc;n tự l&agrave;m tại x&atilde; Thọ Sơn, trị gi&aacute; 262 triệu đồng... Ước tổng thiệt hại do mưa lũ g&acirc;y ra hơn 7,5 tỷ đồng./.</p>\r\n\r\n<p style=\"text-align:right\"><strong><span style=\"font-size:14px\">Mai Phan</span></strong></p>\r\n\r\n<div>&nbsp;</div>\r\n</div>\r\n',NULL,NULL,'26',0,NULL,NULL),(27,'Mức xử phạt vi phạm an toàn giao thông chưa đủ sức răn đe',0,NULL,'<div style=\"text-align: justify;\"><strong>BPO - Ng&agrave;y 22-7, tại trụ sở Ch&iacute;nh phủ, Thủ tướng Nguyễn Xu&acirc;n Ph&uacute;c đ&atilde; chủ tr&igrave; Hội nghị trực tuyến to&agrave;n quốc sơ kết c&ocirc;ng t&aacute;c bảo đảm trật tự an to&agrave;n giao th&ocirc;ng (ATGT) 6 th&aacute;ng đầu năm v&agrave; triển khai kế hoạch 6 th&aacute;ng cuối năm 2019.</strong></div>\r\n\r\n<div style=\"page-break-after: always\"><span style=\"display:none\">&nbsp;</span></div>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:justify\">Theo b&aacute;o c&aacute;o của Ủy ban ATGT Quốc gia, 6 th&aacute;ng đầu năm tai nạn giao th&ocirc;ng giảm cả 3 ti&ecirc;u ch&iacute;, giảm s&acirc;u nhất trong nhiều năm. To&agrave;n quốc xảy ra 8.385 vụ tai nạn giao th&ocirc;ng (giảm hơn 7%), l&agrave;m chết 3.810 người (giảm hơn 7,5%), bị thương 6.358 người (giảm hơn 9,6%) so với c&ugrave;ng kỳ năm 2018.</p>\r\n\r\n<p style=\"text-align:center !important\"><img alt=\"Thủ tướng Nguyễn Xuân Phúc phát biểu tại hội nghị\" src=\"http://baobinhphuoc.com.vn/Content/UploadFiles/EditorFiles/images/2019/Quy3/3423072019024929.jpg\" style=\"background:transparent; border-radius:4px; border:0px none; box-shadow:rgb(51, 51, 51) 0px 0px 3px; box-sizing:inherit; cursor:-webkit-zoom-in; height:377px; margin:0px 0px 3px; max-width:100%; outline:0px; padding:0px; vertical-align:middle; width:575px\" />Thủ tướng Nguyễn Xu&acirc;n Ph&uacute;c ph&aacute;t biểu tại hội nghị</p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:justify\">Ph&aacute;t biểu kết luận hội nghị, Thủ tướng Nguyễn Xu&acirc;n Ph&uacute;c đ&aacute;nh gi&aacute;, Ủy ban ATGT Quốc gia, c&aacute;c bộ, cơ quan, địa phương c&ugrave;ng sự v&agrave;o cuộc của cả hệ thống ch&iacute;nh trị với nhiều h&igrave;nh thức v&agrave; c&aacute;ch l&agrave;m tốt đ&atilde; g&oacute;p phần cải thiện t&igrave;nh h&igrave;nh ATGT. Thủ tướng cũng nhấn mạnh vấn nạn &ugrave;n tắc giao th&ocirc;ng ở hai th&agrave;nh phố lớn l&agrave; TP H&agrave; Nội v&agrave; TP Hồ Ch&iacute; Minh. C&ugrave;ng với đ&oacute;, một số th&agrave;nh phố kh&aacute;c xuất hiện &ugrave;n tắc cục bộ. Nhiều vụ xe kh&aacute;ch, xe tải g&acirc;y tai nạn nghi&ecirc;m trọng vẫn xảy ra. Xuất hiện những &ldquo;điểm đen&rdquo; từ ch&iacute;nh những cơ quan c&oacute; chức năng trong c&aacute;c kh&acirc;u đ&agrave;o tạo, kiểm tra. Thủ tướng Nguyễn Xu&acirc;n Ph&uacute;c cũng ph&acirc;n t&iacute;ch những điểm chưa ph&ugrave; hợp về thể chế trong c&ocirc;ng t&aacute;c bảo đảm ATGT kh&ocirc;ng chỉ đường bộ m&agrave; c&ograve;n đường sắt, đường thủy, h&agrave;ng kh&ocirc;ng, nhất l&agrave; vấn đề an to&agrave;n bay.&nbsp;</p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:justify\">Chỉ đạo một số nội dung lớn về c&ocirc;ng t&aacute;c bảo đảm trật tự ATGT, Thủ tướng y&ecirc;u cầu Bộ Giao th&ocirc;ng vận tải khẩn trương đề xuất để tiến tới sửa đổi Luật Giao th&ocirc;ng đường bộ năm 2018, bởi đ&acirc;y l&agrave; khung ph&aacute;p luật quan trọng nhất để xử l&yacute; c&aacute;c hoạt động li&ecirc;n quan. Bộ Tư ph&aacute;p khẩn trương đề xuất x&acirc;y dựng Luật Xử l&yacute; vi phạm h&agrave;nh ch&iacute;nh trong tham gia giao th&ocirc;ng theo hướng tăng mạnh khung xử phạt.&nbsp;Hiện nay, quy định xử phạt vi phạm trật tự ATGT mức phạt đ&atilde; cao nhưng chưa đủ sức răn đe.&nbsp;Thủ tướng cũng chỉ đạo c&aacute;c bộ, ng&agrave;nh li&ecirc;n quan b&aacute;o c&aacute;o lại Thủ tướng về việc x&acirc;y dựng Nghị định thay thế Nghị định 86/2014/NĐ-CP&nbsp;về&nbsp;kinh doanh v&agrave;&nbsp;điều kiện kinh doanh vận tải&nbsp;bằng &ocirc; t&ocirc;. Theo đ&oacute;, Thủ tướng y&ecirc;u cầu lấy &yacute; kiến th&agrave;nh vi&ecirc;n Ch&iacute;nh phủ; loại bỏ ngay c&aacute;c điều kiện kh&ocirc;ng cần thiết, đẩy mạnh ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin v&agrave;o quản l&yacute; vận tải thay cho phương thức quản l&yacute; truyền thống.&nbsp;Đồng thời, cần sớm ban h&agrave;nh văn bản hướng dẫn Luật Ph&ograve;ng chống t&aacute;c hại của rượu, bia m&agrave; Quốc hội đ&atilde; th&ocirc;ng qua.&nbsp;&nbsp;</p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:justify\">Đặc biệt, Thủ tướng y&ecirc;u cầu n&acirc;ng cao hiệu quả c&ocirc;ng t&aacute;c tuy&ecirc;n truyền về ATGT trật tự đ&ocirc; thị theo hướng &ldquo;đ&uacute;ng người, đ&uacute;ng thời điểm, đặc biệt l&agrave; giới trẻ&rdquo;; đưa v&agrave;o sinh hoạt văn h&oacute;a địa phương lối sống, &yacute; thức ATGT.&nbsp;B&ecirc;n cạnh đ&oacute;, cần n&acirc;ng cao chất lượng quản l&yacute; Nh&agrave; nước về trật tự ATGT; tập trung n&acirc;ng cao năng lực lực lượng tuần tra kiểm so&aacute;t tr&ecirc;n tinh thần nghi&ecirc;m minh, li&ecirc;m ch&iacute;nh, th&acirc;n thiện; đẩy mạnh c&ocirc;ng nghệ để gi&aacute;m s&aacute;t xử phạt, n&acirc;ng cao hiệu quả chống ti&ecirc;u cực tham nhũng; n&acirc;ng cao chất lượng hệ thống cơ sở hạ tầng giao th&ocirc;ng;...&nbsp;</p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:justify\">Giao nhiệm vụ cụ thể cho Ủy ban ATGT Quốc gia, Thủ tướng y&ecirc;u cầu, khi cấp lại giấy ph&eacute;p l&aacute;i xe cần r&agrave; so&aacute;t những h&agrave;nh vi trước đ&oacute; một c&aacute;ch c&ocirc;ng khai; đ&oacute;ng cửa c&aacute;c cơ sở đăng kiểm s&aacute;t hạch kh&ocirc;ng đạt y&ecirc;u cầu, nhất l&agrave; c&aacute;c cơ sở c&oacute; ti&ecirc;u cực.&nbsp;Thủ tướng cũng đề cao tinh thần Cảnh s&aacute;t giao th&ocirc;ng kh&ocirc;ng nhận hối lộ, bao che sai phạm, hay c&aacute;c lĩnh vực li&ecirc;n quan đến ATGT v&agrave; n&oacute;i kh&ocirc;ng với ti&ecirc;u cực lợi &iacute;ch nh&oacute;m; chấn chỉnh những c&aacute; nh&acirc;n c&oacute; h&agrave;nh vi sai tr&aacute;i. C&aacute;c cơ quan tố tụng cần xử l&yacute; nghi&ecirc;m c&aacute;c vụ vi phạm trật tự ATGT dưới sự gi&aacute;m s&aacute;t của quần ch&uacute;ng.&nbsp;Tr&ecirc;n tinh thần đ&oacute;, Thủ tướng Nguyễn Xu&acirc;n Ph&uacute;c y&ecirc;u cầu ủy ban ATGT c&aacute;c cấp n&acirc;ng cao tr&aacute;ch nhiệm, tr&igrave;nh độ trong t&igrave;nh h&igrave;nh mới để n&acirc;ng cao hiệu quả bảo vệ t&iacute;nh mạng, t&agrave;i sản của người d&acirc;n.</p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:right\"><strong>Nguồn TTXVN</strong></p>\r\n',NULL,NULL,'27',0,NULL,NULL),(28,'Thay đổi hướng tuyến đường Phú Riềng Đỏ nối dài có hợp lý?',0,NULL,'<div style=\"text-align: justify;\">&Ocirc;ng Nguyễn Quang Qu&yacute;, tr&uacute; khu phố T&acirc;n An, thị trấn T&acirc;n Ph&uacute; (Đồng Ph&uacute;) vừa c&oacute; đơn kiến nghị, phản &aacute;nh về việc dự &aacute;n x&acirc;y dựng đường Ph&uacute; Riềng Đỏ nối d&agrave;i (kết nối thị trấn T&acirc;n Ph&uacute; với đường tổ, x&atilde; T&acirc;n Tiến, huyện Đồng Ph&uacute;) bị thay đổi hướng tuyến, kh&ocirc;ng theo quy hoạch được duyệt v&agrave; g&acirc;y l&atilde;ng ph&iacute; ng&acirc;n s&aacute;ch, v&igrave; tuyến mới kh&ocirc;ng đấu nối v&agrave;o điểm cuối đường từ x&atilde; T&acirc;n Tiến đi l&ecirc;n...\r\n<div style=\"page-break-after: always\"><span style=\"display:none\">&nbsp;</span></div>\r\n\r\n<p>&nbsp;</p>\r\n</div>\r\n\r\n<div style=\"text-align: justify;\">Trao đổi với ph&oacute;ng vi&ecirc;n, &ocirc;ng Nguyễn Thanh T&ugrave;ng, Trưởng ph&ograve;ng Kinh tế - Hạ tầng huyện Đồng Ph&uacute; cho biết, năm 2017, Huyện ủy, UBND huyện Đồng Ph&uacute; thuận chủ trương đầu tư x&acirc;y mới tuyến đường Ph&uacute; Riềng Đỏ nối d&agrave;i. Dự &aacute;n được thực hiện tr&ecirc;n cơ sở vận động người d&acirc;n hiến đất, c&acirc;y trồng, vật kiến tr&uacute;c. Nh&agrave; nước chỉ đền b&ugrave; những hộ bị giải tỏa hết đất, kh&ocirc;ng c&ograve;n được hưởng lợi trực tiếp từ tuyến đường v&agrave; những hộ được hưởng lợi &iacute;t. Từ chủ trương đ&oacute;, UBND huyện giao Ph&ograve;ng Kinh tế - Hạ tầng chủ tr&igrave; phối hợp UBND thị trấn T&acirc;n Ph&uacute;, x&atilde; T&acirc;n Tiến v&agrave; c&aacute;c cơ quan li&ecirc;n quan họp d&acirc;n, khảo s&aacute;t hướng tuyến, quy m&ocirc;...</div>\r\n\r\n<p style=\"text-align:center !important\"><img src=\"http://baobinhphuoc.com.vn/Content/UploadFiles/EditorFiles/images/2019/Quy3/513082019030006.jpg\" style=\"background:transparent; border-radius:4px; border:0px none; box-shadow:rgb(51, 51, 51) 0px 0px 3px; box-sizing:inherit; cursor:-webkit-zoom-in; height:auto !important; margin:0px 0px 3px; max-width:100%; outline:0px; padding:0px; vertical-align:middle; width:542.563px\" />Tuyến đường Ph&uacute; Riềng Đỏ nối d&agrave;i (tr&aacute;i) vừa được giải ph&oacute;ng mặt bằng, kết nối thị trấn T&acirc;n Ph&uacute; với đường tổ ấp Th&aacute;i Dũng, x&atilde; T&acirc;n Tiến (Đồng Ph&uacute;)</p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:justify\">Tuyến đường dự kiến x&acirc;y dựng d&agrave;i 681,3m, rộng 10m, đi qua 14 thửa đất của 14 hộ d&acirc;n, đấu nối v&agrave;o đường tổ ấp Th&aacute;i Dũng, x&atilde; T&acirc;n Tiến. Sau một thời gian tuy&ecirc;n truyền, vận động, c&oacute; 7/14 hộ d&acirc;n đồng &yacute; hiến đất, c&acirc;y trồng với tổng diện t&iacute;ch 6.443m<span style=\"font-size:12px\">2</span>, trị gi&aacute; 3,389 tỷ đồng. Trong 7 hộ c&ograve;n lại, c&oacute; 4 hộ bị mất hết đất, nh&agrave; ở v&agrave; 3 hộ cuối tuyến kh&ocirc;ng đồng &yacute;. V&igrave; vậy, Ph&ograve;ng Kinh tế - Hạ tầng buộc phải điều chỉnh tuyến đường đi qua đất của hộ &ocirc;ng Mai Đức Nhượng ở thị trấn T&acirc;n Ph&uacute; để đấu nối v&agrave;o điểm cuối đoạn đường nhựa từ x&atilde; T&acirc;n Tiến đi l&ecirc;n. Do 2 điểm đầu v&agrave; cuối lệch nhau n&ecirc;n tuyến đường c&oacute; một kh&uacute;c cua gấp đoạn qua đất của hộ &ocirc;ng Nhượng. Gia đ&igrave;nh &ocirc;ng Nhượng đề nghị đền b&ugrave; 551,5m<span style=\"font-size:12px\">2</span>&nbsp;đất, trị gi&aacute; hơn 263 triệu đồng. Tr&ecirc;n cơ sở thống nhất của c&aacute;c hộ d&acirc;n, UBND huyện đ&atilde; bổ sung quy hoạch v&agrave; được UBND tỉnh ph&ecirc; duyệt. Th&aacute;ng 4-2019, c&ocirc;ng tr&igrave;nh khởi c&ocirc;ng x&acirc;y dựng. Tuy nhi&ecirc;n, khi tuyến đường san ủi đến đất của hộ &ocirc;ng Mai Đức Nhượng th&igrave; 3 hộ cuối tuyến gồm: L&ecirc; C&ocirc;ng Trầm, Nguyễn Thị H&agrave; v&agrave; L&ecirc; C&ocirc;ng T&aacute;m c&ugrave;ng ở ấp Th&aacute;i Dũng, x&atilde; T&acirc;n Tiến lại đồng &yacute; để tuyến đường đi thẳng ngang qua đất của gia đ&igrave;nh m&igrave;nh. Tr&ecirc;n cơ sở đơn tự nguyện hiến đất của c&aacute;c hộ d&acirc;n, gia đ&igrave;nh &ocirc;ng Mai Đức Nhượng cũng xin hiến đất v&agrave; trả lại 263 triệu đồng đ&atilde; nhận đền b&ugrave; trước đ&oacute; để tuyến đường được đi thẳng. Sau khi được sự đồng thuận của c&aacute;c hộ d&acirc;n, UBND huyện đ&atilde; họp, thống nhất chủ trương thay đổi hướng tuyến nhằm đảm bảo an to&agrave;n giao th&ocirc;ng, tạo mỹ quan tuyến đường v&agrave; c&oacute; lợi cho ng&acirc;n s&aacute;ch nh&agrave; nước do gia đ&igrave;nh &ocirc;ng Nhượng trả lại 263 triệu đồng đ&atilde; nhận trước đ&oacute;.</p>\r\n\r\n<p style=\"text-align:justify\">&Ocirc;ng Nguyễn Quang Qu&yacute; cho rằng, huyện vội san ủi, giải ph&oacute;ng mặt bằng khi chưa họp d&acirc;n, chưa c&ocirc;ng khai bản đồ quy hoạch l&agrave; kh&ocirc;ng đ&uacute;ng quy tr&igrave;nh v&agrave; nếu thi c&ocirc;ng thẳng, kh&ocirc;ng đấu nối v&agrave;o điểm cuối tuyến đường từ x&atilde; T&acirc;n Tiến đi l&ecirc;n sẽ g&acirc;y l&atilde;ng ph&iacute; một đoạn đường nhựa... Trưởng ph&ograve;ng Kinh tế - Hạ tầng Nguyễn Thanh T&ugrave;ng cho biết th&ecirc;m, đến nay, huyện vẫn chưa thi c&ocirc;ng theo hướng tuyến mới. Việc san ủi, giải ph&oacute;ng mặt bằng đoạn qua 3 hộ d&acirc;n cuối tuyến được triển khai, thực hiện ngay l&agrave; do c&aacute;c hộ n&agrave;y sau khi cắt c&acirc;y thanh l&yacute;, đ&atilde; nhờ đơn vị thi c&ocirc;ng san ủi lu&ocirc;n. Hơn nữa, việc người d&acirc;n đồng thuận hiến đất, c&acirc;y trồng, vật kiến tr&uacute;c đến đ&acirc;u, giải ph&oacute;ng mặt bằng ngay đến đ&oacute; l&agrave; chủ trương chung nhằm tạo thuận lợi cho c&ocirc;ng t&aacute;c đầu tư sau n&agrave;y. Hiện nay, ph&ograve;ng đ&atilde; ho&agrave;n thiện bản vẽ, chuẩn bị họp d&acirc;n xin &yacute; kiến, ho&agrave;n tất thủ tục điều chỉnh cục bộ quy hoạch n&ocirc;ng th&ocirc;n mới x&atilde; T&acirc;n Tiến v&agrave; quy hoạch chung huyện lỵ Đồng Ph&uacute; l&agrave;m cơ sở điều chỉnh dự &aacute;n c&ocirc;ng tr&igrave;nh v&agrave; triển khai thi c&ocirc;ng. Đối với đoạn cuối tuyến đường tổ từ x&atilde; T&acirc;n Tiến đi l&ecirc;n d&agrave;i khoảng 120m vẫn giữ nguy&ecirc;n hiện trạng, đảm bảo lưu th&ocirc;ng cho c&aacute;c hộ d&acirc;n c&oacute; đất 2 b&ecirc;n đường n&ecirc;n kh&ocirc;ng g&acirc;y l&atilde;ng ph&iacute;.</p>\r\n\r\n<p style=\"text-align:justify\">Cũng theo đơn, &ocirc;ng Nguyễn Quang Qu&yacute; cho rằng, việc điều chỉnh hướng tuyến c&oacute; sự thao t&uacute;ng về quyền lực, c&oacute; li&ecirc;n quan đến lợi &iacute;ch c&aacute; nh&acirc;n của &ocirc;ng Nguyễn Văn Minh, Trưởng ban Tổ chức Huyện ủy. Tuy nhi&ecirc;n, Trưởng ph&ograve;ng Kinh tế - Hạ tầng huyện cho biết, qua r&agrave; so&aacute;t giấy chứng nhận quyền sử dụng đất của 14 hộ d&acirc;n c&oacute; li&ecirc;n quan đến tuyến đường th&igrave; kh&ocirc;ng c&oacute; hộ d&acirc;n n&agrave;o li&ecirc;n quan đến &ocirc;ng Nguyễn Văn Minh.</p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:justify\">Dự &aacute;n đường Ph&uacute; Riềng Đỏ nối d&agrave;i kh&ocirc;ng chỉ kết nối thị trấn T&acirc;n Ph&uacute; với x&atilde; T&acirc;n Tiến m&agrave; c&ograve;n l&agrave; động lực ph&aacute;t triển kinh tế, x&atilde; hội tr&ecirc;n địa b&agrave;n. Ngo&agrave;i ra, khi ho&agrave;n th&agrave;nh tuyến đường n&agrave;y sẽ th&ocirc;ng đến Khu c&ocirc;ng nghiệp Bắc Đồng Ph&uacute; gi&uacute;p giảm lưu lượng phương tiện tham gia giao th&ocirc;ng tr&ecirc;n tuyến ĐT741. Thay đổi hướng tuyến, bỏ được đoạn đường cong theo thiết kế đ&atilde; được duyệt g&oacute;p phần đảm bảo an to&agrave;n giao th&ocirc;ng v&agrave; mỹ quan tuyến đường l&agrave; việc cần l&agrave;m v&agrave; đ&atilde; được quy định tại Điểm b, Khoản 1, Điều 61 Luật X&acirc;y dựng năm 2014. Tuy nhi&ecirc;n, từ thực tế cho thấy, để thực hiện c&aacute;c dự &aacute;n giao th&ocirc;ng thuận lợi, c&aacute;c cấp ch&iacute;nh quyền, ban, ng&agrave;nh, đo&agrave;n thể cần l&agrave;m tốt c&ocirc;ng t&aacute;c tuy&ecirc;n truyền, vận động; ng&agrave;nh chức năng thực hiện kịp thời, đồng bộ c&aacute;c quy định, đảm bảo c&ocirc;ng khai, d&acirc;n chủ, tr&aacute;nh g&acirc;y dư luận xấu trong nh&acirc;n d&acirc;n.</p>\r\n\r\n<p style=\"text-align:right\"><strong>Minh Luận</strong></p>\r\n',NULL,NULL,'28',0,NULL,NULL),(31,'Lũ bất ngờ ở Bình Phước gây ngập 189 căn nhà',0,NULL,'<div><strong>TTO - Lũ đột ngột đổ về khiến người d&acirc;n ở TP Đồng Xo&agrave;i v&agrave; huyện Đồng Ph&uacute; (tỉnh B&igrave;nh Phước) kh&ocirc;ng kịp trở tay. H&agrave;ng trăm căn nh&agrave; bị ngập nặng khiến người d&acirc;n phải sơ t&aacute;n, g&acirc;y thiệt hại hơn 6 tỉ đồng.</strong></div>\r\n\r\n<div style=\"page-break-after: always\"><span style=\"display:none\">&nbsp;</span></div>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"margin: 1.3em auto 0px; padding: 0px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 11px; line-height: 1.45; font-family: Roboto; vertical-align: baseline; text-align: center; display: table; position: relative; z-index: 2; color: rgb(34, 34, 34); letter-spacing: -0.2px; background-color: rgba(255, 255, 255, 0.95);\">\r\n<div style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\"><a class=\"detail-img-lightbox\" href=\"https://cdn.tuoitre.vn/thumb_w/586/2019/10/5/1-1570279337847783986653.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; display: initial; text-decoration-line: none; cursor: zoom-in; color: rgb(237, 27, 47); letter-spacing: -0.2px;\" target=\"_blank\" title=\"Lực lượng chức năng sơ tán người dân đến nơi an toàn - Ảnh: B.L\"><img alt=\"Lũ bất ngờ ở Bình Phước gây ngập 189 căn nhà - Ảnh 1.\" class=\"lightbox-content\" id=\"img_82e99b30-e76d-11e9-8729-add39314a455\" src=\"https://cdn.tuoitre.vn/thumb_w/586/2019/10/5/1-1570279337847783986653.jpg\" style=\"border-style:initial; border-width:0px; color:transparent; font:inherit; margin:0px; max-width:100%; padding:0px; vertical-align:baseline; width:586px\" title=\"Lũ bất ngờ ở Bình Phước gây ngập 189 căn nhà - Ảnh 1.\" /></a>\r\n\r\n<div id=\"wrapper-inimage-pos-0\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\">\r\n<div id=\"zone-joqxux31\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\">\r\n<div id=\"share-joqxuxkg\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div class=\"PhotoCMS_Caption ck_legend caption\" style=\"margin: -5px 0px 24px; padding: 5px 8px; border: 0px; font: inherit; vertical-align: baseline; background: rgb(233, 233, 233); text-align: left;\">\r\n<p style=\"margin-left:0px; margin-right:0px\">Lực lượng chức năng sơ t&aacute;n người d&acirc;n đến nơi an to&agrave;n - Ảnh: B.L</p>\r\n</div>\r\n</div>\r\n\r\n<p>Tối 5-10, &ocirc;ng Nguyễn Văn Quyết - ch&aacute;nh văn ph&ograve;ng Ban chỉ huy PCTT v&agrave; TKCN tỉnh&nbsp;<a href=\"https://tuoitre.vn/binh-phuoc.html\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; display: initial; text-decoration-line: none; color: rgb(237, 27, 47); letter-spacing: -0.2px;\" target=\"_blank\" title=\"Bình Phước\">B&igrave;nh Phước</a>&nbsp;- cho hay: s&aacute;ng c&ugrave;ng ng&agrave;y, c&aacute;c lực lượng chức năng của tỉnh đ&atilde; phải sơ t&aacute;n khẩn cấp h&agrave;ng trăm người d&acirc;n thuộc c&aacute;c phường T&acirc;n Đồng v&agrave; T&acirc;n Thiện (TP Đồng Xo&agrave;i), x&atilde; Đồng Tiến v&agrave; T&acirc;n Phước (huyện Đồng Ph&uacute;) đến nơi an to&agrave;n, v&igrave; nước lũ từ thượng nguồn đột ngột đổ về d&acirc;ng cao.</p>\r\n\r\n<p>Trước đ&oacute;, do mưa lớn li&ecirc;n tục k&eacute;o d&agrave;i nhiều ng&agrave;y n&ecirc;n lũ từ thượng nguồn suối Rạt (x&atilde; B&ugrave; Nho, huyện Ph&uacute; Riềng) đổ về đ&atilde; g&acirc;y ngập s&acirc;u ở một số nơi tại TP Đồng Xo&agrave;i, trong đ&oacute; khu vực ngập s&acirc;u nhất l&agrave; T&acirc;n Thiện l&ecirc;n đến 3m.</p>\r\n\r\n<p>Theo thống kế sơ bộ, lũ đ&atilde; g&acirc;y ngập nặng 189 căn nh&agrave;, trong đ&oacute; TP Đồng Xo&agrave;i 137 căn, huyện Đồng Ph&uacute; 52 căn, c&ugrave;ng h&agrave;ng trăm ha c&acirc;y trồng, nhiều gia s&uacute;c gia cầm bị cuốn tr&ocirc;i. Tổng thiệt hại&nbsp;ban đầu ước t&iacute;nh hơn 6 tỉ đồng.</p>\r\n\r\n<p>&quot;Đến chiều tối nay, lũ đ&atilde; r&uacute;t dần nhưng t&igrave;nh h&igrave;nh vẫn diễn biến kh&aacute; phức tạp, mưa vẫn tiếp tục diễn ra. Để đảm bảo t&iacute;nh mạng v&agrave; t&agrave;i sản của d&acirc;n, ch&uacute;ng t&ocirc;i đ&atilde; khuyến c&aacute;o người d&acirc;n trong đ&ecirc;m nay kh&ocirc;ng được trở về nh&agrave; khi lũ chưa r&uacute;t hẳn&quot; - &ocirc;ng Quyết n&oacute;i.</p>\r\n\r\n<p>Cũng theo &ocirc;ng Quyết, th&ocirc;ng tin từ người d&acirc;n trong v&ugrave;ng chịu ảnh hưởng lũ v&agrave; tr&ecirc;n mạng x&atilde; cho rằng: th&ocirc;ng tin nh&agrave; m&aacute;y thủy điện Th&aacute;c Mơ xả đập g&acirc;y ra lũ l&agrave; kh&ocirc;ng ch&iacute;nh x&aacute;c.</p>\r\n\r\n<p>Dưới đ&acirc;y l&agrave; ch&ugrave;m ảnh m&agrave;&nbsp;<em>Tuổi Trẻ Online&nbsp;</em>ghi lại được tại khu vực ngập lụt.</p>\r\n\r\n<div id=\"zone-jnvk0c1v\" style=\"margin: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: NotoSans-Regular; vertical-align: baseline; color: rgb(34, 34, 34); letter-spacing: -0.2px; background-color: rgba(255, 255, 255, 0.95);\">\r\n<div id=\"share-jnvk0cro\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\">&nbsp;</div>\r\n</div>\r\n\r\n<div class=\"VCSortableInPreviewMode active\" style=\"margin: 1.3em auto 0px; padding: 0px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 11px; line-height: 1.45; font-family: Roboto; vertical-align: baseline; text-align: center; display: table; position: relative; z-index: 2; color: rgb(34, 34, 34); letter-spacing: -0.2px; background-color: rgba(255, 255, 255, 0.95);\">\r\n<div style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\"><a class=\"detail-img-lightbox\" href=\"https://cdn.tuoitre.vn/thumb_w/586/2019/10/5/2-1570279406077561307578.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; display: initial; text-decoration-line: none; cursor: zoom-in; color: rgb(237, 27, 47); letter-spacing: -0.2px;\" target=\"_blank\" title=\"Lực lượng chức năng sơ tán người dân đến nơi an toàn - Ảnh: B.L\"><img alt=\"Lũ bất ngờ ở Bình Phước gây ngập 189 căn nhà - Ảnh 2.\" class=\"lightbox-content\" id=\"img_a611b2a0-e76d-11e9-a080-bd923f79a53e\" src=\"https://cdn.tuoitre.vn/thumb_w/586/2019/10/5/2-1570279406077561307578.jpg\" style=\"border-style:initial; border-width:0px; color:transparent; font:inherit; margin:0px; max-width:100%; padding:0px; vertical-align:baseline; width:586px\" title=\"Lũ bất ngờ ở Bình Phước gây ngập 189 căn nhà - Ảnh 2.\" /></a></div>\r\n\r\n<div class=\"PhotoCMS_Caption ck_legend caption\" style=\"margin: -5px 0px 24px; padding: 5px 8px; border: 0px; font: inherit; vertical-align: baseline; background: rgb(233, 233, 233); text-align: left;\">\r\n<p style=\"margin-left:0px; margin-right:0px\"><span style=\"font-size:12px\">Lực lượng chức năng sơ t&aacute;n người d&acirc;n đến nơi an to&agrave;n - Ảnh: B.L</span></p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"margin: 1.3em auto 0px; padding: 0px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 11px; line-height: 1.45; font-family: Roboto; vertical-align: baseline; text-align: center; display: table; position: relative; z-index: 2; color: rgb(34, 34, 34); letter-spacing: -0.2px; background-color: rgba(255, 255, 255, 0.95);\">\r\n<div style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\">\r\n<div class=\"VCSortableInPreviewMode active\" style=\"margin: 1.3em auto 0px; padding: 0px; border: 0px; font-va